INSERT INTO `tbl_bai_viet` (`ma_bai_viet`, `ma_loai_bai_viet`, `tieu_de`, `anh_minh_hoa`, `mo_ta`, `noi_dung`, `ngay_dang`, `tac_gia`) VALUES
(1, 1, 'Trà hoa cúc - Món quà quý giá cho sức khỏe của bạn', 'tra_hoa_cuc.jpg', '<p><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Từ xưa đến nay hoa c&uacute;c kh&ocirc;ng những l&agrave; một lo&agrave;i hoa mang nhiều &yacute; nghĩa tốt đẹp m&agrave; c&ograve;n l&agrave; nguy&ecirc;n liệu ch&iacute;nh để tạo n&ecirc;n một loại tr&agrave; khiến bao người y&ecirc;u th&iacute;ch. Tr&agrave; hoa c&uacute;c kh&ocirc;ng chỉ l&agrave; thức uống giải nhiệt m&agrave; c&ograve;n l&agrave; m&oacute;n qu&agrave; qu&yacute; gi&aacute; cho sức khỏe của người d&', '<p style=\"text-align: justify;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Tr&agrave; hoa c&uacute;c l&agrave; g&igrave;? C&ugrave;ng với tr&agrave; sen th&igrave; tr&agrave; hoa c&uacute;c cũng l&agrave; một loại thức uống được người Việt y&ecirc;u th&iacute;ch. Theo đ&oacute;, tr&agrave; hoa c&uacute;c l&agrave; một loại tr&agrave; thảo mộc c&oacute; th&agrave;nh phần ch&iacute;nh l&agrave; hoa c&uacute;c kh&ocirc;. Hoa c&uacute;c kh&ocirc; c&oacute; t&ecirc;n khoa học l&agrave; Chrysanthemum Indicum, họ Asteraceae. Với đặc t&iacute;nh đắng, cay, t&iacute;nh m&aacute;t, t&aacute;c dụng giải độc, tr&agrave; l&agrave; thức uống gi&uacute;p thanh nhiệt, bổ n&atilde;o v&agrave; chữa suy nhược thần kinh v&ocirc; c&ugrave;ng hiệu quả. Dựa v&agrave;o th&agrave;nh phần th&igrave; tr&agrave; hoa c&uacute;c được chia l&agrave;m hai loại:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Tr&agrave; c&uacute;c hoa: Tr&agrave; được l&agrave;m từ hoa c&uacute;c v&agrave;ng hoặc hoa c&uacute;c trắng.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Tr&agrave; c&uacute;c La M&atilde;. Lợi &iacute;ch của tr&agrave; hoa c&uacute;c đối với sức khỏe con người.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.traviet.com/wp-content/uploads/2017/03/tra-hoa-cuc-1200x1200.jpg\" alt=\"Tr&agrave; hoa c&uacute;c\" width=\"607\" height=\"607\" /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Nhắc đến tr&agrave; hoa c&uacute;c, người ta nghĩ ngay đến một loại tr&agrave; thanh tao, đơn giản nhưng lại mang đến nhiều t&aacute;c dụng tuyệt vời. Trong đ&oacute; phải kể đến c&aacute;c t&aacute;c dụng như: Cải thiện sức khỏe tim mạch. C&oacute; thể bạn chưa biết, trong th&agrave;nh phần của tr&agrave; hoa c&uacute;c c&oacute; chứa một lớp chất chống oxy h&oacute;a gọi l&agrave; flavones. Theo nghi&ecirc;n cứu, chất n&agrave;y c&oacute; khả năng l&agrave;m giảm huyết &aacute;p v&agrave; cholesterol l&agrave; những nguy&ecirc;n nh&acirc;n phổ biến g&acirc;y n&ecirc;n nguy cơ mắc bệnh tim của bạn. B&ecirc;n cạnh đ&oacute;, c&aacute;c nghi&ecirc;n cứu cũng chứng minh rằng hoạt chất chống oxy h&oacute;a trong tr&agrave; hoa c&uacute;c c&ograve;n c&oacute; t&aacute;c dụng hiệu quả trong việc điều trị chứng đau thắt ngực v&agrave; l&agrave;m dịu những cơn đau từ bệnh động mạch v&agrave;nh. Do đ&oacute;, uống tr&agrave; hoa c&uacute;c ch&iacute;nh l&agrave; c&aacute;ch chăm s&oacute;c v&agrave; bảo vệ tim hiệu quả. Tr&agrave; hoa c&uacute;c gi&uacute;p cải thiện sức khỏe tim mạch Tr&agrave; hoa c&uacute;c gi&uacute;p cải thiện sức khỏe tim mạch Tr&agrave; hoa c&uacute;c d&ugrave;ng để giải cảm, giải nhiệt hiệu quả Nhờ t&iacute;nh m&aacute;t của m&igrave;nh m&agrave; từ xa xưa tr&agrave; hoa c&uacute;c đ&atilde; được biết đến l&agrave; một loại thức uống trong Y học c&oacute; t&aacute;c dụng giải cảm, giải nhiệt v&ocirc; c&ugrave;ng hiệu quả. C&aacute;c trường hợp phong h&agrave;n, sốt cao đều c&oacute; thể được hạ nhiệt bằng loại tr&agrave; n&agrave;y. Tr&agrave; hoa c&uacute;c cho giấc ngủ ngon hơn.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\"> Ng&agrave;y nay, việc thưởng thức một ly tr&agrave; hoa c&uacute;c trước khi đi ngủ đ&atilde; trở th&agrave;nh một th&oacute;i quen của rất nhiều người. Đ&acirc;y kh&ocirc;ng chỉ đơn thuần l&agrave; sở th&iacute;ch m&agrave; c&ograve;n bởi tr&agrave; hoa c&uacute;c c&oacute; t&aacute;c dụng an thần, đem đến giấc ngủ ngon v&agrave; s&acirc;u hơn rất nhiều. Cuộc sống qu&aacute; nhiều mệt mỏi v&agrave; &aacute;p lực, t&igrave;nh trạng stress thường xuy&ecirc;n xảy ra khiến những giấc ngủ s&acirc;u trở n&ecirc;n xa xỉ với qu&aacute; nhiều người. Thay v&igrave; t&igrave;m đến sự hỗ trợ kh&ocirc;ng mấy t&iacute;ch cực từ thuốc an thần th&igrave; người ta thường nh&acirc;m nhi một ly tr&agrave; hoa c&uacute;c trước khi đi v&agrave;o giấc ngủ. Nguy&ecirc;n nh&acirc;n l&agrave; trong hoa c&uacute;c c&oacute; chứa rất nhiều một th&agrave;nh phần gọi l&agrave; apigenin. Nhiều nghi&ecirc;n cứu đ&atilde; cho thấy h&agrave;nh phần n&agrave;y c&oacute; khả năng kết hợp với một số cơ quan cảm thụ ở n&atilde;o để người uống giảm căng thẳng lo &acirc;u v&agrave; dễ ngủ hơn. Uống tr&agrave; hoa c&uacute;c gi&uacute;p giấc ngủ ngon hơn Uống tr&agrave; hoa c&uacute;c gi&uacute;p giấc ngủ ngon hơn Tr&agrave; hoa c&uacute;c gi&uacute;p đẹp da Nhờ c&aacute;c hoạt chất chống oxy h&oacute;a trong hoa c&uacute;c m&agrave; tr&agrave; hoa c&uacute;c c&oacute; t&aacute;c dụng đến sức khỏe của l&agrave;n da bạn. Nếu kết hợp với mặt nạ hoa c&uacute;c - mật ong th&igrave; bạn sẽ sớm c&oacute; được một l&agrave;n da trẻ trung, trắng s&aacute;ng như &yacute;. Tr&agrave; hoa c&uacute;c ngăn ngừa ung thư Nghi&ecirc;n cứu tại Mỹ đ&atilde; ghi nhận chất apigenin trong tr&agrave; hoa c&uacute;c c&oacute; t&aacute;c dụng ngăn ngừa tế b&agrave;o ung thư lan rộng. Việc sử dụng tr&agrave; hoa c&uacute;c trong thời gian sử dụng c&aacute;c thuốc trị ung thư cũng gi&uacute;p c&aacute;c loại thuốc n&agrave;y ph&aacute;t huy t&aacute;c dụng hiệu quả hơn. Apigenin cũng được chứng minh rằng c&oacute; khả năng chống lại c&aacute;c tế b&agrave;o ung thư, trong đ&oacute; c&oacute; tế b&agrave;o ung thư v&uacute;, đường ti&ecirc;u h&oacute;a, da, tử cung v&agrave; tuyến tiền liệt,... m&agrave; rất nhiều người gặp phải. Với điều kiện m&ocirc;i trường sống như hiện nay, khi m&agrave; tất cả c&aacute;c yếu tố tự nhi&ecirc;n đều c&oacute; thể l&agrave; nguy&ecirc;n nh&acirc;n khiến căn bệnh ung thư xuất hiện th&igrave; việc ph&ograve;ng ngừa ch&uacute;ng bằng c&aacute;c loại thức uống như tr&agrave; hoa c&uacute;c l&agrave; điều v&ocirc; c&ugrave;ng cần thiết. Tr&agrave; hoa c&uacute;c gi&uacute;p ngăn ngừa ung thư hiệu quả Tr&agrave; hoa c&uacute;c gi&uacute;p ngăn ngừa ung thư hiệu quả B&ecirc;n cạnh c&aacute;c lợi &iacute;ch kể tr&ecirc;n, tr&agrave; hoa c&uacute;c c&ograve;n c&oacute; t&aacute;c dụng điều h&ograve;a kinh nguyệt, cải thiện hoạt động của hệ ti&ecirc;u h&oacute;a v&agrave; c&oacute; t&aacute;c dụng đẩy l&ugrave;i một số vi khuẩn hiệu quả. Một số lưu &yacute; khi sử dụng tr&agrave; hoa c&uacute;c Để tr&agrave; ph&aacute;t huy hết c&ocirc;ng dụng của m&igrave;nh cũng như tr&aacute;nh c&aacute;c ảnh hưởng kh&ocirc;ng mong muốn từ việc kh&ocirc;ng biết c&aacute;ch sử dụng tr&agrave;, khi uống tr&agrave; hoa c&uacute;c bạn cần lưu &yacute; một số điều sau đ&acirc;y: Ngo&agrave;i thời gian trước khi đi ngủ th&igrave; sau khi ăn thức ăn nhiều dầu mỡ, sau khi vận động mạnh bạn cũng n&ecirc;n uống tr&agrave; hoa c&uacute;c. Kh&ocirc;ng d&ugrave;ng tr&agrave; hoa c&uacute;c để uống thuốc v&igrave; c&aacute;c hoạt chất trong tr&agrave; sẽ c&oacute; phản ứng h&oacute;a học với thuốc l&agrave;m giảm t&aacute;c dụng của thuốc. Phụ nữ mang thai c&oacute; dạ d&agrave;y v&agrave; c&aacute;c cơ quan kh&aacute;c yếu hơn b&igrave;nh thường n&ecirc;n hạn chế sử dụng tr&agrave; hoa c&uacute;c. Kh&ocirc;ng uống tr&agrave; hoa c&uacute;c khi đ&oacute;i để tr&aacute;nh t&igrave;nh trạng &ldquo;say tr&agrave;&rdquo; g&acirc;y ảnh hưởng kh&ocirc;ng tốt đến sức khỏe. </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Chắc hẳn với những th&ocirc;ng tin tr&ecirc;n đ&acirc;y, bạn đ&atilde; ho&agrave;n to&agrave;n tin rằng tr&agrave; hoa c&uacute;c l&agrave; m&oacute;n qu&agrave; qu&yacute; gi&aacute; cho sức khỏe của m&igrave;nh. Loại tr&agrave; n&agrave;y rất dễ mua v&agrave; c&aacute;ch tự l&agrave;m cũng rất đơn giản n&ecirc;n kh&ocirc;ng kh&oacute; để bạn sở hữu được loại thức uống nhiều gi&aacute; trị n&agrave;y.</span></p>', '2020-11-29 07:01:57', 'Kim Ngân'),
(2, 2, 'Trà đạo Việt Nam có hay không?', 'blog1.jpg', '<p><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Nhiều người thường đặt c&acirc;u hỏi Việt Nam c&oacute; tr&agrave; đạo kh&ocirc;ng? C&aacute;i đạo trong tr&agrave; Việt cốt ở tinh thần, kh&ocirc;ng trọng nghi lễ, c&oacute; người bảo tr&agrave; Việt Nam kh&ocirc;ng phải đạo m&agrave; l&agrave; cuộc sống!</span></p>', '<p><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">1. Ở Việt Nam c&oacute; tr&agrave; đạo kh&ocirc;ng? </span></p>\r\n<p><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Nếu đạo l&agrave; con đường, l&agrave; cung c&aacute;ch uống tr&agrave; th&igrave; Việt Nam hẳn nhi&ecirc;n c&oacute; tr&agrave; đạo. Đ&oacute; l&agrave; c&aacute;ch uống tr&agrave; của người Việt. C&aacute;ch uống tr&agrave; đ&oacute; giản dị, gần gũi, nhưng cũng rất đỗi tinh tế như t&acirc;m hồn người Việt. Nghệ thuật tr&agrave; Việt cốt l&agrave; t&igrave;nh người, t&igrave;nh nghĩa bạn b&egrave; t&acirc;m giao, cốt ở sự giao h&ograve;a giữa con người với thi&ecirc;n nhi&ecirc;n, hơn cả một thứ đạo, Tr&agrave; Việt l&agrave; một phần tất y&ecirc;u của cuộc sống người Việt. Bởi thế, tr&agrave; Việt kh&ocirc;ng l&agrave; một c&aacute;i đạo như tr&agrave; đạo Nhật Bản, kh&ocirc;ng qu&aacute; cầu kỳ như tr&agrave; nghệ Trung Hoa, cũng kh&ocirc;ng qu&aacute; thực dụng như tr&agrave; ch&acirc;u &Acirc;u. </span></p>\r\n<p><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">C&ograve;n nếu n&oacute;i s&acirc;u xa hơn, th&igrave; tr&agrave; đạo Việt Nam kh&ocirc;ng đạo ấy m&agrave; l&agrave; đạo. Đạo ở đ&acirc;y l&agrave; g&igrave;? C&aacute;i đạo ấy b&igrave;nh dị như trong cư trần lạc đạo của Trần Nh&acirc;n T&ocirc;ng: &ldquo;đ&oacute;i ăn kh&aacute;t uống mệt ngủ liền&rdquo;. Đ&oacute; l&agrave; khi uống một b&aacute;t tr&agrave;, người d&acirc;n cảm thấy giải kh&aacute;t, sung sướng trong buổi trưa h&egrave; nắng n&oacute;ng hay cảm thấy &aacute;m &aacute;p trong những ng&agrave;y gi&aacute; r&eacute;t đ&ecirc;m đ&ocirc;ng. Khi đưa b&aacute;t ch&egrave; xanh l&ecirc;n miệng v&agrave; uống đến ực, con người như uống cả thi&ecirc;n nhi&ecirc;n phong thủy hỏa thổ v&agrave;o l&ograve;ng với niềm vui sướng. V&agrave; đ&oacute;, cũng l&agrave; sự tri &acirc;n những người một nắng hai sương b&ecirc;n đồi tr&agrave;, những người bỏng r&aacute;t v&agrave; t&aacute;p mặt v&igrave; sao ch&egrave; b&ecirc;n chảo lửa.</span></p>\r\n<p><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\"> Một ch&eacute;n tr&agrave; T&acirc;n Cương Th&aacute;i Nguy&ecirc;n thơm được người Việt trao tay bạn n&oacute; chứa đựng nhiều &yacute; nghĩa, dẫu khung cảnh giản đơn, b&agrave;y biện sơ lược. Dẫu ch&eacute;n c&oacute; sứt, dẫu ghế c&oacute; cập k&ecirc;nh th&igrave; khi nhấp ch&eacute;n tr&agrave;, hẳn mỗi người đều m&ecirc; mẩn bởi m&agrave;u nước v&agrave;ng s&aacute;nh trong xanh như trời như biển. Vị đắng ch&aacute;t gợi l&ecirc;n nỗi vất vả, cần lao của những người l&agrave;m tr&agrave; thủ c&ocirc;ng. Hậu vị ngọt m&aacute;t của tr&agrave; ch&iacute;nh l&agrave; t&acirc;m hồn người Việt gi&agrave;u t&igrave;nh, gi&agrave;u nghĩa như nhiều d&acirc;n tộc hiền l&agrave;nh kh&aacute;c. V&igrave; vậy m&agrave; ch&eacute;n tr&agrave; cho con người ta nh&igrave;n nhận được lẽ cuộc sống, thấy được ngay giả, thiện &aacute;c m&agrave; l&ograve;ng người cũng tĩnh lặng trước khổ đau, b&atilde;o tố cuộc đời. </span></p>\r\n<p><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">2. Nghệ thuật uống tr&agrave; c&ograve;n thể hiện một ho&aacute; ứng xử tr&ecirc;n k&iacute;nh dưới nhường của người Việt ta. </span></p>\r\n<p><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Trong gia đ&igrave;nh truyền thống, người nhỏ pha tr&agrave; mời người lớn, phụ nữ pha tr&agrave; mời c&aacute;c &ocirc;ng. Người ta c&oacute; thể uống tr&agrave; trong y&ecirc;n lặng suy ngẫm như để giao ho&agrave; với thi&ecirc;n nhi&ecirc;n.C&aacute;c ch&eacute;n nằm cạnh nhau thể hiện sự gắn b&oacute; giữa con người với con người, t&igrave;nh l&aacute;ng giềng khi mời ấm tr&agrave; thơm &yacute; ới gọi nhau đầu hồi cuối ng&otilde;. Nếu r&oacute;t tr&agrave; theo chiều kim đồng hồ, mỗi ch&eacute;n r&oacute;t một ch&uacute;t, từ đầu đến cuối, rồi lại v&ograve;ng ngược lại đến đầu, c&aacute;c ch&eacute;n đều nhau c&ugrave;ng đậm hoặc c&ugrave;ng nhạt hoặc c&ugrave;ng vừa vặn m&ecirc; người. &Yacute; nghĩa của n&oacute; l&agrave; thể hiện sự b&igrave;nh đẳng giữa chủ v&agrave; kh&aacute;ch trong sự hưởng thụ lộc giời. </span></p>\r\n<p><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Những c&aacute;i đ&oacute; l&agrave; đạo của tr&agrave; Việt. Người Việt mời tr&agrave; nhau biểu hiện một phong độ văn h&oacute;a thanh cao, một sự kết giao tri kỷ, một tấm l&ograve;ng ước mong h&ograve;a hợp, một sự t&acirc;m đắc của những người đối thoại. Người Việt Nam mời nhau uống tr&agrave; l&agrave; để bắt đầu một lời t&acirc;m sự, để b&agrave;n chuyện gia đ&igrave;nh, x&atilde; hội, chuyện thế th&aacute;i nh&acirc;n t&igrave;nh. Đ&acirc;y l&agrave; thứ văn h&oacute;a b&igrave;nh dị, cao cả m&agrave; hướng đến thực h&agrave;nh, hướng đến cảm nhận c&aacute;i hồn của cuộc sống, loại bỏ hết mọi nguy&ecirc;n tắc nghi thức tr&oacute;i buộc, để con người tự do thưởng thức. M&agrave; tự do l&agrave; điều kiện ti&ecirc;n quyết để nhận thức cuộc sống, để kh&aacute;m bản th&acirc;n mỗi người,để s&aacute;ng tạo đ&oacute; sao, v&igrave; vậy c&acirc;u hỏi tr&agrave; đạo Việt Nam c&oacute; hay kh&ocirc;ng kh&ocirc;ng c&ograve;n quan trọng nữa.</span></p>', '2020-11-29 18:54:46', ''),
(3, 2, 'Nghệ thuật thưởng thức trà đạo của Việt Nam', 'blog2.jpg', '<p style=\"text-align: justify;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Ở Việt Nam c&aacute;ch uống tr&agrave; thể hiện phong ph&uacute; những kh&iacute;a cạnh văn ho&aacute; ứng xử của người d&acirc;n Việt. V&agrave; c&aacute;ch ẩm tr&agrave; của người Việt cũng rất ri&ecirc;ng, rất độc đ&aacute;o: c&aacute;c bậc tiền nh&acirc;n xưa cho rằng uống tr&agrave; l&agrave; một nghệ thuật, m&agrave; nghệ thuật th&igrave; phi c&ocirc;ng thức.</span></p>', '<p style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: justify;\"><span style=\"font-size: 12pt; color: #000000;\">C&oacute; rất nhiều nước trồng ch&egrave; v&agrave; tương ứng với n&oacute; cũng c&oacute; bấy nhiều c&aacute;ch thức uống. C&aacute;ch thức thưởng tr&agrave; được n&acirc;ng l&ecirc;n th&agrave;nh nghệ thuật c&oacute; ở Trung Hoa v&agrave; Nhật Bản - nơi đ&atilde; từng coi tr&agrave; như một t&ocirc;n gi&aacute;o như c&aacute;ch gọi tr&agrave; kinh, tr&agrave; đạo. C&ograve;n ở Việt Nam c&aacute;ch uống tr&agrave; thể hiện phong ph&uacute; những kh&iacute;a cạnh văn ho&aacute; ứng xử của người d&acirc;n Việt. V&agrave; c&aacute;ch ẩm tr&agrave; của người Việt cũng rất ri&ecirc;ng, rất độc đ&aacute;o: c&aacute;c bậc tiền nh&acirc;n xưa cho rằng uống tr&agrave; l&agrave; một nghệ thuật, m&agrave; nghệ thuật th&igrave; phi c&ocirc;ng thức.</span></p>\r\n<p class=\"separator\" style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; clear: both;\"><span style=\"color: #000000;\"><a style=\"color: #000000; text-decoration: underline; outline: currentcolor none medium; margin-left: 1em; margin-right: 1em;\" href=\"http://4.bp.blogspot.com/-AFNciyuD0oE/Ub7qE6pVmyI/AAAAAAAAABU/Z2uMqYcsWNU/s1600/10.jpg\"><img style=\"padding: 0px; background: transparent none repeat scroll 0% 0%; display: block; margin-left: auto; margin-right: auto;\" title=\"Nghệ thuật thưởng thức tr&agrave; đạo của Việt Nam 1\" src=\"http://4.bp.blogspot.com/-AFNciyuD0oE/Ub7qE6pVmyI/AAAAAAAAABU/Z2uMqYcsWNU/s640/10.jpg\" alt=\"ch&egrave; th&aacute;i nguy&ecirc;n, b&aacute;n ch&egrave; th&aacute;i nguy&ecirc;n, ch&egrave; t&acirc;n cương th&aacute;i nguy&ecirc;n, b&aacute;n ch&egrave; t&acirc;n cương th&aacute;i nguy&ecirc;n 1\" width=\"464\" height=\"371\" border=\"0\" /></a></span></p>\r\n<p style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: justify;\"><span style=\"font-size: large; color: #000000;\">V&igrave; lẽ ấy c&aacute;c bậc tiền nh&acirc;n thường c&oacute; c&aacute;ch uống tr&agrave; như sau: trước khi uống thường đưa qua mũi để tận hưởng hương vị tr&agrave;, sau mới hạ dần xuống miệng, m&ocirc;i nhấp ngụm nhỏ thấy ch&aacute;t đắng, ch&acirc;n răng cảm nhận như chặt lại, miệng ch&eacute;p liền mấy c&aacute;i đ&atilde; thấy dịch vị trong miệng tiết ra c&oacute; vị ngọt dịu, l&ograve;ng sảng kho&aacute;i luận về tr&agrave;. Từ chất lượng của ly tr&agrave; người uống sẽ thấy được c&aacute;i t&acirc;m, c&aacute;i t&igrave;nh của người đ&atilde; pha chung tr&agrave;.</span></p>\r\n<p class=\"separator\" style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; clear: both; text-align: justify;\"><span style=\"color: #000000;\"><a style=\"color: #000000; text-decoration: none; outline: currentcolor none medium; margin-left: 1em; margin-right: 1em;\" href=\"http://4.bp.blogspot.com/-2XPQIgMLqN0/UosavtjnS_I/AAAAAAAAAJI/EEQ2_HYltZA/s1600/Nghe-thuat-uong-tra-cua-nguoi-Viet-1.jpg\"><img style=\"padding: 0px; background: transparent none repeat scroll 0% 0%; display: block; margin-left: auto; margin-right: auto;\" title=\"Nghệ thuật thưởng thức tr&agrave; đạo của Việt Nam 2\" src=\"http://4.bp.blogspot.com/-2XPQIgMLqN0/UosavtjnS_I/AAAAAAAAAJI/EEQ2_HYltZA/s640/Nghe-thuat-uong-tra-cua-nguoi-Viet-1.jpg\" alt=\"ch&egrave; th&aacute;i nguy&ecirc;n, b&aacute;n ch&egrave; th&aacute;i nguy&ecirc;n, ch&egrave; t&acirc;n cương th&aacute;i nguy&ecirc;n, b&aacute;n ch&egrave; t&acirc;n cương th&aacute;i nguy&ecirc;n 2\" width=\"640\" height=\"372\" border=\"0\" /></a></span></p>\r\n<p style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: justify;\"><span style=\"font-size: large; color: #000000;\">Người Việt d&ugrave;ng tr&agrave; nguy&ecirc;n thủy (tr&agrave; mộc) ướp với nhiều hương liệu kh&aacute;c nhau th&agrave;nh tr&agrave; sen, tr&agrave; s&oacute;i, tr&agrave; bạch ngọc (ướp hương từ năm loại hoa m&agrave;u trắng: nh&agrave;i, c&uacute;c trắng, b&ocirc;ng bạch, mộc v&agrave; ngọc lan), tr&agrave; mật ong, tr&agrave; long nh&atilde;n, tr&agrave; nh&acirc;n s&acirc;m... Mỗi loại tr&agrave; l&agrave;m n&ecirc;n một hương vị kh&aacute;c nhau trong đ&oacute; tr&agrave; sen l&agrave; thứ tr&agrave; qu&yacute; nhất m&agrave; ng&agrave;y xưa chỉ d&agrave;nh cho bậc vua ch&uacute;a thưởng thức. Người Việt thưởng tr&agrave; theo c&aacute;ch độc ẩm (một m&igrave;nh), đối ẩm (hai người), hay quần ẩm (nhiều người) vừa thể hiện văn h&oacute;a thuần chất của m&igrave;nh đồng thời vừa c&oacute; những ti&ecirc;u chuẩn về chất lượng cũng như vị thế của việc thưởng tr&agrave;.</span></p>\r\n<table class=\"tr-caption-container\" style=\"margin-bottom: 0.5em; width: 663.2px; padding: 4px; color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: center;\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 4px 10px 4px 5px; text-align: center;\">\r\n<p><span style=\"color: #000000;\"><a style=\"color: #000000; text-decoration: none; outline: currentcolor none medium; margin-left: auto; margin-right: auto;\" href=\"http://3.bp.blogspot.com/-cdkmBqJ8ic4/Uosb24_I-1I/AAAAAAAAAJQ/bmr396xfUEk/s1600/Nghe-thuat-uong-tra-cua-nguoi-Viet-4.jpg\"><img style=\"border: none; padding: 0px; background: transparent;\" title=\"Nghệ thuật thưởng thức tr&agrave; đạo của Việt Nam 3\" src=\"http://3.bp.blogspot.com/-cdkmBqJ8ic4/Uosb24_I-1I/AAAAAAAAAJQ/bmr396xfUEk/s640/Nghe-thuat-uong-tra-cua-nguoi-Viet-4.jpg\" alt=\"ch&egrave; th&aacute;i nguy&ecirc;n, b&aacute;n ch&egrave; th&aacute;i nguy&ecirc;n, ch&egrave; t&acirc;n cương th&aacute;i nguy&ecirc;n, b&aacute;n ch&egrave; t&acirc;n cương th&aacute;i nguy&ecirc;n 3\" width=\"640\" height=\"480\" border=\"0\" /></a></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"tr-caption\" style=\"padding: 4px 10px 4px 5px; font-size: 9.6px; text-align: center;\">\r\n<p><span style=\"color: #000000;\"><em><span style=\"font-size: large;\">Uống tr&agrave; đối ẩm hai người</span></em></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: justify;\"><span style=\"font-size: large; color: #000000;\">Nếu tr&agrave; d&ugrave;ng khi độc ẩm (uống tr&agrave; một m&igrave;nh) l&agrave; l&uacute;c người đ&oacute; đang nh&acirc;m nhi lẩm nhẩm thi thơ &ocirc;n luyện, nếu đối ẩm (hai người uống tr&agrave;) th&igrave; c&ugrave;ng cởi mở văn b&agrave;i ti&ecirc;u dao, thậm ch&iacute; hưng phấn c&ugrave;ng cầm kỳ thi họa v&agrave; c&ugrave;ng nhau thưởng thức tiếng oanh nỉ non ngo&agrave;i vườn. Tr&agrave; cũng như người bạn t&acirc;m giao của con người khi c&oacute; t&acirc;m sự, gi&uacute;p cho người ta nhớ đến tri &acirc;n, tri kỷ hoặc suy ngẫm về người, về m&igrave;nh, về nh&acirc;n t&igrave;nh thế th&aacute;i những năm th&aacute;ng qua. Khi giận dữ kh&ocirc;ng ai tự pha được ấm tr&agrave; ngon, chỉ sau khi ngu&ocirc;i ngoai người ta mới c&oacute; thể ngồi uống tr&agrave; như một c&aacute;ch thiền &ldquo;ch&aacute;nh niệm&rdquo; vậy.</span></p>\r\n<h3 style=\"margin-bottom: 16px; font-weight: normal; line-height: 1; font-size: 20px;\"><span style=\"color: #000000;\"><strong><span style=\"font-size: large;\">&ldquo;Nhất thủy, nh&igrave; tr&agrave;, tam b&ocirc;i, tứ b&igrave;nh, ngũ quần anh\"</span></strong></span></h3>\r\n<p style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: justify;\"><span style=\"font-size: large; color: #000000;\">Người Việt gọi l&agrave; &ldquo;nhất thủy, nh&igrave; tr&agrave;&rdquo;, nước d&ugrave;ng pha tr&agrave; thường phải l&agrave; thứ nước mưa được hứng giữa trời, hay từ c&aacute;c suối nguồn tự nhi&ecirc;n, cầu kỳ hơn nữa l&agrave; thứ sương đọng tr&ecirc;n l&aacute; sen v&agrave;o buổi sớm mai. C&aacute;ch đun nước cũng phải phải đảm bảo giữ được độ thanh tĩnh v&agrave; kh&ocirc;ng l&agrave;m ảnh hưởng đến hương vị của tr&agrave;. Người trong Ho&agrave;ng cung trước kia khi pha tr&agrave; cho c&aacute;c &ocirc;ng vua, b&agrave; ho&agrave;ng rất cầu kỳ v&agrave; c&ocirc;ng phu, phải hứng từng giọt sương tr&ecirc;n b&uacute;p sen v&agrave;o l&uacute;c chưa c&oacute; &aacute;nh nắng. C&ograve;n c&aacute;c cụ xưa thường d&ugrave;ng nước mưa sẽ gi&uacute;p cho nước tr&agrave; tăng th&ecirc;m vị ngọt, sau khi uống sẽ thấy vị ngọt lưu lại nơi cổ họng. Những người c&oacute; th&uacute; đam m&ecirc; uống tr&agrave; ng&agrave;y nay đ&atilde; đề ra 5 chuẩn mực về c&aacute;ch chọn tr&agrave; ngon: sắc, thanh, khi, vị, thần nhưng với những người s&agrave;nh tr&agrave; th&igrave; tr&agrave; mộc m&oacute;c c&acirc;u (c&aacute;nh tr&agrave; sao quăn lại giống h&igrave;nh m&oacute;c c&acirc;u) l&agrave; loại tr&agrave; qu&yacute; nhất. &ldquo;Tam b&ocirc;i, tứ b&igrave;nh&rdquo; (b&igrave;nh v&agrave; ấm tr&agrave;) ch&iacute;nh l&agrave; bộ đồ pha v&agrave; uống tr&agrave; c&oacute; ấm v&agrave; 4 ch&eacute;n qu&acirc;n, 1 ch&eacute;n tống để chuy&ecirc;n tr&agrave;.</span></p>\r\n<table class=\"tr-caption-container\" style=\"margin-bottom: 0.5em; width: 663.2px; padding: 4px; color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: center;\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 4px 10px 4px 5px; text-align: center;\">\r\n<p><span style=\"color: #000000;\"><a style=\"color: #000000; text-decoration: none; outline: currentcolor none medium; margin-left: auto; margin-right: auto;\" href=\"http://1.bp.blogspot.com/-YDdWKMsZmQw/UosavZMyaBI/AAAAAAAAAJM/Chb2mrsLed4/s1600/Nghe-thuat-uong-tra-cua-nguoi-Viet-2.jpg\"><img style=\"border: none; padding: 0px; background: transparent;\" title=\"Nghệ thuật thưởng thức tr&agrave; đạo của Việt Nam 4\" src=\"http://1.bp.blogspot.com/-YDdWKMsZmQw/UosavZMyaBI/AAAAAAAAAJM/Chb2mrsLed4/s640/Nghe-thuat-uong-tra-cua-nguoi-Viet-2.jpg\" alt=\"ch&egrave; th&aacute;i nguy&ecirc;n, b&aacute;n ch&egrave; th&aacute;i nguy&ecirc;n, ch&egrave; t&acirc;n cương th&aacute;i nguy&ecirc;n, b&aacute;n ch&egrave; t&acirc;n cương th&aacute;i nguy&ecirc;n 4\" width=\"640\" height=\"480\" border=\"0\" /></a></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"tr-caption\" style=\"padding: 4px 10px 4px 5px; font-size: 9.6px; text-align: center;\">\r\n<p><span style=\"color: #000000;\"><em><span style=\"font-size: large;\">Nhất thủy, nh&igrave; tr&agrave;, tam b&ocirc;i, tứ b&igrave;nh</span></em></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: justify;\"><span style=\"font-size: large; color: #000000;\">Ch&eacute;n qu&acirc;n thường l&agrave; loại ch&eacute;n hạt m&iacute;t (mắt tr&acirc;u), b&igrave;nh cũng c&oacute; b&igrave;nh chuy&ecirc;n, b&igrave;nh tống. T&ugrave;y theo lối uống &ldquo;độc ẩm, song ẩm, tứ ẩm hay quần ẩm&rdquo; để chọn loại b&igrave;nh tương ứng. Kỹ năng pha tr&agrave; t&ugrave;y theo kinh nghiệm b&iacute; quyết của mỗi người, t&ugrave;y v&agrave;o chất lượng v&agrave; hương vị của mỗi loại tr&agrave; n&ecirc;n pha loại ấm n&agrave;o. Trước khi pha tr&agrave; phải tr&aacute;ng ấm bằng nuớc s&ocirc;i cho n&oacute;ng trước rồi cho tr&agrave; v&agrave;o, khi pha xong đậy nắp k&iacute;n tiếp tục r&oacute;t nước s&ocirc;i từ tr&ecirc;n nắp xuống như tắm ấm để giữ nhiệt độ n&oacute;ng trong ấm gi&uacute;p cho c&aacute;c c&aacute;nh tr&agrave; được thấm đều.</span></p>\r\n<p class=\"separator\" style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; clear: both;\"><span style=\"color: #000000;\">&nbsp;</span></p>\r\n<p style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: justify;\"><span style=\"font-size: large; color: #000000;\">Với người thưởng tr&agrave;, &ldquo;ngũ quần anh&rdquo;, tức t&igrave;m bạn tr&agrave;, đ&ocirc;i khi kh&oacute; hơn t&igrave;m bạn rượu. V&igrave; bạn tr&agrave; l&agrave; người bạn tri &acirc;m, c&ugrave;ng nhau thưởng tr&agrave;, ng&acirc;m thơ, bộc bạch nỗi niềm hay b&agrave;n chuyện gia đ&igrave;nh, x&atilde; hội nh&acirc;n t&igrave;nh thế th&aacute;i để cảm nhận trong tr&agrave; c&oacute; cả trời đất, cỏ c&acirc;y.</span></p>\r\n<h3 style=\"margin-bottom: 16px; font-weight: normal; line-height: 1; font-size: 20px;\"><span style=\"color: #000000;\"><strong><span style=\"font-size: large;\">Kh&ocirc;ng gian v&agrave; thời điểm thưởng tr&agrave;</span></strong></span></h3>\r\n<p style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: justify;\"><span style=\"font-size: large; color: #000000;\">Người thưởng tr&agrave; s&agrave;nh điệu l&agrave; người chọn thời điểm uống tr&agrave; v&agrave;o l&uacute;c mờ s&aacute;ng (khoảng 4-5 giờ s&aacute;ng) khi thời khắc &acirc;m dương giao h&ograve;a, đ&ecirc;m qua - ng&agrave;y tới), uống tr&agrave; thời khắc giao h&ograve;a n&agrave;y sẽ c&oacute; lợi cho sức khỏe v&agrave; t&acirc;m hồn, hướng người thưởng tr&agrave; tới những điều tốt đẹp.</span></p>\r\n<table class=\"tr-caption-container\" style=\"margin-bottom: 0.5em; width: 663.2px; padding: 4px; color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: center;\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 4px 10px 4px 5px; text-align: center;\">\r\n<p><span style=\"color: #000000;\"><a style=\"color: #000000; text-decoration: none; outline: currentcolor none medium; margin-left: auto; margin-right: auto;\" href=\"http://3.bp.blogspot.com/-6KT00yjGbzw/UosavaU4PEI/AAAAAAAAAJA/bG9Yy4_QNK0/s1600/Nghe-thuat-uong-tra-cua-nguoi-Viet-3.jpg\"><img style=\"border: none; padding: 0px; background: transparent;\" title=\"Nghệ thuật thưởng thức tr&agrave; đạo của Việt Nam 6\" src=\"http://3.bp.blogspot.com/-6KT00yjGbzw/UosavaU4PEI/AAAAAAAAAJA/bG9Yy4_QNK0/s640/Nghe-thuat-uong-tra-cua-nguoi-Viet-3.jpg\" alt=\"ch&egrave; th&aacute;i nguy&ecirc;n, b&aacute;n ch&egrave; th&aacute;i nguy&ecirc;n, ch&egrave; t&acirc;n cương th&aacute;i nguy&ecirc;n, b&aacute;n ch&egrave; t&acirc;n cương th&aacute;i nguy&ecirc;n 6\" width=\"640\" height=\"424\" border=\"0\" /></a></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"tr-caption\" style=\"padding: 4px 10px 4px 5px; font-size: 9.6px; text-align: center;\">\r\n<p><span style=\"color: #000000;\"><em><span style=\"font-size: large;\">Thưởng tr&agrave; cung đ&igrave;nh Huế</span></em></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: justify;\"><span style=\"font-size: large; color: #000000;\">Về kh&ocirc;ng gian thưởng tr&agrave; của người Việt cũng đ&ograve;i hỏi c&oacute; một kh&ocirc;ng gian rộng để tận hưởng được hết sự tinh t&uacute;y của tr&agrave;. Kh&ocirc;ng gian thưởng tr&agrave; thường mang hơi hướng của văn h&oacute;a thiền - l&agrave; kh&ocirc;ng gian thanh tịnh, thuần khiết, tao nh&atilde;, &ecirc;m dịu. L&yacute; tưởng nhất l&agrave; những nơi c&oacute; khung cảnh thi&ecirc;n nhi&ecirc;n đẹp, y&ecirc;n tĩnh, b&ecirc;n trong c&oacute; tranh ảnh, thư ph&aacute;p, g&oacute;c đọc s&aacute;ch b&aacute;o hoặc b&agrave;n cờ.</span></p>\r\n<h3 style=\"margin-bottom: 16px; font-weight: normal; line-height: 1; font-size: 20px;\"><span style=\"color: #000000;\"><strong><span style=\"font-size: large;\">Phong c&aacute;ch tr&agrave; Việt</span></strong></span></h3>\r\n<p class=\"separator\" style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; clear: both;\"><span style=\"color: #000000;\"><a style=\"color: #000000; text-decoration: none; outline: currentcolor none medium; margin-left: 1em; margin-right: 1em;\" href=\"http://1.bp.blogspot.com/-2t1-Kh2-zI0/UosdAw6aNPI/AAAAAAAAAJk/Sus6NQoh4bk/s1600/Nghe-thuat-uong-tra-cua-nguoi-Viet-6.jpg\"><img style=\"padding: 0px; background: transparent none repeat scroll 0% 0%; display: block; margin-left: auto; margin-right: auto;\" title=\"Nghệ thuật thưởng thức tr&agrave; đạo của Việt Nam 7\" src=\"http://1.bp.blogspot.com/-2t1-Kh2-zI0/UosdAw6aNPI/AAAAAAAAAJk/Sus6NQoh4bk/s640/Nghe-thuat-uong-tra-cua-nguoi-Viet-6.jpg\" alt=\"ch&egrave; th&aacute;i nguy&ecirc;n, b&aacute;n ch&egrave; th&aacute;i nguy&ecirc;n, ch&egrave; t&acirc;n cương th&aacute;i nguy&ecirc;n, b&aacute;n ch&egrave; t&acirc;n cương th&aacute;i nguy&ecirc;n 7\" width=\"640\" height=\"462\" border=\"0\" /></a></span></p>\r\n<p style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: justify;\"><span style=\"font-size: large; color: #000000;\">Phong c&aacute;ch uống tr&agrave; của người Việt Nam rất đa dạng kh&ocirc;ng theo chuẩn mực n&agrave;o, biểu hiện đầy đủ kh&iacute;a cạnh ng&ocirc;n ngữ s&acirc;u xa trong văn h&oacute;a ứng xử đầy t&iacute;nh s&aacute;ng tạo của người pha tr&agrave; v&agrave; người được mời uống tr&agrave; đ&atilde; được n&acirc;ng l&ecirc;n bậc nghệ thuật pha tr&agrave; v&agrave; văn h&oacute;a uống tr&agrave;. Những người hiểu biết về văn h&oacute;a uống tr&agrave;, nghệ thuật pha tr&agrave; của người Việt Nam th&igrave; kh&ocirc;ng bao giờ chịu ảnh hưởng ch&uacute;t n&agrave;o của người Trung Quốc, H&agrave;n Quốc, c&agrave;ng kh&ocirc;ng giống tr&agrave; đạo của người Nhật Bản. C&oacute; thể khẳng định ở Việt Nam kh&ocirc;ng c&oacute; tr&agrave; đạo m&agrave; chỉ c&oacute; nghệ thuật uống tr&agrave;.</span></p>', '2020-11-29 19:44:39', '');
INSERT INTO `tbl_bai_viet` (`ma_bai_viet`, `ma_loai_bai_viet`, `tieu_de`, `anh_minh_hoa`, `mo_ta`, `noi_dung`, `ngay_dang`, `tac_gia`) VALUES
(6, 2, 'Trà xưa và nay', 'blog5.jpg', '<p><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Uống tr&agrave; l&agrave; n&eacute;t văn h&oacute;a l&acirc;u đời của người Việt Nam. Ng&agrave;y xưa tr&agrave; chỉ được d&ugrave;ng trong lớp quyền qu&yacute; cao sang. T&aacute;c phong mời tr&agrave; một c&aacute;ch cung k&iacute;nh, n&acirc;ng t&aacute;ch tr&agrave; bằng hai tay tỏ ra rất thanh tao lịch l&atilde;m.</span></p>', '<p style=\"text-align: justify;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; font-size: 12pt;\">Uống tr&agrave; l&agrave; n&eacute;t văn h&oacute;a l&acirc;u đời của người Việt Nam. Ng&agrave;y xưa tr&agrave; chỉ được d&ugrave;ng trong lớp quyền qu&yacute; cao sang. T&aacute;c phong mời tr&agrave; một c&aacute;ch cung k&iacute;nh, n&acirc;ng t&aacute;ch tr&agrave; bằng hai tay tỏ ra rất thanh tao lịch l&atilde;m. Qua cung c&aacute;ch n&agrave;y người được mời c&oacute; thể thấy được phần n&agrave;o cốt c&aacute;ch sống v&agrave; hiểu phần n&agrave;o chịu ảnh hưởng gia phong của người mời tr&agrave;. Trước khi uống người ta nhẹ nh&agrave;ng đưa t&aacute;ch tr&agrave; l&ecirc;n thưởng thức hương tr&agrave; rồi từ tốn nhấp từng ngụm nhỏ để thưởng thức vị ngon của tr&agrave;. Từ chất lượng của t&aacute;ch tr&agrave; người uống sẽ thấy được c&aacute;i t&acirc;m, c&aacute;i t&igrave;nh của người đ&atilde; pha chung tr&agrave;.</span></p>\r\n<table class=\"tr-caption-container\" style=\"margin-bottom: 0.5em; width: 663.2px; padding: 4px; color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: center;\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 4px 10px 4px 5px; text-align: center;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\"><a style=\"color: #000000; text-decoration: none; outline: currentcolor none medium; margin-left: auto; margin-right: auto;\" href=\"http://3.bp.blogspot.com/-eJNGG01MV2o/UortY5sc6WI/AAAAAAAAAIM/0dVMVGGp-EE/s1600/tra-xua-va-nay-4.jpg\"><img style=\"border: none; padding: 0px; background: transparent;\" src=\"http://3.bp.blogspot.com/-eJNGG01MV2o/UortY5sc6WI/AAAAAAAAAIM/0dVMVGGp-EE/s640/tra-xua-va-nay-4.jpg\" width=\"640\" height=\"356\" border=\"0\" /></a></span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"tr-caption\" style=\"padding: 4px 10px 4px 5px; font-size: 9.6px; text-align: center;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\"><em><span style=\"font-size: large;\">Tr&agrave; xưa</span></em></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; font-size: large;\">Tr&agrave; kh&ocirc;ng chỉ l&agrave; một thức uống mang đậm bản sắc văn h&oacute;a m&agrave; c&oacute; g&iacute;a trị liệu ph&aacute;p, gi&uacute;p cho m&aacute;u huyết lưu th&ocirc;ng, lợi tiểu, c&oacute; khả năng chống ung thư, ti&ecirc;u độc, điều h&ograve;a huyết &aacute;p&hellip; Trước đ&acirc;y tr&agrave; chỉ phổ biến ở một số nước Ch&acirc;u &aacute; như ở Ấn Độ, Srilanca chỗ n&agrave;o cũng trồng tr&agrave; để cung cấp cho người bản địa v&agrave; cả cho xuất khẩu. Sau n&agrave;y được lan rộng ra c&aacute;c nước Trung &Aacute; như một số nước thuộc khối &ldquo;Udơb&ecirc;ch&rdquo; của c&aacute;c nước cộng h&ograve;a Li&ecirc;n X&ocirc; cũ, đặc biệt những nước n&agrave;y kh&ocirc;ng trồng tr&agrave;, nhưng ở nơi n&agrave;o cũng uống tr&agrave;. Tr&ecirc;n &ldquo;con đường tơ lụa&rdquo; tr&agrave; dần dần c&oacute; mặt ở c&aacute;c nước Ch&acirc;u &Acirc;u.</span></p>\r\n<table class=\"tr-caption-container\" style=\"margin-bottom: 0.5em; width: 663.2px; padding: 4px; color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: center;\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 4px 10px 4px 5px; text-align: center;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\"><a style=\"color: #000000; text-decoration: underline; outline: currentcolor none medium; margin-left: auto; margin-right: auto;\" href=\"http://3.bp.blogspot.com/-ItHAGp77ZY4/UortYAN9U0I/AAAAAAAAAHs/yFsXD--PSbo/s1600/tra-xua-va-nay-2.jpg\"><img style=\"border: none; padding: 0px; background: transparent;\" src=\"http://3.bp.blogspot.com/-ItHAGp77ZY4/UortYAN9U0I/AAAAAAAAAHs/yFsXD--PSbo/s640/tra-xua-va-nay-2.jpg\" width=\"640\" height=\"439\" border=\"0\" /></a></span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"tr-caption\" style=\"padding: 4px 10px 4px 5px; font-size: 9.6px; text-align: center;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\"><em><span style=\"font-size: large;\">Qu&aacute;n c&oacute;c ở H&agrave; Nội</span></em></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; font-size: large;\">Ở Việt Nam, c&oacute; thể n&oacute;i tr&agrave; c&oacute; mặt trong mọi hoạt động của x&atilde; hội, từ trong gia đ&igrave;nh ra ngo&agrave;i phố, từ nh&agrave; h&agrave;ng, qu&aacute;n chợ cho đến những nơi tiếp kh&aacute;ch sang trọng. Từ tế lễ, cưới hỏi, sinh nhật, ma chay, c&uacute;ng giỗ&hellip;<br />Nếu tr&agrave; d&ugrave;ng khi nhất ẩm (uống tr&agrave; một m&igrave;nh) l&agrave; l&uacute;c người đ&oacute; đang nh&acirc;m nhi lẩm nhẩm thi thơ &ocirc;n luyện, nếu song ẩm (hai người uống tr&agrave;) th&igrave; c&ugrave;ng cởi mở văn b&agrave;i ti&ecirc;u dao, thậm ch&iacute; hưng phấn c&ugrave;ng cầm kỳ thi họa v&agrave; c&ugrave;ng nhau thưởng thức tiếng oanh nỉ non ngo&agrave;i vườn. Tr&agrave; cũng như người bạn t&acirc;m giao của con người khi c&oacute; t&acirc;m sự, gi&uacute;p cho người ta nhớ đến tri &acirc;n, tri kỷ hoặc suy ngẫm về người, về m&igrave;nh, về nh&acirc;n t&igrave;nh thế th&aacute;i những năm th&aacute;ng qua. Khi giận dữ kh&ocirc;ng ai tự pha được ấm tr&agrave; ngon, chỉ sau khi ngu&ocirc;i ngoai người ta mới c&oacute; thể ngồi uống tr&agrave; như một c&aacute;ch thiền &ldquo;ch&aacute;nh niệm&rdquo; vậy</span></p>\r\n<table class=\"tr-caption-container\" style=\"margin-bottom: 0.5em; width: 604.8px; padding: 4px; color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: center;\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 4px 10px 4px 5px; text-align: center;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\"><a style=\"color: #000000; text-decoration: none; outline: currentcolor none medium; margin-left: auto; margin-right: auto;\" href=\"http://4.bp.blogspot.com/-PWwQqgXWZpc/UortYYFE7gI/AAAAAAAAAHw/u1oV7t8WCaI/s1600/tra-xua-va-nay-3.jpg\"><img style=\"border: none; padding: 0px; background: transparent;\" src=\"http://4.bp.blogspot.com/-PWwQqgXWZpc/UortYYFE7gI/AAAAAAAAAHw/u1oV7t8WCaI/s640/tra-xua-va-nay-3.jpg\" width=\"560\" height=\"640\" border=\"0\" /></a></span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"tr-caption\" style=\"padding: 4px 10px 4px 5px; font-size: 9.6px; text-align: center;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\"><em><span style=\"font-size: large;\">Người xưa uống tr&agrave;</span></em></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; font-size: large;\">Phong c&aacute;ch uống tr&agrave; của người Việt Nam rất đa dạng kh&ocirc;ng theo chuẩn mực n&agrave;o, biểu hiện đầy đủ kh&iacute;a cạnh ng&ocirc;n ngữ s&acirc;u xa trong văn h&oacute;a ứng xử đầy t&iacute;nh s&aacute;ng tạo của người pha tr&agrave; v&agrave; người được mời uống tr&agrave; đ&atilde; được n&acirc;ng l&ecirc;n bậc nghệ thuật pha tr&agrave; v&agrave; văn h&oacute;a uống tr&agrave;. Những người hiểu biết về văn h&oacute;a uống tr&agrave;, nghệ thuật pha tr&agrave; của người Việt Nam th&igrave; kh&ocirc;ng bao giờ chịu ảnh hưởng ch&uacute;t n&agrave;o của người Trung Quốc, H&agrave;n Quốc, c&agrave;ng kh&ocirc;ng giống tr&agrave; đạo của người Nhật Bản. C&oacute; thể khẳng định ở Việt Nam kh&ocirc;ng c&oacute; tr&agrave; đạo m&agrave; chỉ c&oacute; nghệ thuật uống tr&agrave;.</span></p>\r\n<div class=\"separator\" style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; clear: both;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\"><a style=\"color: #000000; text-decoration: none; outline: currentcolor none medium; margin-left: 1em; margin-right: 1em;\" href=\"http://1.bp.blogspot.com/-LtNjBqe5w9U/UortYNLI9XI/AAAAAAAAAH4/jbZCe4ZcwKQ/s1600/tra-xua-va-nay-1.jpg\"><img style=\"padding: 0px; background: transparent none repeat scroll 0% 0%; display: block; margin-left: auto; margin-right: auto;\" src=\"http://1.bp.blogspot.com/-LtNjBqe5w9U/UortYNLI9XI/AAAAAAAAAH4/jbZCe4ZcwKQ/s640/tra-xua-va-nay-1.jpg\" width=\"640\" height=\"416\" border=\"0\" /></a></span></div>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; font-size: large;\">Người trong Ho&agrave;ng cung trước kia khi pha tr&agrave; cho c&aacute;c &ocirc;ng vua, b&agrave; ho&agrave;ng rất cầu kỳ v&agrave; c&ocirc;ng phu, phải hứng từng giọt sương tr&ecirc;n b&uacute;p sen v&agrave;o l&uacute;c chưa c&oacute; &aacute;nh nắng. C&ograve;n c&aacute;c cụ xưa thường d&ugrave;ng nước mưa sẽ gi&uacute;p cho nước tr&agrave; tăng th&ecirc;m vị ngọt, sau khi uống sẽ thấy vị ngọt lưu lại nơi cổ họng. Kỹ năng pha tr&agrave; t&ugrave;y theo kinh nghiệm b&iacute; quyết của mỗi người, t&ugrave;y v&agrave;o chất lượng v&agrave; hương vị của mỗi loại tr&agrave; n&ecirc;n pha loại ấm n&agrave;o. Trước khi pha tr&agrave; phải tr&aacute;ng ấm bằng nuớc s&ocirc;i cho n&oacute;ng trước rồi cho tr&agrave; v&agrave;o, khi pha xong đậy nắp k&iacute;n tiếp tục r&oacute;t nước s&ocirc;i từ tr&ecirc;n nắp xuống như tắm ấm để giữ nhiệt độ n&oacute;ng trong ấm gi&uacute;p cho c&aacute;c c&aacute;nh tr&agrave; được thấm đều.</span></p>\r\n<table class=\"tr-caption-container\" style=\"margin-bottom: 0.5em; width: 663.2px; padding: 4px; color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: center;\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 4px 10px 4px 5px; text-align: center;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\"><a style=\"color: #000000; text-decoration: none; outline: currentcolor none medium; margin-left: auto; margin-right: auto;\" href=\"http://2.bp.blogspot.com/-OTqDrZ3qouo/UortZJM52TI/AAAAAAAAAIA/5b5MnvKC9oQ/s1600/tra-xua-va-nay-5.jpg\"><img style=\"border: none; padding: 0px; background: transparent;\" src=\"http://2.bp.blogspot.com/-OTqDrZ3qouo/UortZJM52TI/AAAAAAAAAIA/5b5MnvKC9oQ/s640/tra-xua-va-nay-5.jpg\" width=\"640\" height=\"436\" border=\"0\" /></a></span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"tr-caption\" style=\"padding: 4px 10px 4px 5px; font-size: 9.6px; text-align: center;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\"><em><span style=\"font-size: large;\">Người bạn Tri kỉ</span></em></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table class=\"tr-caption-container\" style=\"margin-bottom: 0.5em; width: 618px; padding: 4px; color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: center; height: 15px;\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 4px 10px 4px 5px; text-align: center;\">&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td class=\"tr-caption\" style=\"padding-top: 4px; padding-right: 10px; padding-bottom: 4px; font-size: 9.6px; text-align: center;\">&nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; font-size: large;\">Những người uống tr&agrave; s&agrave;nh điệu miền Bắc thường uống tr&agrave; kh&ocirc;ng ướp hương, v&igrave; như thế sẽ kh&ocirc;ng c&ograve;n hương vị thật của tr&agrave;. Khoảng mười năm trở lại đ&acirc;y tr&agrave; được ph&aacute;t triển rộng r&atilde;i để phục vụ nhu cầu trong nước v&agrave; để xuất khẩu. Nhiều doanh nghiệp tư nh&acirc;n đ&atilde; sử dụng hương liệu để ướp tr&agrave; trong đ&oacute; c&oacute; những c&ocirc;ng ty v&agrave; cơ sở nổi tiếng cũng d&ugrave;ng kỹ thuật n&agrave;y nhằm mục đ&iacute;ch c&oacute; l&atilde;i cao. Điều đ&aacute;ng ch&uacute; &yacute; l&agrave; tr&agrave; ướp hương liệu thường c&oacute; m&ugrave;i thơm đậm hơn tr&agrave; c&oacute; hương vị thật, chỉ c&oacute; người s&agrave;nh điệu uống tr&agrave; mới biết tr&agrave; n&agrave;o ướp hương liệu v&agrave; tr&agrave; n&agrave;o l&agrave; nguy&ecirc;n chất. C&oacute; doanh nghiệp c&ograve;n d&ugrave;ng thủ đoạn dụ du kh&aacute;ch v&agrave;o uống tr&agrave; miễn ph&iacute; để rồi kh&aacute;ch phải mua tr&agrave; với c&aacute;i g&iacute;a &ldquo;cắt cổ&rdquo; mang về.<br />Ng&agrave;y tết hay trong sinh hoạt thường nhật, đến bất cứ gia đ&igrave;nh n&agrave;o hay cơ quan n&agrave;o chỉ cần nh&igrave;n cung c&aacute;ch chủ nh&agrave; pha tr&agrave;, r&oacute;t tr&agrave;, mời tr&agrave; l&agrave; c&oacute; thể thấy được người đ&oacute; c&oacute; s&agrave;nh văn h&oacute;a uống tr&agrave; hay kh&ocirc;ng, chưa n&oacute;i đến nghệ thuật pha tr&agrave;. Thế nhưng, d&ugrave; con người đang ở bất cứ trạng th&aacute;i n&agrave;o khi c&oacute; t&aacute;ch tr&agrave; tr&ecirc;n tay cũng gi&uacute;p cho người ta thấy lịch l&atilde;m thư th&aacute;i. Ch&iacute;nh v&igrave; vậy tr&agrave; kh&ocirc;ng chỉ l&agrave; một thức uống mang đậm bản sắc văn h&oacute;a trong đời sống m&agrave; c&ograve;n được c&aacute;c nh&agrave; l&agrave;m nghệ thuật s&acirc;n khấu v&agrave; điện ảnh thường sử dụng như một đạo cụ, một phương tiện để c&aacute;c nh&acirc;n vật giao lưu gi&uacute;p cho diễn vi&ecirc;n n&acirc;ng cao tr&igrave;nh độ diễn xuất. Mới đ&acirc;y Cục điện ảnh chủ trương kh&ocirc;ng đưa thuốc l&aacute; l&ecirc;n phim th&igrave; văn h&oacute;a uống tr&agrave; của d&acirc;n tộc ta c&agrave;ng được n&acirc;ng cao gi&aacute; trị tr&ecirc;n m&agrave;n bạc v&agrave; trong cuộc sống của cộng đồng người Việt Nam.</span></p>', '2021-01-19 09:44:31', ''),
(7, 2, 'Văn hóa trà Việt', 'blog6.jpg', '<p><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Tr&agrave; Việt được coi l&agrave; một thức uống kh&ocirc;ng thể thiếu trong sinh hoạt h&agrave;ng ng&agrave;y, kh&ocirc;ng những c&oacute; lợi cho sức khỏe m&agrave; c&ograve;n l&agrave; nghi thức văn ho&aacute; giao tiếp giữa con người với con người, l&agrave; th&uacute; vui tinh thần khi ngồi y&ecirc;n lặng nh&acirc;m nhi ch&eacute;n tr&agrave;, ngẫm nghĩ về cuộc sống nh&acirc;n sinh.</span></p>', '<p style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: justify;\"><span style=\"font-size: large; font-family: arial, helvetica, sans-serif;\"><a style=\"color: #f70413; text-decoration: none; outline: none;\" href=\"http://greentea-vietnam.blogspot.com/2013/06/tra-xua-va-nay.html\" target=\"_blank\" rel=\"noopener\"><span style=\"font-size: 12pt; color: #000000;\">Tr&agrave; Việt</span></a><span style=\"font-size: 12pt; color: #000000;\">&nbsp;được coi l&agrave; một thức uống kh&ocirc;ng thể thiếu trong sinh hoạt h&agrave;ng ng&agrave;y, kh&ocirc;ng những c&oacute; lợi cho sức khỏe m&agrave; c&ograve;n l&agrave; nghi thức&nbsp;văn ho&aacute;&nbsp;giao tiếp giữa con người với con người, l&agrave; th&uacute; vui tinh thần khi ngồi y&ecirc;n lặng nh&acirc;m nhi ch&eacute;n tr&agrave;, ngẫm nghĩ về cuộc sống nh&acirc;n sinh.</span></span></p>\r\n<p class=\"separator\" style=\"color: #555555; font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; clear: both; text-align: center;\"><span style=\"font-size: 12pt; color: #000000; font-family: arial, helvetica, sans-serif;\"><a style=\"color: #000000; text-decoration: none; outline: currentcolor none medium; margin-left: 1em; margin-right: 1em;\" href=\"http://3.bp.blogspot.com/-6tSv3Q9VN78/UoroZhZ4ioI/AAAAAAAAAHQ/O7aTAOb0nU0/s1600/van-hoa-tra-viet-1.jpg\"><img style=\"border: none; padding: 0px; background: transparent;\" title=\"Văn ho&aacute; Tr&agrave; Việt 1\" src=\"http://3.bp.blogspot.com/-6tSv3Q9VN78/UoroZhZ4ioI/AAAAAAAAAHQ/O7aTAOb0nU0/s640/van-hoa-tra-viet-1.jpg\" alt=\"văn ho&aacute; thưởng thức tr&agrave; việt, đối ẩm, độc ẩm, quần ẩm\" width=\"640\" height=\"472\" border=\"0\" /></a></span></p>\r\n<p><span style=\"font-size: 12pt; color: #000000; font-family: arial, helvetica, sans-serif;\">Doanh nh&acirc;n hướng tới tr&agrave;, thưởng thức tr&agrave; cũng kh&ocirc;ng nằm ngo&agrave;i mục đ&iacute;ch đ&oacute;. Sau những giờ l&agrave;m việc căng thẳng, mệt mỏi với những cuộc họp k&eacute;o d&agrave;i, v&ocirc; số những bản hợp đồng, giấy tờ c&ocirc;ng văn ngổn ngang... th&igrave; những gi&acirc;y ph&uacute;t hiếm hoi thư gi&atilde;n b&ecirc;n t&aacute;ch tr&agrave; c&ugrave;ng gia đ&igrave;nh, c&ugrave;ng bạn b&egrave; th&acirc;n th&iacute;ch quả l&agrave; một điều đ&aacute;ng qu&yacute;. Vậy họ đ&atilde; thật sự hiểu về n&eacute;t&nbsp;văn h&oacute;a tr&agrave; Việt&nbsp;hay chưa? B&agrave;i viết sau đ&acirc;y sẽ l&agrave; c&aacute;i nh&igrave;n kh&aacute;i qu&aacute;t về&nbsp;văn h&oacute;a tr&agrave; Việt Nam.</span></p>\r\n<h3 style=\"margin-bottom: 16px; font-weight: normal; line-height: 1; font-size: 20px;\"><span style=\"font-size: 12pt; color: #000000; font-family: arial, helvetica, sans-serif;\"><strong>Bản sắc văn h&oacute;a tr&agrave; Việt</strong></span></h3>\r\n<p><span style=\"font-size: 12pt; color: #000000; font-family: arial, helvetica, sans-serif;\">Tr&agrave;&nbsp;l&agrave; quốc thủy của&nbsp;người Việt, l&agrave; tinh chất của nền văn minh l&uacute;a nước, tr&agrave; đi v&agrave;o t&acirc;m hồn Việt một c&aacute;ch tự nhi&ecirc;n, tĩnh lặng.&nbsp;Văn h&oacute;a tr&agrave; Việt&nbsp;kh&ocirc;ng chỉ thể hiện l&agrave; một thứ thức uống phổ biến trong đời sống sinh hoạt của người d&acirc;n m&agrave; c&ograve;n trở th&agrave;nh phong tục tập qu&aacute;n, l&agrave; th&uacute; vui thanh cao của&nbsp;người Việt.<br />Từ rất l&acirc;u rồi người Việt đ&atilde; c&oacute; tục pha tr&agrave; với nước s&ocirc;i h&atilde;m n&oacute;ng để uống.&nbsp;Tr&agrave;&nbsp;hiện diện khắp nơi, từ mảnh vườn sau nh&agrave; ở đồng bằng, l&agrave;m bạn với c&acirc;y cọ Trung du, hay mọc th&agrave;nh rừng cổ thụ h&agrave;ng vạn c&acirc;y ở miền n&uacute;i.&nbsp;Người Việt&nbsp;d&ugrave; ở tầng lớp n&agrave;o, l&agrave; người miền xu&ocirc;i hay miền ngược, từ th&agrave;nh thị đến n&ocirc;ng th&ocirc;n cũng đều coi trọng n&eacute;t&nbsp;văn h&oacute;a tr&agrave; Việt.<br />Trong những dịp lễ tết, lễ ma chay, cưới hỏi, tr&agrave; l&agrave; thứ kh&ocirc;ng thể thiếu.&nbsp;Tr&agrave;&nbsp;xuất hiện tr&ecirc;n ban gia ti&ecirc;n của mỗi gia đ&igrave;nh, tr&ecirc;n m&acirc;m lễ hỏi của nh&agrave; trai sang nh&agrave; g&aacute;i, tr&agrave; d&ugrave;ng để mời kh&aacute;ch khứa, họ h&agrave;ng hai b&ecirc;n... Vui cũng&nbsp;tr&agrave;, m&agrave; buồn cũng tr&agrave;.<br />C&aacute;ch thưởng thức tr&agrave; ướp hương với&nbsp;tr&agrave; sen, nh&agrave;i ng&acirc;u s&oacute;i, c&uacute;c, thủy ti&ecirc;n, lan...vừa thể hiện được sự sang trọng, lịch l&atilde;m, vừa thể hiện sự giao h&ograve;a giữa thi&ecirc;n nhi&ecirc;n, cỏ c&acirc;y hoa l&aacute; v&agrave; con người.<br />Người Việt&nbsp;thưởng thức tr&agrave; theo từng ngụm nhỏ để thưởng thức hết c&aacute;i thơm ngọt của tr&agrave; v&agrave; cảm nhận hơi ấm của&nbsp;<em>ch&eacute;n tr&agrave;</em>&nbsp;đủ n&oacute;ng b&agrave;n tay khi m&ugrave;a đ&ocirc;ng lạnh gi&aacute;. Uống để đ&aacute;p lại l&ograve;ng mến kh&aacute;ch của người d&acirc;ng tr&agrave;, để dốc bầu t&acirc;m sự. Ch&iacute;nh những c&aacute;ch thể hiện n&agrave;y tạo n&ecirc;n n&eacute;t&nbsp;<em>văn h&oacute;a</em>, sự thanh cao, t&igrave;nh tri &acirc;m, tri kỷ giữa người với người. Uống tr&agrave; đ&ocirc;i khi c&ograve;n l&agrave; sự độc thoại với nội t&acirc;m, l&agrave; sự x&eacute;t đo&aacute;n t&acirc;m l&yacute; người đối thoại, l&agrave; t&igrave;m đến sự tỉnh t&aacute;o, tĩnh t&acirc;m, xua tan mọi phiền muộn bon chen...</span></p>\r\n<h3 style=\"margin-bottom: 16px; font-weight: normal; line-height: 1; font-size: 20px;\"><span style=\"font-size: 12pt; color: #000000; font-family: arial, helvetica, sans-serif;\">C&aacute;ch d&ugrave;ng tr&agrave; thể hiện n&eacute;t văn h&oacute;a<strong> </strong>của người Việt</span></h3>\r\n<p><span style=\"font-size: 12pt; color: #000000; font-family: arial, helvetica, sans-serif;\">Người Việt d&ugrave;ng tr&agrave;&nbsp;d&ugrave; theo c&aacute;ch truyền thống n&agrave;o:&nbsp;từ độc ẩm,&nbsp;đối ẩm&nbsp;hay&nbsp;quần ẩm&nbsp;đều l&agrave;m to&aacute;t l&ecirc;n c&aacute;i n&eacute;t văn h&oacute;a thuần chất của&nbsp;người Việt. Người xưa c&oacute; c&acirc;u: \"Nhất thủy, nh&igrave; tr&agrave;, tam b&ocirc;i, tứ b&igrave;nh, ngũ quần anh\", đ&oacute; ch&iacute;nh l&agrave;&nbsp;c&aacute;i th&uacute;&nbsp;\"thưởng\" tr&agrave;&nbsp;thực thụ.<br />\"Nhất thủy\": để c&oacute; một ấm tr&agrave; ngon, trước ti&ecirc;n phải ch&uacute; &yacute; đến nước. Nước d&ugrave;ng pha tr&agrave; thường phải l&agrave; thứ nước mưa được hứng giữa trời, hay từ c&aacute;c nguồn suối tự nhi&ecirc;n, cầu kỳ hơn nữa l&agrave; thứ sương đọng tr&ecirc;n l&aacute; sen m&agrave; người đi thuyền hứng từng giọt v&agrave;o buổi sớm mai. Thứ nước tinh khiết đ&oacute; khi đun cũng kh&ocirc;ng được ph&eacute;p mất độ thanh tịnh để kh&ocirc;ng l&agrave;m ảnh hưởng đến hương vị của tr&agrave;.<br />\"Nh&igrave; tr&agrave;\": đứng thứ hai sau nước l&agrave; c&aacute;ch&nbsp;chọn tr&agrave;: t&ugrave;y theo sở th&iacute;ch mỗi người, c&oacute; người th&iacute;ch d&ugrave;ng tr&agrave; nguy&ecirc;n, hay ch&iacute;nh l&agrave; \"tr&agrave; mộc\" c&aacute;nh tr&agrave; sao quăn giống h&igrave;nh m&oacute;c c&acirc;u, c&aacute;nh tr&ograve;n, tr&ocirc;i tay, c&oacute; mốc trắng như mốc c&acirc;y cau.&nbsp;Tr&agrave;&nbsp;n&agrave;y được pha ở nhiệt độ 80oC, hay 165 - 170 độ F. Người th&igrave; lại th&iacute;ch thưởng thức tr&agrave; hương: l&agrave; loại tr&agrave; được ướp hương từ c&aacute;c lo&agrave;i hoa: hoa sen, c&uacute;c, nh&agrave;i, s&oacute;i, ng&acirc;u; nước pha tr&agrave; tẩm hương phải c&oacute; độ s&ocirc;i ở 200 - 205 độ F.<br />Tiếp đến l&agrave; dụng cụ&nbsp;pha tr&agrave;&nbsp;gồm&nbsp;ch&eacute;n tr&agrave;&nbsp;v&agrave;&nbsp;b&igrave;nh tr&agrave;, \"tam b&ocirc;i, tứ b&igrave;nh\": một bộ đồ tr&agrave; thường c&oacute; bốn ch&eacute;n qu&acirc;n, một ch&eacute;n tống để chuy&ecirc;n tr&agrave;; ch&eacute;n thường l&agrave; loại ch&eacute;n dạng hạt m&iacute;t (mắt tr&acirc;u). B&igrave;nh cũng c&oacute; b&igrave;nh chuy&ecirc;n v&agrave; b&igrave;nh tống. T&ugrave;y theo lối uống \"độc ẩm\", \"song ẩm\", \"tứ ẩm\" hay \"quần ẩm\" m&agrave; c&oacute; những loại b&igrave;nh tương ứng. Trước khi&nbsp;pha tr&agrave;&nbsp;phải d&ugrave;ng nước s&ocirc;i để tr&aacute;ng sơ ch&eacute;n v&agrave; b&igrave;nh.<br />V&agrave; cuối c&ugrave;ng, \"ngũ quần anh\" ch&iacute;nh l&agrave; \"bạn tr&agrave;\". T&igrave;m \"bạn tr&agrave;\" c&ograve;n kh&oacute; t&igrave;m hơn \"bạn rượu\". V&igrave; bạn tr&agrave; l&agrave; người bạn tri &acirc;m, c&ugrave;ng nhau thưởng tr&agrave;, ng&acirc;m thơ, bộc bạch nỗi niềm, hay b&agrave;n chuyện gia đ&igrave;nh, x&atilde; hội, nh&acirc;n t&igrave;nh thế th&aacute;i để cảm thấy trong tr&agrave; c&oacute; cả hương vị của đất trời, cỏ c&acirc;y.<br />Khi&nbsp;thưởng thức tr&agrave; c&oacute; thể d&ugrave;ng c&aacute;c đồ ăn nhẹ k&egrave;m với tr&agrave;: kẹo lạc, kẹo vừng thanh, kẹo cu đơ, b&aacute;nh cốm, b&aacute;nh đậu xanh.</span></p>', '2021-01-19 10:05:18', ''),
(8, 3, 'Cách pha trà', 'blog8.jpg', '<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">C&aacute;ch pha tr&agrave; b&aacute;t ti&ecirc;n v&agrave; uống tr&agrave; b&aacute;t ti&ecirc;n cũng c&oacute; nhiều đặc biệt th&uacute; vị m&agrave; bạn sẽ được hướng dẫn sau đ&acirc;y:<br />N&agrave;o ch&uacute;ng ta c&ugrave;ng chuẩn bị<br />&ndash; Nước đun s&ocirc;i<br />&ndash; Ấm tr&agrave;, ch&eacute;n tr&agrave; đ&atilde; được rửa sạch<br />&ndash; Tr&agrave;</span></p>', '<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">C&aacute;ch pha tr&agrave; b&aacute;t ti&ecirc;n v&agrave; uống tr&agrave; b&aacute;t ti&ecirc;n cũng c&oacute; nhiều đặc biệt th&uacute; vị m&agrave; bạn sẽ được hướng dẫn sau đ&acirc;y:</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">N&agrave;o ch&uacute;ng ta c&ugrave;ng chuẩn bị</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">&ndash; Nước đun s&ocirc;i</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">&ndash; Ấm tr&agrave;, ch&eacute;n tr&agrave; đ&atilde; được rửa sạch</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">&ndash; Tr&agrave;</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">C&aacute;ch pha tr&agrave;:</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 1: Đầu ti&ecirc;n bạn r&oacute;t một &iacute;t nước s&ocirc;i v&agrave;o ấm (khoảng nửa ấm) v&agrave; l&aacute;ng đều bạn r&oacute;t ra từng ch&eacute;n v&agrave; đổ đi c&ocirc;ng đoạn n&agrave;y c&oacute; &yacute; nghĩa l&agrave; sử dụng nước s&ocirc;i để tiệt tr&ugrave;ng v&agrave; l&agrave;m n&oacute;ng ấm tr&agrave; gi&uacute;p tr&agrave; kh&ocirc;ng bị mất nhiệt khi pha tr&agrave;</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 2: Cho tr&agrave; v&agrave;o ấm th&ocirc;ng thường loại tr&agrave; n&agrave;y đậm đặc hơn do vậy bạn n&ecirc;n pha nhạt hơn (đặc biệt lưu &yacute; điều n&agrave;y) thường th&igrave; sử dụng ấm loại vừa bạn cho từ 6 &ndash; 8 gram tr&agrave;, với loại tr&agrave; kh&aacute;c bạn cho 8-10 gram. Sau khi pha nhiều sẽ c&oacute; kinh nghiệm v&agrave; lựa theo khẩu vị để được ấm tr&agrave; ngon.</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 3: R&oacute;t nước s&ocirc;i v&agrave;o ấm. Bạn n&ecirc;n r&oacute;t sắp sắp lượng tr&agrave; th&ocirc;ng thường th&igrave; dưới nửa ấm sau đ&oacute; l&aacute;ng nhẹ v&agrave; r&oacute;t bỏ nước n&agrave;y. C&ocirc;ng đoạn n&agrave;y c&oacute; &yacute; nghĩa l&agrave;m n&oacute;ng b&uacute;p tr&agrave; v&agrave; khử bụi tr&agrave;</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 4: R&oacute;t v&agrave;o ấm lưng ấm tr&agrave; sao cho nước c&aacute;ch miệng ấm khoảng 1 đốt ng&oacute;n tay. Đậy nắp v&agrave; r&oacute;t đều tr&ecirc;n nắp ấm cho nước chảy tr&agrave;n xuống ph&iacute;a dưới. Bạn n&ecirc;n sử dụng loại khay c&oacute; chứa nước. &Yacute; nghĩa của c&ocirc;ng đoạn n&agrave;y l&agrave; cấp nhiệt l&agrave;m n&oacute;ng đều cả trong v&agrave; ngo&agrave;i ấm tr&agrave;.</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 5: Để ấm tr&agrave; khoảng 3 ph&uacute;t r&oacute;t ra v&agrave; thưởng thức. N&ecirc;n r&oacute;t mỗi ch&eacute;n một &iacute;t v&agrave; cứ vậy r&oacute;t đều ra c&aacute;c ch&eacute;n th&agrave;nh nhiều lần r&oacute;t. &Yacute; nghĩa l&agrave; l&agrave;m đều lượng tr&agrave; ngấm ra đều cho c&aacute;c ch&eacute;n</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Lưu &yacute;: Loại tr&agrave; b&aacute;t ti&ecirc;n c&oacute; thể sử dụng pha được nhiều nước hơn c&aacute;c loại tr&agrave; kh&aacute;c. Khi pha cần sử dụng nước mới đun s&ocirc;i từ 90 &ndash; 100 độ kh&ocirc;ng sử dụng nước nguội sẽ l&agrave;m giảm dư vị của tr&agrave;. N&ecirc;n sử dụng nước mưa đ&atilde; qua lọc hoặc nước m&aacute;y đ&atilde; qua lọc để đảm bảo độ tinh khiết kh&ocirc;ng l&agrave;m giảm chất lượng tr&agrave;.</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Thưởng thức !!!</span></p>', '2021-01-19 10:22:37', ''),
(10, 1, '5 loại trà giảm cân', 'blog7.jpg', '<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Những loại tr&agrave; giảm c&acirc;n hiệu quả như tr&agrave; xanh thường c&oacute; chứa caffeine g&acirc;y mất ngủ. Thế n&ecirc;n đối với tr&agrave; giảm c&acirc;n ban đ&ecirc;m th&igrave; bạn n&ecirc;n chọn những loại tr&agrave; thảo mộc kh&ocirc;ng c&oacute; chứa caffeine. Như vậy bạn vừa ngủ ngon m&agrave; lại vừa giảm c&acirc;n trong l&uacute;c ngủ.</span></p>', '<p style=\"text-align: justify;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Những loại tr&agrave; giảm c&acirc;n hiệu quả như tr&agrave; xanh thường c&oacute; chứa caffeine g&acirc;y mất ngủ. Thế n&ecirc;n đối với tr&agrave; giảm c&acirc;n ban đ&ecirc;m th&igrave; bạn n&ecirc;n chọn những loại tr&agrave; thảo mộc kh&ocirc;ng c&oacute; chứa caffeine. Như vậy bạn vừa ngủ ngon m&agrave; lại vừa giảm c&acirc;n trong l&uacute;c ngủ.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Nhiều người cứ nghĩ rằng ngủ nhiều sẽ g&acirc;y b&eacute;o ph&igrave;. Thế nhưng sự thật l&agrave; trong l&uacute;c ngủ th&igrave; cơ thể ch&uacute;ng ta sẽ vẫn li&ecirc;n tục đốt năng lượng. Nhờ vậy m&agrave; bạn ho&agrave;n to&agrave;n c&oacute; thể giảm c&acirc;n nhờ việc ngủ đủ giấc.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">V&agrave; sau đ&acirc;y l&agrave; 5 loại tr&agrave; giảm c&acirc;n ban đ&ecirc;m tốt nhất. Vừa gi&uacute;p bạn giảm c&acirc;n m&agrave; lại vừa kh&ocirc;ng g&acirc;y mất ngủ.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">1.&nbsp;&nbsp;&nbsp; Tr&agrave; kỷ tử</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">2.&nbsp;&nbsp;&nbsp; Tr&agrave; gừng</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">3.&nbsp;&nbsp;&nbsp; Tr&agrave; bột nghệ</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">4.&nbsp;&nbsp;&nbsp; Tr&agrave; Rooibos</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">5.&nbsp;&nbsp;&nbsp; Tr&agrave; bồ c&ocirc;ng anh</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Tr&agrave; kỷ tử</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Kỷ tử l&agrave; một trong những th&agrave;nh phần hay gặp trong thuốc Đ&ocirc;ng Y. Những tr&aacute;i m&agrave;u đỏ m&agrave; bạn hay thấy trong những thang thuốc ch&iacute;nh l&agrave; kỷ tử.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Kỷ tử c&oacute; nhiều t&aacute;c dụng tốt cho sức khoẻ. V&agrave; một trong số n&agrave;y đ&oacute; ch&iacute;nh l&agrave; khả năng ngừa bệnh tiểu đường v&agrave; giảm c&acirc;n. V&agrave; loại thảo mộc n&agrave;y rất th&iacute;ch hợp để l&agrave;m tr&agrave; giảm c&acirc;n ban đ&ecirc;m.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Trong một nghi&ecirc;n cứu th&igrave; một nh&oacute;m những người t&igrave;nh nguyện được cho uống nước kỷ tử sau khi ăn. C&ograve;n nh&oacute;m kh&aacute;c th&igrave; kh&ocirc;ng.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">C&aacute;c nh&agrave; nghi&ecirc;n cứu nhận thấy l&agrave; nh&oacute;m được cho uống nước kỷ tự đốt năng lượng nhiều hơn 10% so với nh&oacute;m c&ograve;n lại. V&agrave; t&aacute;c dụng n&agrave;y k&eacute;o d&agrave;i l&ecirc;n đến 4 giờ đồng hồ.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Điều n&agrave;y c&oacute; nghĩa l&agrave; nếu bạn d&ugrave;ng kỷ tử l&agrave;m tr&agrave; giảm c&acirc;n ban đ&ecirc;m. Th&igrave; bạn c&oacute; thể đốt nhiều năng lượng hơn b&igrave;nh thường trong v&ograve;ng nhiều giờ.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Bạn c&oacute; thể mua kỷ tử ở c&aacute;c tiệm thuốc Đ&ocirc;ng Y. V&agrave; kết hợp với c&aacute;c loại thảo mộc kh&aacute;c như t&aacute;o đỏ v&agrave; hoa c&uacute;c để l&agrave;m tr&agrave; giảm c&acirc;n ban đ&ecirc;m hay tr&agrave; detox cũng rất tốt.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">C&aacute;ch kết hợp n&agrave;y sẽ vừa gi&uacute;p bạn giảm c&acirc;n hiệu quả. M&agrave; lại vừa gi&uacute;p bạn đẹp da v&agrave; ngủ ngon hơn b&igrave;nh thường. V&igrave; hoa c&uacute;c v&agrave; t&aacute;o đỏ đều c&oacute; khả năng gi&uacute;p ngủ ngon v&agrave; detox tốt cho gan.</span></p>\r\n<p style=\"text-align: justify;\"><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Tr&agrave; gừng</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Gừng được xem l&agrave; một trong những loại thảo mộc lẫn gia vị tốt nhất thế giới. Gừng xuất hiện trong rất nhiều m&oacute;n ăn của nhiều nước. Đồng thời cũng l&agrave; một vị thuốc được sử dụng rỗng r&atilde;i.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Nhiều nghi&ecirc;n cứu cho thấy tr&agrave; gừng c&oacute; khả năng gi&uacute;p giảm c&acirc;n hiệu quả. Đặc biệt l&agrave; khả năng ngừa b&eacute;o ph&igrave; v&agrave; giảm mỡ ở v&ugrave;ng eo v&agrave; h&ocirc;ng.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Bạn c&oacute; thể mua gừng ở bất kỳ chợ hay si&ecirc;u thị n&agrave;o. Bạn cũng c&oacute; thể l&agrave;m tr&agrave; gừng từ gừng tươi hay gừng kh&ocirc; đều tốt. Nhưng lưu &yacute; l&agrave; kh&ocirc;ng n&ecirc;n d&ugrave;ng tr&agrave; gừng ho&agrave; tan.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Tr&agrave; gừng ho&agrave; tan về cơ bản l&agrave; tốt v&agrave; tiện dụng. Thế nhưng nếu bạn đang cần giảm c&acirc;n th&igrave; kh&ocirc;ng n&ecirc;n d&ugrave;ng loại tr&agrave; n&agrave;y. V&igrave; tr&agrave; gừng ho&agrave; tan được chủ yếu từ đường v&agrave; nước gừng.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Đường c&oacute; gi&aacute; trị năng lương cao n&ecirc;n sẽ l&agrave;m giảm những t&aacute;c dụng giảm c&acirc;n m&agrave; gừng mang lại.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Mặc d&ugrave; kh&ocirc;ng trị mất ngủ nhưng uống tr&agrave; gừng trước khi ngủ c&oacute; thể phần n&agrave;o gi&uacute;p bạn ngủ ngon hơn. V&igrave; tr&agrave; gừng gi&uacute;p &lsquo;&ecirc;m bụng&rsquo; n&ecirc;n bạn sẽ ngủ say giấc hơn.</span></p>\r\n<p style=\"text-align: justify;\"><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Tr&agrave; bột nghệ</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Cho một &iacute;t tinh bột nghệ v&agrave;o t&aacute;ch nước ấm th&igrave; bạn sẽ c&oacute; ngay một t&aacute;ch tr&agrave; giảm c&acirc;n đ&ecirc;m hiệu quả! Kh&ocirc;ng chỉ đơn giản</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Đ&atilde; c&oacute; rất nhiều nghi&ecirc;n cứu n&oacute;i về những t&aacute;c dụng tốt của bột nghệ rồi. V&agrave; khả năng giảm c&acirc;n của nghệ cũng đ&atilde; được xem x&eacute;t đến.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Theo một nghi&ecirc;n cứu tr&ecirc;n những người thừa c&acirc;n th&igrave; th&agrave;nh phần cucurmin trong nghệ c&oacute; t&aacute;c dụng tăng cường đốt mỡ v&agrave; gi&uacute;p giảm c&acirc;n.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Nghi&ecirc;n cứu tr&ecirc;n động vật c&ograve;n cho thấy việc nghệ c&ograve;n c&oacute; thể gi&uacute;p hỗ trợ chứng thiếu ngủ. Nhờ v&agrave;o việc nghệ c&oacute; khả năng gi&uacute;p thư gi&atilde;n đồng thời giảm trầm cảm.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">&nbsp;Bột nghệ v&agrave; chanh l&agrave; combo detox l&agrave; giảm c&acirc;n rất được ưa chuộng.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Cũng như nhiều sản phẩm từ bột kh&aacute;c th&igrave; nghệ rất dễ l&agrave;m giả. Nhiều sản phẩm tinh bột nghệ gi&aacute; rẻ b&aacute;n tr&ecirc;n c&aacute;c s&agrave;n thương mại điện tử đều c&oacute; lượng tinh bột nghệ thấp.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Tinh bột nghệ tốt sẽ được l&agrave;m từ c&acirc;y nghệ gi&agrave; (trồng mất thời gian) v&agrave; chỉ lấy phần bột tinh m&agrave; th&ocirc;i (mất c&ocirc;ng). Thế n&ecirc;n gi&aacute; kh&ocirc;ng thể n&agrave;o m&agrave; rẻ được.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Hiện nay nghệ cũng được trồng ở nhiều v&ugrave;ng qu&ecirc;. V&agrave; nhiều nơi cũng sản xuất tinh bột nghệ 100% c&oacute; chất lượng tốt. Thế n&ecirc;n bạn c&oacute; thể nhờ người quen ở qu&ecirc; hay t&igrave;m mua nơi uy t&iacute;n th&igrave; sẽ an t&acirc;m hơn.</span></p>\r\n<p style=\"text-align: justify;\"><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Tr&agrave; Rooibos</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Tr&agrave; rooibos hay c&ograve;n gọi l&agrave; hồng tr&agrave; Nam Phi l&agrave; một loại tr&agrave; rất hay xuất hiện trong nhiều loại tr&agrave; giảm c&acirc;n của nước ngo&agrave;i. Một loại tr&agrave; giảm c&acirc;n ban đ&ecirc;m của Nhật hiện kh&aacute; nổi tiếng ở Việt Nam cũng c&oacute; th&agrave;nh phần ch&iacute;nh l&agrave; Rooibos.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Rooibos l&agrave; một lo&agrave;i c&acirc;y dạng bụi mọc nhiều ở Nam Phi. Loại c&acirc;y n&agrave;y th&igrave; phơi kh&ocirc; l&agrave;m th&agrave;nh tr&agrave; th&igrave; c&oacute; m&agrave;u sắc v&agrave; hương vị gần giống với hồng tr&agrave;.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Ch&iacute;nh v&igrave; vậy n&ecirc;n Rooibos c&ograve;n được gọi l&agrave; hồng tr&agrave; Nam Phi. Tuy nhi&ecirc;n, kh&aacute;c với hồng tr&agrave; thật sự th&igrave; rooibos kh&ocirc;ng c&oacute; chứa caffeine. N&ecirc;n loại tr&agrave; n&agrave;y kh&ocirc;ng hề g&acirc;y mất ngủ. N&ecirc;n bạn c&oacute; thể d&ugrave;ng tr&agrave; rooibos để l&agrave;m tr&agrave; giảm c&acirc;n ban đ&ecirc;m.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Hồng tr&agrave; Nam Phi gi&uacute;p bạn giảm c&acirc;n bằng c&aacute;ch giảm cảm gi&aacute;c th&egrave;m ăn. V&igrave; loại tr&agrave; n&agrave;y gi&uacute;p l&agrave;m tăng một loại nội tiết tố trong cơ thể c&oacute; t&ecirc;n l&agrave; leptin. Khi lượng leptin tăng cao th&igrave; cơ thể sẽ tự biết l&agrave; đ&atilde; đủ năng lượng v&agrave; kh&ocirc;ng tạo cảm gi&aacute;c đ&oacute;i.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Kh&ocirc;ng chỉ vậy m&agrave; tr&agrave; rooibos c&ograve;n gi&uacute;p hạn chế sự h&igrave;nh th&agrave;nh của m&ocirc; mỡ. Đồng thời gi&uacute;p tăng cường sự chuyển ho&aacute; của mỡ trong cơ thể.</span></p>\r\n<p style=\"text-align: justify;\"><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Tr&agrave; bồ c&ocirc;ng anh</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Một trong những &lsquo;chi&ecirc;u thức&rsquo; của một số loại tr&agrave; giảm c&acirc;n ban đ&ecirc;m đ&oacute; l&agrave; th&ecirc;m chất k&iacute;ch th&iacute;ch nhuận tr&agrave;ng. N&oacute;i một c&aacute;ch đơn giản l&agrave; bạn sẽ đi ngo&agrave;i nhiều hơn.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Khi đi ngo&agrave;i nhiều th&igrave; bạn sẽ mất một lượng nước lớn. Nhờ vậy m&agrave; khi uống những loại tr&agrave; giảm c&acirc;n ban đ&ecirc;m n&agrave;y. Th&igrave; c&oacute; trường hợp sẽ thấy m&igrave;nh giảm c&acirc;n rất nhanh.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Nhưng sự thật l&agrave; bạn chỉ mất c&acirc;n nặng nước m&agrave; th&ocirc;i. Chứ lượng mỡ kh&ocirc;ng hề thay đổi. Ngo&agrave;i ra th&igrave; những chất n&agrave;y c&ograve;n c&oacute; thể g&acirc;y bệnh tim hay ung thư. Bạn chỉ cần t&igrave;m kiếm chất cấm trong tr&agrave; giảm c&acirc;n th&igrave; sẽ ra rất nhiều b&agrave;i viết về vấn đề n&agrave;y.</span><br /><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Nếu bạn vẫn muốn giảm lượng c&acirc;n nặng nước của cơ thể th&igrave; c&oacute; thể uống tr&agrave; bồ c&ocirc;ng anh. Trong tr&agrave; bồ c&ocirc;ng anh c&oacute; nhiều th&agrave;nh phần kali gi&uacute;p bạn đi tiểu nhiều hơn.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Nhờ vậy sẽ tống bớt lượng nước thừa trong cơ thể. Qua đ&oacute; kh&ocirc;ng chỉ giảm c&acirc;n nặng của bạn m&agrave; c&ograve;n gi&uacute;p bạn tr&ocirc;ng đỡ b&eacute;o hơn so với l&uacute;c b&igrave;nh thường.</span><br /><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Nếu c&oacute; điều kiện th&igrave; bạn c&oacute; thể mua loại tr&agrave; giảm c&acirc;n ban đ&ecirc;m của Nhật n&agrave;y về d&ugrave;ng. C&ograve;n tiết kiệm hơn th&igrave; c&oacute; thể tự l&agrave;m ra một blend tương tự.</span><br /><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">M&igrave;nh gợi &yacute; l&agrave; kết hợp tr&agrave; rooibos, gừng kh&ocirc;, kỷ tử v&agrave; tr&agrave; bồ c&ocirc;ng anh. Nếu uống ngọt th&igrave; c&oacute; thể d&ugrave;ng th&ecirc;m cỏ ngọt. V&agrave; muốn ngủ ngon th&igrave; c&oacute; thể th&ecirc;m hoa c&uacute;c kh&ocirc;.</span><br /><br /></p>', '2021-01-19 11:00:23', '');
INSERT INTO `tbl_bai_viet` (`ma_bai_viet`, `ma_loai_bai_viet`, `tieu_de`, `anh_minh_hoa`, `mo_ta`, `noi_dung`, `ngay_dang`, `tac_gia`) VALUES
(11, 3, 'Một số mẹo giúp pha trà ngon', 'blog3.jpg', '<p style=\"text-align: justify;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Mọi người thường nghĩ pha tr&agrave; rất c&ocirc;ng phu phức tạp, n&ecirc;n cũng h&igrave;nh th&agrave;nh 2 &ldquo;trường ph&aacute;i&rdquo;: một l&agrave; hời hợt v&igrave; kh&ocirc;ng th&iacute;ch t&igrave;m hiểu. Hai l&agrave; sa đ&agrave; v&agrave;o những phương thức huyền hoặc kh&ocirc;ng thực tế. T&ocirc;i sẽ chỉ cho bạn 3 điểm đơn giản để c&oacute; một ch&eacute;n tr&agrave; ngon.</spa', '<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Ai cũng biết c&aacute;c bước pha tr&agrave; cơ bản, l&agrave; tr&agrave; kh&ocirc; được h&atilde;m bằng nước s&ocirc;i trong ấm v&agrave; r&oacute;t ra ch&eacute;n thưởng thức. Pha tr&agrave; kh&ocirc;ng phức tạp đến mức bạn phải l&agrave; một chuy&ecirc;n gia, nhưng cũng kh&ocirc;ng phải đơn giản chỉ l&agrave; ng&acirc;m tr&agrave; trong ấm.</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">3 yếu tố quan trọng nhất trong pha tr&agrave;</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">B&iacute; quyết để pha tr&agrave; ngon nằm trong 3 yếu tố: nhiệt độ nước, lượng tr&agrave; v&agrave; thời gian h&atilde;m.</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Nhiệt độ nước</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Tất nhi&ecirc;n pha tr&agrave; phải d&ugrave;ng nước n&oacute;ng, nhưng n&oacute;ng bao nhi&ecirc;u th&igrave; mỗi loại tr&agrave; lại th&iacute;ch hợp với một nhiệt độ kh&aacute;c nhau. Nước vừa s&ocirc;i chỉ ph&ugrave; hợp với tr&agrave; đen hoặc tr&agrave; &ocirc; long gi&agrave;. N&oacute; cần nhiệt độ cao để ph&aacute; vỡ c&aacute;c kết cấu v&agrave; ph&aacute;t t&aacute;n hương vị. Nhưng phải d&ugrave;ng nước nguội hơn cho c&aacute;c loại tr&agrave; c&oacute; hương vị tinh tế, như tr&agrave; xanh, tr&agrave; &ocirc; long.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Nước qu&aacute; n&oacute;ng sẽ l&agrave;m tr&agrave; bị &ldquo;ch&aacute;y&rdquo;, l&agrave;m cho tr&agrave; bị đắng ch&aacute;t v&agrave; mất đi c&aacute;c hương vị tinh tế. Nhưng nước qu&aacute; nguội cũng sẽ l&agrave;m hương vị tr&agrave; yếu đi rất nhiều v&igrave; c&aacute;c hợp chất trong tr&agrave; kh&ocirc;ng được h&ograve;a tan.</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Lượng tr&agrave;</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Lượng tr&agrave; qu&aacute; nhiều sẽ l&agrave;m tr&agrave; qu&aacute; đắng. Nhưng qu&aacute; &iacute;t tr&agrave; th&igrave; hương vị sẽ rất yếu kh&ocirc;ng đủ thưởng thức. Tuỳ v&agrave;o từng loại tr&agrave; sẽ c&oacute; định lượng kh&aacute;c nhau. Một tỉ lệ m&agrave; c&aacute;c bạn c&oacute; thể bắt đầu thử l&agrave; 8g tr&agrave; cho một ấm 300ml. Sau đ&oacute; bạn c&oacute; thể gia giảm lượng tr&agrave; cho ph&ugrave; hợp với khẩu vị của m&igrave;nh.</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Thời gian h&atilde;m</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Kỵ nhất trong pha tr&agrave; l&agrave; &ldquo;ng&acirc;m&rdquo; tr&agrave;, nhưng lỗi n&agrave;y hầu hết mọi người đều mắc phải. N&oacute; sẽ l&agrave;m tr&agrave; qu&aacute; đắng ch&aacute;t v&agrave; c&oacute; m&ugrave;i nẫu. Giống như nhiệt độ nước, mỗi loại tr&agrave; sẽ ph&ugrave; hợp với một thời gian h&atilde;m nhất định. Tr&agrave; đen, tr&agrave; &ocirc; long c&oacute; thể ng&acirc;m l&acirc;u hơn, nhưng tr&agrave; xanh th&igrave; nhanh hơn nhiều.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Nh&igrave;n chung thời gian h&atilde;m tr&agrave; được t&iacute;nh bằng gi&acirc;y chứ kh&ocirc;ng phải bằng ph&uacute;t.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Đun nước : Một lần nữa chắc chắn rằng nước bạn c&oacute; l&agrave; nước đ&oacute;ng chai đ&atilde; được lọc, kh&ocirc;ng phải nước kho&aacute;ng hay nước m&aacute;y. Hầu hết c&aacute;c loại tr&agrave; đều pha tr&agrave; dưới nhiệt độ s&ocirc;i, trong khoảng 75&deg;C &ndash; 98&deg;C tuỳ loại.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">L&agrave;m n&oacute;ng ấm ch&eacute;n: Khi ấm đun nước gần đạt độ s&ocirc;i, bạn r&oacute;t nước v&agrave;o ấm, đậy nắp lại. Khi ấm tr&agrave; n&oacute;ng l&ecirc;n, bạn r&oacute;t hết nước ra chuy&ecirc;n tr&agrave; v&agrave; c&aacute;c ly.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Đong tr&agrave; : Cho tr&agrave; v&agrave;o ấm, lượng tr&agrave; &iacute;t nhiều tuỳ từng loại. Th&ocirc;ng thường l&agrave; 1/5 đến 1/2 ấm tr&agrave;.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Đ&aacute;nh thức tr&agrave;: r&oacute;t nước n&oacute;ng ngập tr&agrave; v&agrave; đổ đi c&agrave;ng nhanh c&agrave;ng tốt..</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Đ&acirc;y kh&ocirc;ng phải l&agrave; nước để uống. N&oacute; c&oacute; t&aacute;c dụng &ldquo;đ&aacute;nh thức&rdquo; để c&aacute;c l&aacute; tr&agrave; bắt đầu nở ra.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Nước n&oacute;ng đ&aacute;nh thức tr&agrave; kh&ocirc;ng phải l&agrave; nước s&ocirc;i.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">H&atilde;m tr&agrave;: Đổ nước n&oacute;ng v&agrave;o đầy ấm, đậy nắp ấm v&agrave; h&atilde;m tr&agrave; trong khoảng 10-40 gi&acirc;y tuỳ loại tr&agrave;. Đ&acirc;y l&agrave; c&ocirc;ng đoạn quan trọng nhất. H&atilde;y đảm bảo nhiệt độ nước v&agrave; thời gian h&atilde;m ph&ugrave; hợp với loại tr&agrave;.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">R&oacute;t tr&agrave;: sau 10-40 gi&acirc;y, r&oacute;t hết nước từ ấm tr&agrave; v&agrave;o chuy&ecirc;n. Rồi từ chuy&ecirc;n mới r&oacute;t ra c&aacute;c ch&eacute;n uống tr&agrave;. Chuy&ecirc;n tr&agrave; l&uacute;c n&agrave;y rất quan trọng, gi&uacute;p bạn ngừng ngay qu&aacute; tr&igrave;nh h&atilde;m tr&agrave; trong ấm. Bạn n&ecirc;n r&oacute;t nhanh v&agrave; r&oacute;t hết nước trong ấm ra chuy&ecirc;n.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Đảm bảo nước trong ấm được r&oacute;t ra hết, kh&ocirc;ng để nước dư trong ấm.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Mở nắp ấm sau khi r&oacute;t tr&agrave; ra chuy&ecirc;n, để tr&agrave; kh&ocirc;ng bị &ldquo;nẫu&rdquo; v&igrave; nhiệt độ cao trong ấm.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">H&atilde;m tr&agrave; lần tiếp theo : Lặp lại bước 5 v&agrave; bước 6 cho c&aacute;c lần pha tiếp theo. Lần h&atilde;m sau thường c&oacute; thời gian l&acirc;u hơn so với lần pha trước.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Nếu nước tr&agrave; đầu ti&ecirc;n qu&aacute; nhạt hoặc qu&aacute; đậm, h&atilde;y điều chỉnh thời gian ở lần h&atilde;m tiếp theo.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Tr&agrave; ngon v&agrave; pha kh&eacute;o, bạn c&oacute; thể lặp lại 5-8 lần h&atilde;m tr&agrave;, trước khi hương vị trở n&ecirc;n qu&aacute; nhạt.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Một số mẹo nhỏ gi&uacute;p pha tr&agrave; ngon</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">H&atilde;y bắt đầu với những chiếc ấm nhỏ v&agrave; th&agrave;nh mỏng, mọi thứ sẽ dễ d&agrave;ng hơn nhiều. Nếu bạn mới bắt đầu, n&ecirc;n chọn một chiếc ấm thủy tinh hoặc ấm sứ để pha tr&agrave;. Hai loại ấm n&agrave;y sẽ phản &aacute;nh trung thực vị tr&agrave;, v&agrave; kh&ocirc;ng để lại m&ugrave;i, n&ecirc;n bạn c&oacute; thể pha được nhiều loại tr&agrave; kh&aacute;c nhau.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Nước m&aacute;y kh&ocirc;ng thể ph&agrave; tr&agrave; được. Đơn giản nhất l&agrave; d&ugrave;ng c&aacute;c loại nước đ&oacute;ng chai (nhưng kh&ocirc;ng phải l&agrave; nước kho&aacute;ng)</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Kh&ocirc;ng cần c&oacute; nhiệt kế để đo nhiệt độ. Bạn c&oacute; thể giảm nhiệt độ nước bằng c&aacute;ch đun s&ocirc;i v&agrave; để nguội dần. Nước đun trong b&igrave;nh si&ecirc;u tốc, sau 5 ph&uacute;t sẽ giảm c&ograve;n khoảng 83&deg;C</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Sợi tr&agrave; nhỏ, mỏng, xốp pha nhiệt độ thấp. Sợi tr&agrave; lớn, chắc pha nhiệt độ cao. Tr&agrave; xanh pha nhiệt độ thấp hơn. Tr&agrave; &ocirc; long, tr&agrave; đen cần nhiệt độ cao hơn.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Nếu muốn hương vị đậm hơn, h&atilde;y tăng lượng tr&agrave;, kh&ocirc;ng phải tăng nhiệt độ hay ng&acirc;m l&acirc;u hơn.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Kh&ocirc;ng c&oacute; một đ&aacute;p &aacute;n ho&agrave;n to&agrave;n ch&iacute;nh x&aacute;c về nhiệt độ nước, thời gian pha v&agrave; định lượng tr&agrave; cho mỗi loại tr&agrave;. H&atilde;y thử gia giảm 3 yếu tố đ&oacute; để kh&aacute;m ph&aacute; đ&aacute;p &aacute;n của ch&iacute;nh bạn</span></p>', '2021-01-19 13:46:44', '');
INSERT INTO `tbl_bai_viet` (`ma_bai_viet`, `ma_loai_bai_viet`, `tieu_de`, `anh_minh_hoa`, `mo_ta`, `noi_dung`, `ngay_dang`, `tac_gia`) VALUES
(12, 3, 'Cách pha trà Cung Đình Huế chuẩn vị!', 'blog9.jpg', '<p style=\"text-align: justify;\"><span style=\"color: #000000; font-family: arial, helvetica, sans-serif;\">Đ&atilde; từ l&acirc;u, tr&agrave; Cung Đ&igrave;nh Huế được coi l&agrave; một n&eacute;t văn h&oacute;a ẩm thực tốt đẹp v&agrave; qu&yacute; b&aacute;u của Việt Nam. Tr&agrave; Cung Đ&igrave;nh Huế l&agrave; loại tr&agrave; vốn chỉ d&agrave;nh cho vua ch&uacute;a v&agrave; những người qu&yacute; tộc quyền qu&yacute;. Ng&agrave;y nay, tr&agrave; Cung Đ&igrave;nh Huế đ&atilde; được sử dụng phổ', '<p style=\"box-sizing: border-box; padding: 0px; margin: 20px 0px 10px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 30px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">1. T&igrave;m hiểu tr&agrave; cung đ&igrave;nh huế&nbsp;</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Tr&agrave; Cung Đ&igrave;nh Huế l&agrave; t&ecirc;n gọi cho một b&agrave;i tr&agrave; xuất ph&aacute;t từ cung đ&igrave;nh Huế. Trước đ&acirc;y, th&uacute; uống tr&agrave;, thưởng tr&agrave; chỉ d&agrave;nh cho vua quan qu&yacute; tộc bởi quy c&aacute;ch nấu một ấm tr&agrave; rất cầu kỳ phức tạp. Tr&agrave; Cung Đ&igrave;nh Huế kh&ocirc;ng chỉ để thưởng thức m&agrave; c&ograve;n l&agrave; liều thuốc bổ cho sức khỏe.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Tr&agrave; Cung Đ&igrave;nh Huế kh&ocirc;ng chỉ c&oacute; một c&ocirc;ng thức tr&agrave; duy nhất, m&agrave; t&ugrave;y v&agrave;o đối tượng sử dụng, tr&agrave; sẽ bao gồm nhiều nguy&ecirc;n liệu với c&ocirc;ng dụng kh&aacute;c nhau tạo th&agrave;nh. Tr&agrave; d&agrave;nh cho người gi&agrave; th&igrave; c&oacute; khả năng b&igrave;nh ổn huyết &aacute;p. Tr&agrave; d&agrave;nh cho phụ nữ sẽ gi&uacute;p dưỡng nhan, l&agrave;m đẹp da. Tr&agrave; d&agrave;nh cho thanh ni&ecirc;n th&igrave; gi&uacute;p tăng cường sinh lực. B&ecirc;n cạnh đ&oacute;, cũng c&oacute; loại tr&agrave; d&agrave;nh cho người bị bệnh, tr&agrave; gi&uacute;p giảm căng thẳng, giảm stress, giảm c&acirc;n&hellip;</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; display: block; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><img class=\"size-full wp-image-4697 aligncenter\" style=\"box-sizing: border-box; padding: 0px; margin: 0px auto; outline: none !important; border: 0px; vertical-align: middle; max-width: 100%; height: auto; clear: both; display: block;\" src=\"https://jarvis.vn/wp-content/uploads/2021/01/tra-hoa-tra-cung-dinh-hue.jpg\" alt=\"Tr&agrave; hoa cung đ&igrave;nh Huế\" width=\"600\" height=\"368\" /></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: center;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><em style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\">Tr&agrave; Cung Đ&igrave;nh Huế được tạo ra từ nhiều loại thảo dược qu&yacute; v&agrave; bổ dưỡng</span></em></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Để c&oacute; được những c&ocirc;ng dụng tuyệt vời cho sức khỏe, tr&agrave; Cung Đ&igrave;nh Huế l&agrave; sự kết hợp phức tạp giữa nhiều loại thảo dược với nhau. Trong đ&oacute;, c&oacute; 16 loại l&agrave; th&agrave;nh phần cơ bản, nổi tiếng nhất gồm: hoa c&uacute;c, atiso, cỏ ngọt, ho&agrave;i sơn, đảng s&acirc;m, hồng t&aacute;o, t&aacute;o t&agrave;u, cam thảo bắc, hoa hồi, hoa nh&agrave;i, hoa h&ograve;e, thảo quyết minh, nụ vối, kỳ tử, tim sen,khổ qua. Nhờ những th&agrave;nh phần n&agrave;y, tr&agrave; Cung Đ&igrave;nh Huế đ&atilde; mang đến những c&ocirc;ng dụng tuyệt vời cho sức khỏe:</span></p>\r\n<ul style=\"box-sizing: border-box; padding: 0px 0px 0px 40px; margin: 0px; outline: none !important; list-style: unset; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px;\">Cải thiện giấc ngủ</span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\">: Tr&agrave; Cung Đ&igrave;nh c&oacute; th&agrave;nh phần thảo dược tim sen v&agrave; atiso c&oacute; t&aacute;c dụng định thần, tốt cho tim mạch. Đặc biệt, ch&uacute;ng c&oacute; khả năng loại bỏ triệu chứng hồi hộp v&agrave; gi&uacute;p người d&ugrave;ng c&oacute; giấc ngủ s&acirc;u.</span></span></p>\r\n</li>\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px;\">Cải thiện sức khỏe</span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\">: T&aacute;o đỏ, t&aacute;o m&egrave;o, atiso c&oacute; khả năng l&agrave;m giảm h&agrave;m lượng cholesterol trong m&aacute;u. Nhờ đ&oacute;, uống tr&agrave; sẽ gi&uacute;p hạn chế được xơ vữa động mạch, m&aacute;u nhiễm mỡ v&agrave; đột quỵ. Ngo&agrave;i ra, tr&agrave; cũng c&oacute; t&aacute;c dụng bổ sung kh&iacute; huyết v&agrave; ổn định huyết &aacute;p.</span></span></p>\r\n</li>\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px;\">Giải độc v&agrave; l&agrave;m đẹp</span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\">: Tr&agrave; cũng c&oacute; t&aacute;c dụng giải độc gan, bồi bổ gan. Từ đ&oacute; c&oacute; thể gi&uacute;p giảm nổi mụn, cải thiện l&agrave;n da v&agrave; cả v&oacute;c d&aacute;ng cho người d&ugrave;ng.</span></span></p>\r\n</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Ngo&agrave;i ra, tr&agrave; cũng c&oacute; t&aacute;c dụng trong việc cải thiện sinh l&yacute; nam giới, điều trị bệnh g&uacute;t v&agrave; c&aacute;c chứng sỏi thận, tiểu đường,&hellip;</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; display: block; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><img class=\"size-full wp-image-4696 aligncenter\" style=\"box-sizing: border-box; padding: 0px; margin: 0px auto; outline: none !important; border: 0px; vertical-align: middle; max-width: 100%; height: auto; clear: both; display: block;\" src=\"https://jarvis.vn/wp-content/uploads/2021/01/tra-cung-dinh-hue-tot-cho-duong-nhan-lam-dep.jpg\" alt=\"Tr&agrave; cung đ&igrave;nh Huế tốt cho dưỡng nhan v&agrave; l&agrave;m đẹp\" width=\"600\" height=\"422\" /></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: center;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><em style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\">Tr&agrave; Cung Đ&igrave;nh Huế nổi tiếng với c&ocirc;ng dụng dưỡng nhan, l&agrave;m đẹp</span></em></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Tr&ecirc;n thị trường, c&aacute;c loại tr&agrave; Cung Đ&igrave;nh Huế được lưu h&agrave;nh đ&atilde; được c&aacute;c nghệ nh&acirc;n bớt đi một số th&agrave;nh phần hoặc l&agrave; kết hợp th&ecirc;m nhiều loại thảo mộc kh&aacute;c theo c&ocirc;ng dung đ&atilde; định trước. D&ugrave; l&agrave; th&agrave;nh phần n&agrave;o th&igrave; tr&agrave; cũng sẽ đảm bảo khả năng tăng cường sức đề kh&aacute;ng cho cơ thể. Th&ecirc;m v&agrave;o đ&oacute;, tr&agrave; cũng sẽ mang hương vị thơm, ngon, ngọt th&iacute;ch hợp với mọi lứa tuổi.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Theo đ&oacute;, t&ugrave;y v&agrave;o mục đ&iacute;ch sử dụng, bạn n&ecirc;n chọn tr&agrave; Cung Đ&igrave;nh Huế với những th&agrave;nh phần ph&ugrave; hợp. Điều n&agrave;y sẽ gi&uacute;p bạn đạt được mục đ&iacute;ch sử dụng tr&agrave; một c&aacute;ch nhanh nhất.&nbsp;</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 20px 0px 10px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 30px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"2_Cach_pha_tra_cung_dinh_Hue\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px;\">2. C&aacute;ch pha tr&agrave; cung đ&igrave;nh Huế&nbsp;</span></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">C&aacute;ch pha tr&agrave; Cung Đ&igrave;nh Huế trước đ&acirc;y rất cầu kỳ, phức tạp. Từ bước chuẩn bị nguy&ecirc;n liệu, h&aacute;i v&agrave;o giờ n&agrave;o, phơi kh&ocirc; ra sao, nấu nước trong thời gian bao l&acirc;u&hellip; Đều c&oacute; quy tắc ri&ecirc;ng. Thế nhưng, hiện nay, tr&agrave; Cung Đ&igrave;nh Huế đ&atilde; được thương mại h&oacute;a, lược bỏ nhiều c&ocirc;ng đoạn rườm r&agrave;, người d&ugrave;ng chỉ cần pha tr&agrave; như tất cả c&aacute;c loại tr&agrave; kh&aacute;c. Bạn c&oacute; thể lựa chọn c&aacute;ch pha tr&agrave; uống n&oacute;ng hoặc lạnh t&ugrave;y sở th&iacute;ch.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 20px 0px 10px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 25px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"2_1_Cach_pha_tra_Cung_Dinh_Hue_nong\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px;\">2.1. C&aacute;ch pha tr&agrave; Cung Đ&igrave;nh Huế n&oacute;ng</span></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Tr&agrave; n&oacute;ng thường được pha với ấm sứ hay ấm đất nung để giữ được hương vị v&agrave; độ ấm l&acirc;u hơn. Kh&ocirc;ng chỉ với tr&agrave; Cung Đ&igrave;nh Huế m&agrave; bất cứ loại tr&agrave; n&agrave;o pha n&oacute;ng cũng n&ecirc;n sử dụng ấm sứ, ấm đất hoặc ấm thủy tinh.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 10px 0px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 24px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"Nguyen_lieu\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px;\">Nguy&ecirc;n liệu</span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\">:</span></span></p>\r\n<ul style=\"box-sizing: border-box; padding: 0px 0px 0px 40px; margin: 0px; outline: none !important; list-style: unset; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">10-15g tr&agrave; Cung Đ&igrave;nh&nbsp;</span></p>\r\n</li>\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">200 &ndash; 400ml nước s&ocirc;i</span></p>\r\n</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 10px 0px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 24px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"Cach_pha_tra\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px;\">C&aacute;ch pha tr&agrave;:</span></span></p>\r\n<ul style=\"box-sizing: border-box; padding: 0px 0px 0px 40px; margin: 0px; outline: none !important; list-style: unset; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 1: Trước ti&ecirc;n bạn cần trộn đều g&oacute;i tr&agrave;.</span></p>\r\n</li>\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 2: Tiếp theo, bạn cho tr&agrave; v&agrave;o ấm, d&ugrave;ng nước s&ocirc;i tr&aacute;ng sơ rồi đổ đi ngay. Việc tr&aacute;ng sơ sẽ gi&uacute;p đ&aacute;nh thức hương vị tr&agrave; v&agrave; loại bỏ c&aacute;c cặn, bụi bẩn d&iacute;nh tr&ecirc;n l&aacute; tr&agrave;.</span></p>\r\n</li>\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 3: Sau đ&oacute;, bạn r&oacute;t khoảng 200 &ndash; 400ml nước t&ugrave;y thể t&iacute;ch của ấm tr&agrave;.</span></p>\r\n</li>\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 4: Bạn chỉ cần h&atilde;m tr&agrave; trong khoảng 5-7 ph&uacute;t để thảo dược th&ocirc;i hết chất ra l&agrave; c&oacute; thể sử dụng được.</span></p>\r\n</li>\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 5: Cuối c&ugrave;ng, bạn r&oacute;t tr&agrave; ra ch&eacute;n v&agrave; thưởng thức ngay hương vị thơm ngon của tr&agrave;.</span></p>\r\n</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; display: block; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><img class=\"size-full wp-image-4695 aligncenter\" style=\"box-sizing: border-box; padding: 0px; margin: 0px auto; outline: none !important; border: 0px; vertical-align: middle; max-width: 100%; height: auto; clear: both; display: block;\" src=\"https://jarvis.vn/wp-content/uploads/2021/01/tra-cung-dinh-hue-co-the-dung-nong-hoac-lanh-tuy-y.jpg\" alt=\"Tr&agrave; cung đ&igrave;nh Huế c&oacute; thể sử dụng n&oacute;ng hoặc lạnh t&ugrave;y &yacute;\" width=\"600\" height=\"450\" /></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: center;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><em style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\">Tr&agrave; Cung Đ&igrave;nh Huế c&oacute; thể uống n&oacute;ng hay lạnh t&ugrave;y sở th&iacute;ch</span></em></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 20px 0px 10px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 25px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"2_2_Cach_pha_tra_Cung_Dinh_Hue_lanh\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px;\">2.2. C&aacute;ch pha tr&agrave; Cung Đ&igrave;nh Huế lạnh</span></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Nếu muốn sử dụng tr&agrave; lạnh, bạn n&ecirc;n n&ecirc;n nấu tr&agrave; trong một nồi lớn để kh&ocirc;ng cần phải pha nhiều lần trong ng&agrave;y. Thế nhưng, cũng cần lưu &yacute; l&agrave; nấu vừa đủ d&ugrave;ng trong ng&agrave;y v&agrave; kh&ocirc;ng n&ecirc;n sử dụng tr&agrave; đ&atilde; để qua đ&ecirc;m. C&aacute;ch thực hiện như sau:</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 10px 0px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 24px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"Chuan_bi_nguyen_lieu\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px;\">Chuẩn bị nguy&ecirc;n liệu</span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\">:</span></span></p>\r\n<ul style=\"box-sizing: border-box; padding: 0px 0px 0px 40px; margin: 0px; outline: none !important; list-style: unset; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">30g tr&agrave; Cung Đ&igrave;nh</span></p>\r\n</li>\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">2-3 l&iacute;t nước&nbsp;</span></p>\r\n</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 10px 0px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 24px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"Cach_pha_tra-2\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px;\">C&aacute;ch pha tr&agrave;</span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\">:</span></span></p>\r\n<ul style=\"box-sizing: border-box; padding: 0px 0px 0px 40px; margin: 0px; outline: none !important; list-style: unset; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 1: Tương tự c&aacute;ch pha tr&agrave; n&oacute;ng, trước ti&ecirc;n bạn cũng n&ecirc;n trộn đều g&oacute;i tr&agrave; l&ecirc;n để khơi dậy tr&agrave;.</span></p>\r\n</li>\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 2: Tiếp theo bạn lấy một chiếc nồi nh&ocirc;m, cho tr&agrave; c&ugrave;ng nước v&agrave;o đun s&ocirc;i.</span></p>\r\n</li>\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 3: Để tr&agrave; s&ocirc;i trong khoảng 5 ph&uacute;t th&igrave; tắt bếp, để nguội.</span></p>\r\n</li>\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 4: Sau đ&oacute; bạn chắt nước tr&agrave; ra b&igrave;nh rồi để v&agrave;o ngăn m&aacute;t tủ lạnh v&agrave; uống trong ng&agrave;y, kh&ocirc;ng để tr&agrave; qua đ&ecirc;m.</span></p>\r\n</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Một số loại thảo dược c&oacute; trong tr&agrave; như t&aacute;o đỏ, kỳ tử,.. đều l&agrave; những th&agrave;nh phần c&oacute; thể ăn được v&agrave; mang lại nhiều lợi &iacute;ch cho sức khỏe. Do đ&oacute;, sau khi pha tr&agrave; bạn c&oacute; thể sử dụng phần b&atilde; tr&agrave; để ăn như một m&oacute;n đồ ăn vặt vừa ngon vừa tiết kiệm.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 20px 0px 10px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 30px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"3_Luu_y_khi_pha_tra_cung_dinh_hue\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px;\">3. Lưu &yacute; khi pha tr&agrave; cung đ&igrave;nh huế&nbsp;</span></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Tr&agrave; Cung Đ&igrave;nh Huế rất tốt cho sức khỏe, đồng thời tr&agrave; cũng chứa dược t&iacute;nh cao. V&igrave; thế, bạn chỉ n&ecirc;n d&ugrave;ng 30-40g tr&agrave; một ng&agrave;y để tr&aacute;nh lạm dụng tr&agrave;, ảnh hưởng kh&ocirc;ng tốt đến sức khỏe.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Phụ nữ đang mang thai hoặc trẻ nhỏ n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ trước khi sử dụng. Bởi tr&agrave; Cung Đ&igrave;nh Huế c&oacute; rất nhiều loại thảo dược c&oacute; dược t&iacute;nh cao, phụ nữ mang thai n&ecirc;n cẩn thận khi sử dụng để tr&aacute;nh ảnh hưởng kh&ocirc;ng tốt đến sức khỏe.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; display: block; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><img class=\"size-full wp-image-4694 aligncenter\" style=\"box-sizing: border-box; padding: 0px; margin: 0px auto; outline: none !important; border: 0px; vertical-align: middle; max-width: 100%; height: auto; clear: both; display: block;\" src=\"https://jarvis.vn/wp-content/uploads/2021/01/khong-nen-su-dung-qua-nhieu-tra-cung-dinh-hue.jpg\" alt=\"Kh&ocirc;ng n&ecirc;n sử dụng qu&aacute; nhiều tr&agrave; Cung Đ&igrave;nh Huế\" width=\"600\" height=\"400\" /></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><em style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\">Kh&ocirc;ng n&ecirc;n uống qu&aacute; nhiều tr&agrave; Cung Đ&igrave;nh Huế trong một ng&agrave;y</span></em></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Tr&agrave; Cung Đ&igrave;nh Huế c&oacute; rất nhiều loại như như Tr&agrave; Mẫu Hậu, Tr&agrave; Minh Mạng, Tr&agrave; Qu&yacute; Phi, G8, G9, G10. Mỗi loại tr&agrave; sẽ bao gồm những th&agrave;nh phần kh&aacute;c nhau ứng với mỗi c&ocirc;ng dụng kh&aacute;c nhau. Do đ&oacute;, khi mua, bạn phải để &yacute; thật kỹ th&agrave;nh phần của tr&agrave; cũng như c&ocirc;ng dụng m&agrave; tr&agrave; mang đến cho cơ thể.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Tr&agrave; Cung Đ&igrave;nh Huế được l&agrave;m từ rất nhiều loại thảo dược qu&yacute; v&agrave; bổ dưỡng n&ecirc;n rất tốt cho sức khỏe. Ngo&agrave;i ra, tr&agrave; cũng th&iacute;ch hợp để chị em phụ nữ dưỡng da, l&agrave;m đẹp da. Tuy nhi&ecirc;n, tr&agrave; Cung Đ&igrave;nh kh&ocirc;ng phải thuốc chữa bệnh, n&oacute; chỉ c&oacute; t&aacute;cc dụng hỗ trợ sức khỏe m&agrave; th&ocirc;i. Do đ&oacute;, nếu bạn muốn c&oacute; một sức khỏe tốt v&agrave; nhan sắc đẹp th&igrave; cần bỏ thời gian tập luyện thể thao v&agrave; ăn uống khoa học, điều độ.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><em style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px;\">C&aacute;ch pha tr&agrave; Cung Đ&igrave;nh Huế</span></em>&nbsp;cũng rất đơn giản v&agrave; dễ thực hiện. Bạn c&oacute; thể tham khảo b&agrave;i viết tr&ecirc;n để bổ sung một loại thức uống tuyệt vời cho gia đ&igrave;nh nh&eacute;!</span></p>', '2021-01-19 13:55:33', '');
INSERT INTO `tbl_bai_viet` (`ma_bai_viet`, `ma_loai_bai_viet`, `tieu_de`, `anh_minh_hoa`, `mo_ta`, `noi_dung`, `ngay_dang`, `tac_gia`) VALUES
(13, 3, 'Top 3 cách phả trà bồ công anh ngon, tốt cho sức khỏe', 'blog10.jfif', '<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; color: #000000;\">Bồ c&ocirc;ng anh l&agrave; loại c&acirc;y rất quen thuộc ở Việt Nam. C&acirc;y bồ c&ocirc;ng anh c&oacute; rất nhiều t&aacute;c dụng tốt cho sức khỏe như cung cấp canxi bảo vệ xương, cung cấp vitamin K, C v&agrave; c&aacute;c chất chống oxy h&oacute;a gi&uacute;p thanh lọc gan, ngăn ngừa bệnh, chống nhiễm tr&ugrave;ng,&hellip;Do đ&oacute;, uống tr&agrave; l&agrave;m từ c&aacute;c bộ phận của', '<p style=\"box-sizing: border-box; padding: 0px; margin: 20px 0px 10px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 30px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-weight: bold;\">1. C&aacute;c loại tr&agrave; bồ c&ocirc;ng anh</span></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Tr&agrave; bồ c&ocirc;ng anh l&agrave; loại tr&agrave; được l&agrave;m từ l&aacute; hoặc rễ, th&acirc;n của c&acirc;y bồ c&ocirc;ng anh. Một v&agrave;i nơi c&ograve;n sử dụng cả hoa bồ c&ocirc;ng anh để l&agrave;m tr&agrave;. Mỗi bộ phận của c&acirc;y sẽ c&oacute; hương vị v&agrave; dược t&iacute;nh kh&aacute;c nhau. V&igrave; thế, khi mua tr&agrave; bồ c&ocirc;ng anh, bạn cần phải để &yacute; xem m&igrave;nh mua l&agrave; loại tr&agrave; l&agrave;m từ rễ, th&acirc;n , l&aacute; hay l&agrave; l&agrave;m từ hoa bồ c&ocirc;ng anh.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 20px 0px 10px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 25px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"1_1_Tra_re_hoac_than_bo_cong_anh\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-weight: bold;\">1.1. Tr&agrave; rễ hoặc th&acirc;n bồ c&ocirc;ng anh&nbsp;</span></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Rễ, th&acirc;n bồ c&ocirc;ng anh rất tốt cho gan v&agrave; mật, đồng thời c&oacute; thể k&iacute;ch th&iacute;ch vị gi&aacute;c, gi&uacute;p ăn ngon miệng hơn. Do đ&oacute;, tr&agrave; từ rễ, th&acirc;n bồ c&ocirc;ng anh thường phổ biến hơn c&aacute;c loại kh&aacute;c. Tr&agrave; bồ c&ocirc;ng anh l&agrave;m từ rễ, th&acirc;n ngon nhất khi thu hoạch v&agrave;o thu đ&ocirc;ng. V&igrave; l&uacute;c n&agrave;y tr&agrave; sẽ sẽ c&oacute; vị đắng, thơm v&agrave; đậm đ&agrave; nhất.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Đối với t&agrave; từ rễ, th&acirc;n bồ c&ocirc;ng anh, bạn n&ecirc;n h&atilde;m ở nhiệt độ khoảng 60 &ndash; 80 độ C. Thời gian h&atilde;m chỉ từ 5 &ndash; 10 ph&uacute;t l&agrave; tinh chất của tr&agrave; đ&atilde; th&ocirc;i ra hết. Tr&agrave; bồ c&ocirc;ng anh uống n&oacute;ng sẽ tốt hơn cho sức khỏe.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; display: block; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><img class=\"size-full wp-image-4683 aligncenter\" style=\"box-sizing: border-box; padding: 0px; margin: 0px auto; outline: none !important; border: 0px; vertical-align: middle; max-width: 100%; height: auto; clear: both; display: block;\" src=\"https://jarvis.vn/wp-content/uploads/2021/01/la-bo-cong-anh-dung-de-pha-tra.jpg\" alt=\"L&aacute; hoặc th&acirc;n bồ c&ocirc;ng anh d&ugrave;ng để pha tr&agrave;\" width=\"600\" height=\"439\" /></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: center;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><em style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\">Tr&agrave; bồ c&ocirc;ng anh c&oacute; t&aacute;c dụng chữa v&agrave; ph&ograve;ng chống nhiều loại bệnh tật</span></em></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 20px 0px 10px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 25px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"1_2_Tra_la_bo_cong_anh\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-weight: bold;\">1.2. Tr&agrave; l&aacute; bồ c&ocirc;ng anh</span></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Tr&agrave; từ l&aacute; bồ c&ocirc;ng anh c&oacute; t&aacute;c dụng lợi tiểu, th&iacute;ch hợp cho người bị bệnh gan thận, bị ph&ugrave;, cao huyết &aacute;p hay suy tim. L&aacute; bồ c&ocirc;ng anh l&agrave;m tr&agrave; thường được thu hoạch v&agrave;o vụ xu&acirc;n. L&uacute;c n&agrave;y, l&aacute; đ&atilde; ph&aacute;t triển trường th&agrave;nh, những tinh chất tốt nhất được lưu giữ đầy đủ trong l&aacute;.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Tr&agrave; từ l&aacute; bồ c&ocirc;ng anh c&oacute; hương vị nhạt v&agrave; nhẹ nh&agrave;ng nh&agrave;ng hơn so với tr&agrave; từ rễ, th&acirc;n bồ c&ocirc;ng anh. Nhiệt độ nước ph&ugrave; hợp để h&atilde;m tr&agrave; l&agrave; từ 80 &ndash; 95 độ C. Thời gian h&atilde;m th&iacute;ch hợp nhất cũng chỉ 5 &ndash; 10 ph&uacute;t l&agrave; c&oacute; thể lấy được tinh chất của tr&agrave;.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 20px 0px 10px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 25px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"1_3_Tra_hoa_bo_cong_anh\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-weight: bold;\">1.3. Tr&agrave; hoa bồ c&ocirc;ng anh&nbsp;</span></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Trong hoa bồ c&ocirc;ng anh&nbsp; c&oacute; chứa chất diệt khuẩn v&agrave; chống vi&ecirc;m rất tốt. Do đ&oacute;, tr&agrave; hoa bồ c&ocirc;ng anh cũng rất được y&ecirc;u th&iacute;ch. Khi pha tr&agrave; hoa bồ c&ocirc;ng anh, nước tr&agrave; c&oacute; m&agrave;u rất nhạt, vị kh&ocirc;ng đắng v&agrave; đậm như tr&agrave; từ rễ, th&acirc;n hay l&aacute; bồ c&ocirc;ng anh. Hoa kh&ocirc; vẫn giữ nguy&ecirc;n được m&agrave;u sắc tươi s&aacute;ng hấp dẫn v&agrave; đặc biệt được y&ecirc;u th&iacute;ch bởi hương vị thanh thuần, đậm vị thảo mộc thi&ecirc;n nhi&ecirc;n.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Nhiệt độ nước d&ugrave;ng để h&atilde;m tr&agrave; hoa bồ c&ocirc;ng anh tốt nhất l&agrave; từ 85 &ndash; 95 độ C. Thời gian h&atilde;m tốt nhất l&agrave; từ 10 &ndash; 15 ph&uacute;t. Khi hoa tr&agrave; đ&atilde; nở rộ trong nước, l&agrave; l&uacute;c ch&uacute;ng ta c&oacute; thể sử dụng tr&agrave; rồi.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 20px 0px 10px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 30px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"2_Cach_pha_tra_bo_cong_anh\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-weight: bold;\">2. C&aacute;ch pha tr&agrave; bồ c&ocirc;ng anh&nbsp;</span></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Tr&agrave; bồ c&ocirc;ng anh ho&agrave;n to&agrave;n c&oacute; thể kết hợp với c&aacute;c loại nguy&ecirc;n liệu kh&aacute;c để mang đến nhiều lợi &iacute;ch cho sức khỏe hơn. V&igrave; thế, bạn c&oacute; thể kết hợp tr&agrave; bồ c&ocirc;ng anh với hoa hồng, ch&egrave; xanh, r&acirc;u ng&ocirc; hoặc l&aacute; sen để thay đổi hương vị v&agrave;&nbsp; t&aacute;c dụng theo mong muốn.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; display: block; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><img class=\"size-full wp-image-4682 aligncenter\" style=\"box-sizing: border-box; padding: 0px; margin: 0px auto; outline: none !important; border: 0px; vertical-align: middle; max-width: 100%; height: auto; clear: both; display: block;\" src=\"https://jarvis.vn/wp-content/uploads/2021/01/bo-cong-anh-ket-hop-duoc-voi-nhieu-thanh-phan-de-pha-tra.jpg\" alt=\"Bồ c&ocirc;ng anh kết hợp được với nhiều th&agrave;nh phần để pha tr&agrave;\" width=\"600\" height=\"389\" /></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; text-align: center;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><em style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\">Bồ c&ocirc;ng anh c&oacute; thể kết hợp với nhiều nguy&ecirc;n liệu kh&aacute;c để tạo n&ecirc;n t&aacute;ch tr&agrave; thơm ngon, bổ dưỡng</span></em></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 20px 0px 10px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 25px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"2_1_Cach_pha_tra_bo_cong_anh_voi_hoa_hong_kho\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-weight: bold;\">2.1. C&aacute;ch pha tr&agrave; bồ c&ocirc;ng anh với hoa hồng kh&ocirc;&nbsp;</span></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Hoa hồng c&oacute; t&aacute;c dụng tăng kh&iacute; huyết cho gan, th&iacute;ch hợp với người c&oacute; vấn đề về gan. Khi kết hợp với targ bồ c&ocirc;ng anh sẽ mang đến lợi &iacute;ch tuyệt vời cho gan, thận.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 10px 0px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 24px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"Nguyen_lieu\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-weight: bold;\">Nguy&ecirc;n liệu</span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\">:&nbsp;</span></span></p>\r\n<ul style=\"box-sizing: border-box; padding: 0px 0px 0px 40px; margin: 0px; outline: none !important; list-style: unset; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">0.5g hoa hồng kh&ocirc;</span></p>\r\n</li>\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">3.5g rễ bồ c&ocirc;ng anh</span></p>\r\n</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 10px 0px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 24px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"Cach_pha_tra\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-weight: bold;\">C&aacute;ch pha tr&agrave;</span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\">:</span></span></p>\r\n<ul style=\"box-sizing: border-box; padding: 0px 0px 0px 40px; margin: 0px; outline: none !important; list-style: unset; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 1: Đun s&ocirc;i 400ml nước. cho rễ bồ c&ocirc;ng anh v&agrave;o v&agrave; đun nhỏ lửa khoảng 2 &ndash; 3 ph&uacute;t rồi tắt bếp.</span></p>\r\n</li>\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 2: Để nước nguội c&ograve;n khoảng 60 &ndash; 80 độ C th&igrave; cho hoa hồng kh&ocirc; v&agrave; h&atilde;m khoảng 3-5 ph&uacute;t.</span></p>\r\n</li>\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Bước 3: Lọc lấy nước tr&agrave; v&agrave; d&ugrave;ng khi c&ograve;n n&oacute;ng.</span></p>\r\n</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">Tr&agrave; bồ c&ocirc;ng anh hoa hồng l&agrave; đồ uống kh&ocirc;ng g&acirc;y lạnh, kh&ocirc;ng kh&ocirc; n&ecirc;n đặc biệt th&iacute;ch hợp cho phụ nữ sử dụng. Tr&agrave; bồ c&ocirc;ng anh hoa hồng c&ograve;n c&oacute; t&aacute;c dụng chống vi&ecirc;m rất tốt, n&ecirc;n phụ nữ sử dụng tr&agrave; c&ograve;n c&oacute; thể ph&ograve;ng bệnh vi&ecirc;m v&uacute;, đau v&ugrave;ng ngực.</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 20px 0px 10px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 25px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"2_2_Cach_pha_tra_bo_cong_anh_voi_che_xanh\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-weight: bold;\">2.2. C&aacute;ch pha tr&agrave; bồ c&ocirc;ng anh với ch&egrave; xanh&nbsp;</span></span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 10px; outline: none !important; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">L&aacute; bồ c&ocirc;ng anh c&oacute; khả năng l&agrave;m giảm nhanh chứng vi&ecirc;m họng, vi&ecirc;m nướu, khử m&ugrave;i h&ocirc;i của hơi thở. Ch&egrave; xanh l&agrave;m cho hơi thở trở n&ecirc;n thơm m&aacute;t, gi&uacute;p loại bỏ m&ugrave;i h&ocirc;i của kh&oacute;i thuốc.&nbsp;</span></p>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 10px 0px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 24px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\"><span id=\"Nguyen_lieu-2\" class=\"ez-toc-section\" style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important;\"></span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-weight: bold;\">Nguy&ecirc;n liệu</span><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\">:</span></span></p>\r\n<ul style=\"box-sizing: border-box; padding: 0px 0px 0px 40px; margin: 0px; outline: none !important; list-style: unset; color: #333333; font-family: Montserrat, FontAwesome, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">2.0g l&aacute; bồ c&ocirc;ng anh</span></p>\r\n</li>\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">0.8g ch&egrave; xanh</span></p>\r\n</li>\r\n<li style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: none !important; font-weight: 400;\" aria-level=\"1\">\r\n<p><span style=\"box-sizing: border-box; padding: 0px; margin: 0px; outline: currentcolor none medium !important; font-weight: 400; font-size: 12pt; font-family: arial, helvetica, sans-serif; color: #000000;\">0.2g hoa quả</span></p>\r\n</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; padding: 0px; margin: 10px 0px; outline: none !important; font-family: Nunito-Bold, Arial, Helvetica, sans-serif; font-weight: bold; line-height: 1.1; color: #5e5e5e; font-size: 24px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter