INSERT INTO `tbl_san_pham` (`ma_san_pham`, `ma_loai_san_pham`, `ten_san_pham`, `so_luong`, `khoi_luong`, `gia_nhap`, `gia_ban`, `giam_gia`, `anh_minh_hoa`, `mo_ta_ngan_gon`, `mo_ta`) VALUES
(3000001, 3, 'Trà hoa đậu biếc\r\n', 0, 0, 100000, 130000, 20, 'hoa_dau_biec.jpg', 'Thành phần: hoa đậu biếc tươi được sấy khô. 100% không sử dụng chất bảo quản, không phẩm màu, hút chân không hoàn toàn.\r\n', 'Thương hiệu: DK Harvest\r\nXuất xứ thương hiệu: Việt Nam\r\nXuất xứ: Việt Nam\r\nThành phần: 100% hoa đậu biếc sấy nguyên bông\r\nHạn sử dụng: 1 năm từ ngày sản xuất\r\nHướng dẫn bảo quản: Bảo quản nơi khô ráo thoáng mát\r\nSKU: 5854525011660\r\nQuy cách đóng gói: Đóng gói trong hũ nhựa'),
(3000002, 3, 'Trà hoa cúc', 0, 0, 100000, 150000, 10, 'cuc_trang.jpg', 'Trà hoa cúc hay còn gọi là trà bông cúc, được làm từ những hoa cúc nguyên bông và chọn lọc kỹ qua nhiều giao đoạn khác nhau.\r\n100% hoa cúc chi sấy lạnh\r\nCông dụng hữu hiệu trong thanh nhiệt, giải độc\r\nHỗ trợ an thần, thư giãn cơ thể và cải thiện giấc ngủ\r\nHương thơm hoa cúc tự nhiên, thanh tao, thơm mát\r\n100% thành phần thiên nhiên, không chất bảo quản, không tẩm ướp hương liệu, an toàn cho sức khỏe\r\nCó tính mát rất dễ uống tốt cho sức khỏe và phù hợp với tất cả mọi người.', 'Thương hiệu : DK HARVEST.\r\nXuất xứ thương hiệu : Việt Nam.\r\nNơi sản xuất : Việt Nam.\r\nThành phần : 100% Hoa cúc vàng sấy lạnh\r\nHạn sử dụng : 1 năm kể từ ngày sản xuất.\r\nHướng dẫn bảo quản : Bảo quản nơi khô ráo thoáng mát tránh ánh sáng trực tiếp.\r\nTrọng lượng : 150g.\r\nSKU : 9153052023556\r\nQuy cách đóng gói : Đóng gói trong hũ nhựa có nắp.\r\nHoa cúc là cái tên quen thuộc mà ai cũng biết, được biết đến như một loại hoa để trang trí rất phổ biến nhưng ít ai biết được những công dụng khác của hoa cúc khô với sức khỏe. Trà hoa cúc sấy lạnh nguyên bông là một loại trà thảo mộc có nguồn gốc hoàn toàn từ thiên nhiên được làm từ chính những bông hoa cúc đẹp nhất, tốt nhất. Trà có tính mát, giúp thanh nhiệt và giải độc cho cơ thể rất hiệu quả, là loại trà rất phù hợp với những người bị thiếu ngủ, khó ngủ.'),
(3000003, 3, 'Trà hoa nhài', 0, 1, 90000, 130000, 15, 'tra_hoa_nhai.jpg', '', 'Thành phần\r\n100% nụ hoa nhài, sấy khô, không sử dụng hóa chất và chất bảo quản, sạch – an toàn – tốt cho sức khỏe.\r\nHướng dẫn sử dụng	Cho khoảng 10-12 bông trà hoa nhài sấy khô vào trong ấm trà 200ml Lưu ý: không càn tráng qua nước đầu vì sẽ làm mất hương vị. Rót thêm lượng nước vừa đủ ở nhiệt độ 90 độ vào ấm, đậy nắp và ủ trong khoảng 3 – 5 phút để búp trà nở hoàn toàn, toả hương và thôi vị ra nước.\r\nQuy cách đóng gói\r\nĐóng gói 1kg.\r\nCách bảo quản\r\nBảo quản nơi khô ráo, thoáng mát\r\nXuất xứ\r\nHà Giang\r\nNgày sản xuất\r\nIn trên bao bì\r\nHạn sử dụng\r\n      2 năm kể từ ngày sản xuất'),
(3000004, 3, 'Trà hoa Atiso khô Đà Lạt', 0, 0, 500000, 700000, 0, 'atiso.jpg', '', 'Mô tả về sản phẩm TRÀ HOA ATISO KHÔ ĐÀ LẠT\r\n\r\nThành phần: 100% hoa atiso sấy khô nguyên chất, sạch.\r\n\r\nNguồn gốc: Công ty Adala – Cao nguyên Lâm Viên – Lâm Đồng\r\n\r\nQuy cách đóng gói: 1 Kg 2 Gói 500gram\r\n\r\nCam kết: không sử dụng các chất bảo quản trái phép, đảm bảo an toàn vệ sinh thực phẩm.\r\n\r\nHoa Atiso Khô Đà Lạt, nguyên chất 100%, chất lượng đảm bảo, giúp thanh lọc cơ thể, giải độc gan, tốt cho tuổi mới lớn nhờ khả năng hỗ trợ trị mụn khá tốt. Sản phẩm hiện được bán tại hệ thống cửa hàng Trà thảo dược Toàn Thắng trên toàn quốc. Giao hàng tận nơi.\r\n\r\nCÂY ATISO ĐÀ LẠT LÀ GÌ?\r\n\r\nCây Atiso Đà Lạt là một loại thảo dược quý đối với sức khoẻ con người, atiso có tác dụng lợi mật, dưới tác dụng của hoạt chất trong cây atiso, mật không những không bài tiết nhiều hơn trong gan mà còn được thoát xuống túi mật nhanh hơn bình thường, nhờ đó, chất độc không có đủ thời gian để phá hoại lá gan.\r\n\r\nAtiso Đà Lạt là phương tiện hiệu quả hỗ trợ phòng chống biếng ăn, hỗ trợ điều trị đau bụng trong triệu chứng đường ruột quá nhạy cảm. Với người cao tuổi, Atiso Đà Lạt là liều thuốc hỗ trợ nhuận trường lý tưởng, an toàn khi dùng lâu dài.\r\n\r\nAtiso Đà Lạt còn có công năng hỗ trợ hạ chất mỡ trong máu thông qua cơ chế tác dụng kép vừa mạnh về cường đô, hài hoà về tính hữu tương, một mặt Atiso ức chế uy trình tổng hợp cholesterol trong gan để ngăn không cho chất này bội tăng một cách đột biến.\r\n\r\nHoa Atiso là loại hoa quý, vừa có thể làm thuốc, vừa có thể làm rau nhưng chỉ có 2 mùa trong năm, 1 mùa ngắn vào tháng 8 và mùa dài vào tháng 1 dương lịch. Để có thể sử dụng quanh năm và dễ dàng bảo quản, hoa Atiso được sắt khô dạng lát mỏng. ​​\r\n\r\nCông dụng của hoa atiso khô nguyên chất\r\n\r\nVới tác dụng hỗ trợ thanh nhiệt lọc gan, ngày nay Atiso được dùng nhiều để có 1 lá gan tốt, 1 làn da đẹp, 1 sức khoẻ được bảo vệ hằng ngày. Với vị ngọt thanh dễ uống, không kỵ thai, nước Atiso phù hợp với tất cả mọi người.\r\n​\r\nNếu bạn nóng gan, thường xuyên bị mụn nhọt, nhiệt miệng, làn da thâm nám do mụn, do thuốc tây, … sử dụng Atiso một thời gian bạn sẽ cảm thấy sự thay đổi.\r\n\r\nHoa Atiso khô đảm bảo chất lượng có lát màu trắng riêng biệt, bên ngoài vẫn còn màu xanh lá, có mùi thơm nồng. Nươc sau khi nấu có vị ngọt thanh, nếu lợ hoặc mặn là Atiso có pha cọng.'),
(3000005, 3, 'Trà hoa đào', 0, 0, 100000, 150000, 0, 'Tra_hoa_dao_3.jpg', '<p>Vị trà &Ocirc; long Kim Tuy&ecirc;n hay còn gọi là &Ocirc; long 27 n&ocirc;̉i ti&ecirc;́ng của Đài Loan mùi thơm, nhẹ vị, nước xanh, h&acirc;̣u ngọt dịu<br />C&ocirc;ng ngh&ecirc;̣ sản xu&acirc;́t hữu cơ, ti&ecirc;u chu&acirc;̉n xu&acirc;́t kh&acirc;̉u Đài Loan<br />Pha được nhiều lần nước (8 lần)<br />H&ocirc;̣p trà m&agrave;u đỏ 250 gram, chuy&ecirc;n dụng giữ mùi, lịch sự<br />Sản phẩm ph&ugrave; hợp cho quà bi&ecirc;́u trong dịp sự kiện, lễ tết và sử dụng thường xuy&ecirc;n</p>', '<p>TH&Ocirc;NG TIN CHUNG Xuất xứ : Việt nam Trọng lượng : 60gr T&Iacute;NH NĂNG NỔI BẬT Tr&agrave; hoa đ&agrave;o Ohayge l&agrave; một loại tr&agrave; thảo mộc l&agrave;m đẹp đ&atilde; được c&aacute;c danh y, trong đ&oacute; c&oacute; Tuệ Tĩnh nghi&ecirc;n cứu để tạo ra c&aacute;c b&agrave;i thuốc l&agrave;m đẹp da cho phụ nữ, d&ugrave;ng dể trị n&aacute;m, t&agrave;n nhang, mụn nhọt, trứng c&aacute;&hellip; Đ&acirc;y cũng l&agrave; vị thuốc được Th&aacute;i B&igrave;nh c&ocirc;ng ch&uacute;a d&ugrave;ng để dưỡng da trắng trẻo, nhu nhuận kh&ocirc;ng t&igrave; vết l&agrave;m say l&ograve;ng rất nhiều nam nh&acirc;n trong thi&ecirc;n hạ. Th&agrave;nh phần của tr&agrave; hoa đ&agrave;o: 100% nụ hoa đ&agrave;o sấy kh&ocirc; &aacute;p dụng c&ocirc;ng nghệ sấy lạnh ti&ecirc;n tiến</p>'),
(3000008, 5, 'Bộ ấm pha trà ', NULL, NULL, 300000, 400000, 0, 'am_tra.jpg', '<p>Độc đ&aacute;o lu&ocirc;n l&agrave; t&acirc;m điểm t&igrave;m kiếm của những người th&iacute;ch thưởng thức tr&agrave;. Bạn l&agrave; một trong số những người th&iacute;ch sưu tầm bộ ấm ch&eacute;n tr&agrave; độc đ&aacute;o th&igrave; kh&ocirc;ng thể bỏ qua bộ ấm ch&eacute;n tr&agrave; cối xay n&agrave;y Với m&agrave;u sắc h&agrave;i ho&agrave; kiểu d&aacute;ng độc lạ .</p>', '<p>Chất liệu: gốm Phong c&aacute;ch: Đồ gốm cổ xưa + Bộ sản phẩm bao gồm: 6ch&eacute;n tr&agrave;, 1 ấm tr&agrave;, 1 bộ cối xay</p>'),
(3000009, 5, 'Bộ ấm chén pha trà hắc tử sa', NULL, NULL, 500000, 700000, 15, 'am_1.jpg', 'Gốm sứ đỉnh cao\r\nKungfu trà, trà đạo phong thủy\r\nThể hiện đẳng cấp', '1. CÁC TÍNH NẮNG NỔI BẬT\r\n\r\nGỐM SỨ ĐỈNH CAO\r\n\r\nToàn bộ sản phẩm đều được sản xuất từ gốm sứ cao cấp, chất gốm dày dặn, men Tử Sa cao cấp, màu đen tuyền đặc trưng có 1 không 2, không một thương hiệu nào ngoài thị trường có thể làm giả được.\r\n\r\n \r\n\r\nKUNGFU TRÀ\r\n\r\nBộ trà cối xay gió, khi pha trà trải qua các công đoạn cầu kỳ, tỉ mỉ, trà được pha bởi bộ ấm chén này có màu sắc trong, xanh, ngả vàng nhẹ, tuyệt đối không dính cặn, hương thơm dịu dàng lan tỏa khó quên\r\n\r\n \r\n\r\nTRÀ ĐẠO PHONG THỦY\r\n\r\nKhông chỉ là trà đạo thông thường, đây còn là vật trang trí, bảo vật phong thủy trấn gia đạo, hút tài, tích lộc, vạn sự bình an cho gia chủ.\r\n\r\n \r\n\r\nTHỂ HIỆN ĐẲNG CẤP\r\n\r\nMột bộ đẹp long lanh như thế này rất phù hợp tiếp khách quý, bạn trà, láng giềng sang chơi, vừa thể hiện sự chỉn chu, cẩn thận của gia chủ, vừa là sự tôn trọng với khách khứa, thể hiện sự sành sỏi, chất chơi không ai sánh bằng.\r\n\r\n \r\n\r\n3. THÔNG SỐ KỸ THUẬT\r\n\r\nSản xuất từ vật liệu cao cấp\r\n\r\nKích thước hộp: dài 37 cm - rộng 27 cm - cao 13 cm\r\n\r\nTrọng lượng: 2,7Kg\r\n\r\nFullbox:  6 chén, 1 đong trà, 1 ấm trà, cối xay trà\r\n\r\n \r\n\r\n2. THÔNG TIN CHI TIẾT SẢN PHẨM\r\n\r\nToàn bộ sản phẩm sản xuât thủ công, đường men tử sa bóng bảy, trên thân ấm chén phác họa họa tiết cung đình, biểu thị cho sự tốt lành và an khang thịnh vượng.\r\n\r\nPha bằng ấm chén này có khả năng tăng gấp đôi hương vị trà, những lá trà ủ kỹ, nở bung hương thơm khi đủ nhiệt, qua cối xay chỉ còn lại những dòng nước trong lành, tinh khiết, dư vị ngọt hậu.\r\n\r\nBộ trà trang bị 6 chén rất đặc sắc và độc đáo, bên trong lòng chén tráng men chống dính, ngâm trà vô tư không lo cáu, bẩn, ố vàng.\r\n\r\nTrưng bày 1 bộ trà đẳng cấp như thế này, dù ở nhà, công ty, quán xá, cửa hàng hay bất kỳ đâu, cũng mang lại vẻ đẹp rực rỡ, mới lạ, sang trọng và quý phái. Rất thích hợp làm quà biếu bạn hiền, mừng tân gia, khai trương, các dịp quan trọng, đặc biệt.\r\n\r\n'),
(3000011, 4, 'Hộp trà biếu tết bích loa xuân', NULL, NULL, 450000, 550000, 28, 'tra_bieu_1.jpg', '<p style=\"text-align: justify;\"><span style=\"color: #000000; font-family: Roboto, Helvetica, Arial, sans-serif; font-size: 13px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #fafafa; display: inline !important; float: none;\">Combo Xu&acirc;n Như &Yacute; - Set Tr&agrave; Biếu Tết gồm 3 t&uacute;i tr&agrave; ngon: Tr&agrave; L&agrave;i Loại Đặc Biệt - Tr&agrave; Oolong Kim Tuy&ecirc;n - Tr&agrave; Xanh đ&oacute;ng g&oacute;i trong hộp ngo&agrave;i chắc chắn, sang trọng. Market nghệ thuật Xu&acirc;n Như &Yacute; b&ecirc;n ngo&agrave;i l&agrave;m nổi bật kh&ocirc;ng kh&iacute; ng&agrave;y Tết cổ truyền.</span></p>', '<p style=\"box-sizing: border-box; margin: 5px 0px 12px; color: #242424; font-family: Roboto, Helvetica, Arial, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial; text-align: justify;\"><span style=\"color: #000000;\">Nằm ở độ cao đặc biệt 1600m tr&ecirc;n thổ nhưỡng Bazan m&agrave;u mỡ, Cầu Đất - Đ&agrave; Lạt c&oacute; kh&iacute; hậu &ocirc;n ho&agrave; quanh năm, từ l&acirc;u đ&atilde; trở th&agrave;nh v&ugrave;ng nguy&ecirc;n liệu tr&agrave; truyền thống, v&ugrave;ng nguy&ecirc;n liệu c&agrave; ph&ecirc; Arabica thượng hạng với phẩm chất v&agrave; hương vị rất ri&ecirc;ng biệt.</span></p>\r\n<div style=\"box-sizing: border-box; color: #242424; font-family: Roboto, Helvetica, Arial, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial; text-align: justify;\">\r\n<p style=\"box-sizing: border-box; margin: 5px 0px 12px; text-align: justify;\"><span style=\"color: #000000;\">Với sự k&igrave; c&ocirc;ng chăm s&oacute;c, tr&agrave; được thu h&aacute;i thủ c&ocirc;ng tỉ mỉ 1 t&ocirc;m 3 l&aacute; c&ugrave;ng quy tr&igrave;nh chế biến nghi&ecirc;m ngặt đ&atilde; giữ trọn hương v&agrave; vị tr&agrave; truyền thống, thanh lịch.</span></p>\r\n</div>\r\n<p style=\"box-sizing: border-box; margin: 5px 0px 12px; color: #242424; font-family: Roboto, Helvetica, Arial, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-style: initial; text-decoration-color: initial; text-align: justify;\"><span style=\"color: #000000;\">Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, ...</span></p>'),
(3000033, 5, 'Khay gác chén trà', NULL, NULL, 50000, 75000, 0, 'phatra2.PNG', '<p>gi&aacute; g&aacute;c ly ch&eacute;n l&agrave; đồ chơi bằng tre, c&oacute; thể g&aacute;c được 6-8 c&aacute;i ch&eacute;n.</p>', '<p>k&iacute;ch thước: D&agrave;i 28cm</p>\r\n<p>chất liệu: Tre</p>\r\n<p>Xuất xứ: Việt Nam</p>'),
(3000034, 5, 'Ấm Trà Khay Cách Nhiệt', NULL, 800, 70000, 90000, 10, 'phatra6.PNG', '<p>Hai mặt của n&oacute; c&oacute; thể được sử dụng để đưa ấm tr&agrave;, ch&eacute;n tr&agrave; v&agrave; c&aacute;c loại h&agrave;ng h&oacute;a kh&aacute;c.</p>', '<p>chất liệu: Việt Nam nguy&ecirc;n chất tự nhi&ecirc;n thu M&acirc;y</p>\r\n<p>N&oacute; được dệt bằng tay, tinh tế, đẹp v&agrave; mịn m&agrave;ng</p>'),
(3000035, 5, 'Dụng cụ pha trà gỗ', NULL, 900, 100000, 150000, 0, 'phatra1.PNG', '<p>k&iacute;ch thước 18*18cm</p>', '<p>Được l&agrave;m từ nguy&ecirc;n liệu tre 100%</p>\r\n<p>Bao gồm dụng cụ pha tr&agrave; v&agrave; khay đựng ch&eacute;n.</p>'),
(3000036, 5, 'Ấm sứ pha trà cao cấp', NULL, 2000, 250000, 300000, 0, 'am_su.jpg', '<p>Phong c&aacute;ch Ch&acirc;u &Acirc;u d&aacute;ng giả đ&aacute; cremic cực đẹp.</p>\r\n<p>Họa tiết trắng tối giản ph&ugrave; hợp với mọi kh&ocirc;ng gian, dễ sử dụng, trang tr&iacute;. Đ&uacute;ng gu với xu hướng nh&agrave; nội thất Việt Nam hiện tại.</p>\r\n<p>&nbsp;</p>', '<p>Chất liệu: Sứ sương</p>\r\n<p>Được nung ở nhiệt độ cao v&agrave; nhiệt độ thấp , h&agrave;m lượng bột sương của đồ sứ sương loại tốt theo ti&ecirc;u chuẩn Ch&acirc;u &Acirc;u l&agrave; 45%.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>'),
(3000037, 5, 'Bàn trà điện', NULL, NULL, 300000, 40000, 15, 'ban_tra_dien.jpg', '<p>B&agrave;n tr&agrave; điện th&ocirc;ng minh Phật Di Lắc K&eacute;o Tiền</p>\r\n<p>B&agrave;n tr&agrave; điện th&ocirc;ng minh kh&ocirc;ng chỉ lợi trong việc pha tr&agrave; m&agrave; c&ograve;n được c&aacute;c chuy&ecirc;n gia phong thủy thiết kế h&ograve;a hợp giữa c&aacute;c mệnh, c&acirc;n bằng &acirc;m dương mang lại vượng kh&iacute; cho gia chủ&nbsp;</p>', '<p>Được thiết kế theo phong c&aacute;ch hiện đại m&agrave; kh&ocirc;ng mất đi vẻ cổ k&iacute;nh, trang nh&atilde;, tinh tế, sang trọng.</p>\r\n<p>Hệ thống bơm nước v&agrave; đun s&ocirc;i nước tự động sẽ l&agrave;m thay qu&yacute; tr&agrave; mọi việc gồm:</p>\r\n<p>Bơm nước tại b&agrave;n, tự ngắt khi đầy</p>\r\n<p>T&ugrave;y chỉnh nhiệt độ v&agrave; đun s&ocirc;i lại</p>\r\n<p>Tr&aacute;ng rửa ấm ch&eacute;n</p>\r\n<p>Đổ nước thải</p>\r\n<p>...</p>'),
(3000038, 4, 'Trà Thiết Quan Âm', NULL, 500, 290000, 320000, 10, 'thiet_quan_am.jpg', '<p>Hộp 500gr si&ecirc;u ngon, đẹp, ph&ugrave; hợp l&agrave;m qu&agrave; biếu tặng</p>\r\n<p>&nbsp;</p>', '<p>Nổi tiếng với hương vị ngọt v&agrave; thơm. gi&uacute;p tinh thần nhẹ nh&agrave;ng, sảng kho&aacute;i.</p>'),
(3000040, 2, 'Trà tôm nõn', NULL, 500, 150000, 200000, 10, 'tra_non_tom_3.jpg', '<p>Tr&agrave; t&ocirc;m n&otilde;n 500 gr</p>\r\n<p>&nbsp;</p>\r\n<p>Xuất xứ: Tr&agrave; T&acirc;n Cương Th&aacute;i Nguy&ecirc;n</p>\r\n<p>&nbsp;</p>\r\n<p>K&iacute;ch cỡ: Tuỳ quy c&aacute;ch đ&oacute;ng g&oacute;i (100 gr, 200 gr, 500 gr)</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;Tr&agrave; N&otilde;n T&ocirc;m Th&aacute;i Nguy&ecirc;n c&oacute; vị ch&aacute;t đượm ban đầu v&agrave; hậu vị ngọt s&acirc;u, m&agrave;u sắc giống m&agrave;u v&agrave;ng của cốm. Hương thơm ngầy ngậy, thoang thoảng đặc trưng m&agrave; đậm chất thi&ecirc;n nhi&ecirc;n kh&ocirc;ng hề sử dụng chất tạo hương hay chất bảo quản n&agrave;o.</p>', '<p>Tr&agrave; N&otilde;n T&ocirc;m Th&aacute;i Nguy&ecirc;n l&agrave; loại tr&agrave; T&acirc;n Cương Th&aacute;i Nguy&ecirc;n ngon được l&agrave;m từ những b&uacute;p ch&egrave; tươi, xanh mướt được tuyển chọn kĩ lưỡng qua b&agrave;n tay nghệ nh&acirc;n tr&agrave; của v&ugrave;ng đất T&acirc;n Cương &ndash; Th&aacute;i Nguy&ecirc;n. Khi đến thời kỳ thu hoạch, những b&uacute;p tr&agrave; được h&aacute;i theo ti&ecirc;u chuẩn 1 t&ocirc;m 1 l&aacute; (1 đọt non mới nh&uacute; v&agrave; 1 l&aacute; non ngay kề b&ecirc;n dưới), được trồng ở v&ugrave;ng đất m&agrave;u mỡ, ph&igrave; nhi&ecirc;u v&agrave; nước tưới từ Hồ N&uacute;i Cốc. Tr&agrave; n&otilde;n t&ocirc;m được h&aacute;i v&agrave;o l&uacute;c trời đẹp, &aacute;nh nắng vừa phải, h&aacute;i theo ti&ecirc;u chuẩn, nhờ vậy chất lượng tr&agrave; mới ngon v&agrave; đậm đ&agrave;.</p>\r\n<p>&nbsp;</p>\r\n<p>Tr&agrave; N&otilde;n T&ocirc;m Th&aacute;i Nguy&ecirc;n c&oacute; vị ch&aacute;t đượm ban đầu v&agrave; hậu vị ngọt s&acirc;u, m&agrave;u sắc giống m&agrave;u v&agrave;ng của cốm. Hương thơm ngầy ngậy, thoang thoảng đặc trưng m&agrave; đậm chất thi&ecirc;n nhi&ecirc;n kh&ocirc;ng hề sử dụng chất tạo hương hay chất bảo quản n&agrave;o.</p>\r\n<p>&nbsp;</p>\r\n<p>C&aacute;ch để nhận biết tr&agrave; N&otilde;n T&ocirc;m Th&aacute;i Nguy&ecirc;n với những loại tr&agrave; kh&aacute;c đ&oacute; l&agrave; c&aacute;nh tr&agrave;. C&aacute;nh tr&agrave; thường mỏng, xoăn n&otilde;n tr&agrave; quấn chặt v&agrave;o nhau, nhỏ hơn đầu tăm. Điều đặc biệt về loại tr&agrave; n&agrave;y&nbsp; v&igrave; nguy&ecirc;n liệu tr&agrave; l&agrave; phần n&otilde;n tr&ecirc;n c&ugrave;ng, đ&oacute; l&agrave; phần chứa rất nhiều dinh dưỡng v&agrave; khi thu hoạch th&igrave; sản lượng tuy &iacute;t nhưng khi pha tr&agrave; nước sẽ rất đậm đ&agrave; v&agrave; uống được l&acirc;u hơn.</p>'),
(3000041, 4, 'Bộ trà Đinh Ngọc', NULL, 600, 450000, 600000, 5, 'bo_tra_dinh_2.jpg', '<p style=\"margin: 0mm; margin-bottom: .0001pt; text-align: justify; background: white; vertical-align: baseline;\"><span style=\"font-size: 14.0pt; color: black;\">Bao gồm: 1 h&ocirc;̣p lớn, 2 h&ocirc;̣p bát giác nhỏ, 200gr trà Đinh Ngọc, 1 túi vải.</span></p>\r\n<p style=\"margin: 0mm; margin-bottom: .0001pt; text-align: justify; background: white; vertical-align: baseline;\"><span style=\"font-size: 14.0pt; color: black;\">Kích thước:&nbsp;22 x 18 x 8,5 cm</span></p>\r\n<p style=\"margin: 0mm; margin-bottom: .0001pt; text-align: justify; background: white; vertical-align: baseline;\"><span style=\"font-size: 14.0pt; color: black;\">Hộp Tr&agrave; Truyền Thống - Tr&agrave; Đinh Ngọc Th&aacute;i Nguy&ecirc;n l&agrave; bộ qu&agrave; tặng tr&agrave; cao cấp ph&ugrave; hợp l&agrave;m qu&agrave; tặng, qu&agrave; biếu người th&acirc;n, sếp, bạn b&egrave; hay t&acirc;n gia... Với thiết kế&nbsp;hộp đơn giản, mang m&agrave;u xanh chủ đạo&nbsp;nhưng v&ocirc; c&ugrave;ng đẹp mắt v&agrave; sang trọng, cộng với tr&agrave; Đinh Ngọc th&aacute;i nguy&ecirc;n l&agrave; loại tr&agrave; cao cấp nhất trong c&aacute;c loại tr&agrave; T&acirc;n Cương Th&aacute;i Nguy&ecirc;n sẽ tạo n&ecirc;n một m&oacute;n qu&agrave; v&ocirc; c&ugrave;ng &yacute; nghĩa v&agrave; đẳng cấp.</span></p>\r\n<p style=\"margin: 0mm; margin-bottom: .0001pt; text-align: justify; background: white; vertical-align: baseline;\"><span style=\"font-size: 14.0pt; color: black;\">&nbsp;</span></p>', '<p style=\"margin: 0mm; margin-bottom: .0001pt; text-align: justify; text-indent: 36.0pt; background: white; vertical-align: baseline;\"><a href=\"https://loctancuong.com/che-thai-nguyen/tra-dinh-thai-nguyen/\" target=\"_blank\" rel=\"noopener\"><strong><em><span style=\"font-size: 14.0pt; border: none windowtext 1.0pt; mso-border-alt: none windowtext 0mm; padding: 0mm;\">Tr&agrave; Đinh Ngọc</span></em></strong></a><span style=\"font-size: 14.0pt; color: black; border: none windowtext 1.0pt; mso-border-alt: none windowtext 0mm; padding: 0mm;\">&nbsp;tựa ngọc của đất trời ban tặng cho loại tr&agrave; qu&yacute; n&agrave;y. Tr&ecirc;n những đồi tr&agrave;&nbsp;tuyển chọn, kh&iacute; hậu m&aacute;t mẻ, đất đai m&agrave;u mỡ, thi&ecirc;n nhi&ecirc;n ưu &aacute;i đ&atilde; l&agrave;m n&ecirc;n những c&acirc;y tr&agrave; chất lượng, đủ ti&ecirc;u chuẩn v&agrave; th&iacute;ch hợp để được chọn lọc l&agrave;m n&ecirc;n loại tr&agrave; qu&yacute;.&nbsp;<br />&nbsp; &nbsp; &nbsp; Từ những đọt tr&agrave; non chưa nở c&aacute;nh giống như 1 chiếc đinh nhỏ được h&aacute;i v&ocirc; c&ugrave;ng tỉ mỉ bởi những b&agrave;n tay kh&eacute;o l&eacute;o của những c&ocirc; g&aacute;i Th&aacute;i Nguy&ecirc;n. Để c&oacute; được 1kg Tr&agrave; đinh kh&ocirc;, phải cần đến 20 người h&aacute;i tr&agrave; chuy&ecirc;n nghiệp, qu&aacute; tr&igrave;nh h&aacute;i tr&agrave; k&eacute;o d&agrave;i trong khoảng 2 tiếng đồng hồ v&agrave; phải h&aacute;i v&agrave;o thời điểm s&aacute;ng sớm khi những đọt tr&agrave; c&ograve;n ngậm chặt giọt sương sớm tinh khiết.</span></p>\r\n<p style=\"margin: 0mm; margin-bottom: .0001pt; text-align: justify; text-indent: 36.0pt; background: white; vertical-align: baseline;\"><span style=\"font-size: 14.0pt; color: black; border: none windowtext 1.0pt; mso-border-alt: none windowtext 0mm; padding: 0mm;\">Qu&aacute; tr&igrave;nh trồng trọt v&agrave; thu h&aacute;i v&ocirc; c&ugrave;ng gian nan v&agrave; tỉ mỉ th&igrave; đ&ograve;i hỏi qu&aacute; tr&igrave;nh chế biến ra loại tr&agrave; đinh n&agrave;y cũng kh&ocirc;ng k&eacute;m phần kỳ c&ocirc;ng. Để cho ra mẻ&nbsp;</span><a href=\"https://loctancuong.com/che-thai-nguyen/tra-dinh-thai-nguyen/tra-dinh-ngoc-100gr.html\" target=\"_blank\" rel=\"noopener\"><strong><em><span style=\"font-size: 14.0pt; color: #006600; border: none windowtext 1.0pt; mso-border-alt: none windowtext 0mm; padding: 0mm; text-decoration: none; text-underline: none;\">tr&agrave; đinh</span></em></strong></a><span style=\"font-size: 14.0pt; color: black; border: none windowtext 1.0pt; mso-border-alt: none windowtext 0mm; padding: 0mm;\">&nbsp;thơm ngon chất lượng th&igrave; phải cần đến những b&agrave;n tay kh&eacute;o l&eacute;o của những nghệ nh&acirc;n nhiều năm kinh nghiệm l&agrave;m tr&agrave;. Tại thời điểm n&agrave;y tại v&ugrave;ng đất đệ nhất danh tr&agrave; T&acirc;n Cương Th&aacute;i nguy&ecirc;n chỉ c&oacute; 3 nghệ nh&acirc;n nổi tiếng để đảm nhận l&agrave;m c&ocirc;ng việc n&agrave;y.</span></p>\r\n<p style=\"margin: 0mm; margin-bottom: .0001pt; text-align: justify; background: white; vertical-align: baseline;\"><span style=\"font-size: 14.0pt; color: black; border: none windowtext 1.0pt; mso-border-alt: none windowtext 0mm; padding: 0mm;\">&nbsp; &nbsp; &nbsp; Tr&agrave; Đinh Ngọc&nbsp;Thái Nguy&ecirc;n&nbsp;khi pha ra c&oacute; m&ugrave;i thơm ngọt của cốm non, nước xanh hơi ngả v&agrave;ng, vị ch&aacute;t đượm, hậu ngọt s&acirc;u. Đ&acirc;y l&agrave; loại tr&agrave; kh&ocirc;ng ướp hương, rất được những người s&agrave;nh tr&agrave; ưa chuộng, v&agrave; từ l&acirc;u đ&atilde; l&agrave; thượng phẩm d&acirc;ng l&ecirc;n vua ch&uacute;a.</span></p>\r\n<p style=\"margin: 0mm; margin-bottom: .0001pt; text-align: justify; background: white; vertical-align: baseline;\"><span style=\"font-size: 14.0pt; color: black; border: none windowtext 1.0pt; mso-border-alt: none windowtext 0mm; padding: 0mm;\">&nbsp; &nbsp; &nbsp; H&ocirc;̣p tr&agrave; qu&agrave; tặng đẹp,&nbsp;mang màu sắc sang trọng, mang ý nghĩa may mắn và thành c&ocirc;ng, phù hợp khi bi&ecirc;́u tặng đ&ocirc;́i tác, quà biếu sếp, người th&acirc;n, qu&agrave; tặng cho bạn b&egrave;, đồng nghiệp...</span></p>'),
(3000042, 4, 'Hộp trà Đinh Ngọc', NULL, 400, 320000, 450000, 10, 'hop_tra_dinh_ngoc_2.jpg', '<p>Bao gồm: 1 h&ocirc;̣p gỗ lớn, 2 hộp gỗ nhỏ, 200gr trà, 1 túi vải</p>\r\n<p>Kích thước: 22,5 x 17 x 8,5 cm</p>\r\n<p>G&ocirc;̃ Th&ocirc;ng nh&acirc;̣p kh&acirc;̉u.</p>\r\n<p>Hộp Qu&agrave; Vạn Xu&acirc;n - Tr&agrave; Đinh Ngọc Th&aacute;i Nguy&ecirc;n l&agrave; sản phẩm qu&agrave; tặng tr&agrave; đẳng cấp v&agrave; sang trọng, v&ocirc; c&ugrave;ng th&iacute;ch hợp l&agrave;m qu&agrave; tặng, qu&agrave; biếu. Họa tiết vẽ tay thư ph&aacute;p &eacute;n b&aacute;o xu&acirc;n tr&ecirc;n bề mặt hộp gỗ tạo n&ecirc;n sự kh&aacute;c biệt đầy tinh tế v&agrave; đẹp mắt. Đặc biệt, tr&agrave; đinh th&aacute;i nguy&ecirc;n l&agrave; loại tr&agrave; đặc sản cao cấp nhất trong tất cả loại tr&agrave; xanh của v&ugrave;ng tr&agrave; Th&aacute;i Nguy&ecirc;n.</p>\r\n<p>&nbsp;</p>', '<p>Tr&agrave; Đinh Ngọc tựa ngọc của đất trời ban tặng cho loại tr&agrave; qu&yacute; n&agrave;y. Tr&ecirc;n những đồi tr&agrave; tuyển chọn, kh&iacute; hậu m&aacute;t mẻ, đất đai m&agrave;u mỡ, thi&ecirc;n nhi&ecirc;n ưu &aacute;i đ&atilde; l&agrave;m n&ecirc;n những c&acirc;y tr&agrave; chất lượng, đủ ti&ecirc;u chuẩn v&agrave; th&iacute;ch hợp để được chọn lọc l&agrave;m n&ecirc;n loại tr&agrave; qu&yacute;.&nbsp;</p>\r\n<p>&nbsp; &nbsp; &nbsp; Từ những đọt tr&agrave; non chưa nở c&aacute;nh giống như 1 chiếc đinh nhỏ được h&aacute;i v&ocirc; c&ugrave;ng tỉ mỉ bởi những b&agrave;n tay kh&eacute;o l&eacute;o của những c&ocirc; g&aacute;i Th&aacute;i Nguy&ecirc;n. Để c&oacute; được 1kg Tr&agrave; đinh kh&ocirc;, phải cần đến 20 người h&aacute;i tr&agrave; chuy&ecirc;n nghiệp, qu&aacute; tr&igrave;nh h&aacute;i tr&agrave; k&eacute;o d&agrave;i trong khoảng 2 tiếng đồng hồ v&agrave; phải h&aacute;i v&agrave;o thời điểm s&aacute;ng sớm khi những đọt tr&agrave; c&ograve;n ngậm chặt giọt sương sớm tinh khiết.</p>\r\n<p>Qu&aacute; tr&igrave;nh trồng trọt v&agrave; thu h&aacute;i v&ocirc; c&ugrave;ng gian nan v&agrave; tỉ mỉ th&igrave; đ&ograve;i hỏi qu&aacute; tr&igrave;nh chế biến ra loại tr&agrave; đinh n&agrave;y cũng kh&ocirc;ng k&eacute;m phần kỳ c&ocirc;ng. Để cho ra mẻ tr&agrave; đinh thơm ngon chất lượng th&igrave; phải cần đến những b&agrave;n tay kh&eacute;o l&eacute;o của những nghệ nh&acirc;n nhiều năm kinh nghiệm l&agrave;m tr&agrave;. Tại thời điểm n&agrave;y tại v&ugrave;ng đất đệ nhất danh tr&agrave; T&acirc;n Cương Th&aacute;i nguy&ecirc;n chỉ c&oacute; 3 nghệ nh&acirc;n nổi tiếng để đảm nhận l&agrave;m c&ocirc;ng việc n&agrave;y.</p>\r\n<p>&nbsp; &nbsp; &nbsp; Tr&agrave; Đinh Ngọc Thái Nguy&ecirc;n khi pha ra c&oacute; m&ugrave;i thơm ngọt của cốm non, nước xanh hơi ngả v&agrave;ng, vị ch&aacute;t đượm, hậu ngọt s&acirc;u. Đ&acirc;y l&agrave; loại tr&agrave; kh&ocirc;ng ướp hương, rất được những người s&agrave;nh tr&agrave; ưa chuộng, v&agrave; từ l&acirc;u đ&atilde; l&agrave; thượng phẩm d&acirc;ng l&ecirc;n vua ch&uacute;a.</p>\r\n<p>&nbsp; &nbsp; &nbsp; H&ocirc;̣p tr&agrave; qu&agrave; tặng đẹp, mang màu sắc sang trọng, mang ý nghĩa may mắn và thành c&ocirc;ng, phù hợp khi bi&ecirc;́u tặng đ&ocirc;́i tác, quà biếu sếp, người th&acirc;n, qu&agrave; tặng cho bạn b&egrave;, đồng nghiệp...</p>\r\n<p>&nbsp;</p>'),
(3000043, 4, 'Trà sen Tây Hồ', NULL, 450, 600000, 760000, 5, 'tra_sen_3.jpg', '<p>Tr&agrave; Sen T&acirc;y Hồ l&agrave; sự kết hợp của Tr&agrave; N&otilde;n T&ocirc;m với Hoa Sen B&aacute;ch Diệp. Những b&ocirc;ng Sen được h&aacute;i trong s&aacute;ng sớm, mang về t&aacute;ch lấy gạo sen, phần c&ograve;n lại của hoa phải bỏ hết, chỉ lấy Gạo sen để ướp với tr&agrave; th&ocirc;i, qu&aacute; tr&igrave;nh ướp từ 7 - 10 ng&agrave;y với số lần ướp từ 4 đến 7 lần cho 1 mẻ tr&agrave; th&agrave;nh c&ocirc;ng. Tr&agrave; Sen T&acirc;y Hồ của Lộc T&acirc;n Cương c&oacute; c&aacute;nh tr&agrave; nhỏ, hương thơm ngọt của hoa sen tự nhi&ecirc;n, tiền vị ch&aacute;t dịu, hậu vị ngọt rất đượm đ&agrave; v&agrave; lưu lại trong cổ họng rất l&acirc;u, rất dễ chịu.</p>\r\n<p>KT: 20 x 12 x 9 cm</p>\r\n<p>&nbsp;</p>', '<p>Tr&agrave; sen T&acirc;y Hồ từ l&acirc;u đ&atilde; l&agrave; loại tr&agrave; cao qu&yacute; nhất, l&agrave; loại tr&agrave; được d&ugrave;ng để d&acirc;ng l&ecirc;n vua ch&uacute;a thời đấy v&agrave; cho đến ng&agrave;y nay tr&agrave; lại được xem l&agrave; đỉnh cao nghệ thuật tr&agrave; của Việt Nam. Trong hầu hết c&aacute;c dịp đặc biệt, người ta thường nghĩ đến tr&agrave; sen v&agrave; d&ugrave;ng l&agrave;m thức uống để mời kh&aacute;ch qu&yacute;, biếu tặng người th&acirc;n v&agrave; những người quan trọng. Bởi qua tr&agrave; sen người tặng c&oacute; thể thể hiện được sự th&agrave;nh k&iacute;nh, tr&acirc;n trọng v&agrave; kh&ocirc;ng k&eacute;m phần tinh tế đến người nhận.</p>\r\n<p>&nbsp; &nbsp; &nbsp; Hộp t&acirc;m giao tr&agrave; sen t&acirc;y hồ t&eacute;p với thiết kế tỉ mỉ, m&agrave;u sắc h&agrave;i h&ograve;a tạo n&ecirc;n sự thu h&uacute;t v&agrave; đẹp mắt, thoạt nh&igrave;n qua dễ l&agrave;m cho người ta thổi hồn v&agrave;o đ&oacute;. Hộp tr&agrave; sẽ l&agrave; m&oacute;n qu&agrave; v&ocirc; c&ugrave;ng &yacute; nghĩa để thể hiện mối t&acirc;m giao giữa người biếu v&agrave; nhận.. người nhận vui ắt người tặng cũng vui l&acirc;y kh&ocirc;ng k&eacute;m phần, đ&oacute; ch&iacute;nh l&agrave; thứ m&agrave; người đời hay gọi l&agrave; t&acirc;m giao.</p>\r\n<p>Sen ở mỗi nơi trong Việt Nam sẽ c&oacute; n&eacute;t đặc trưng kh&aacute;c nhau nhưng chỉ c&oacute; sen ở v&ugrave;ng Hồ T&acirc;y mới l&agrave; thứ nguy&ecirc;n liệu l&agrave;m n&ecirc;n thức uống vừa thanh tao lại cao qu&yacute;.</p>\r\n<p>&nbsp; &nbsp; &nbsp;Sen ở đ&acirc;y sở dĩ c&oacute; được hương vị đặc biệt đấy l&agrave; v&igrave; b&ugrave;n ở đ&acirc;y rất m&agrave;u mỡ, ch&iacute;nh v&igrave; thế b&ocirc;ng sen ph&aacute;t triển rất to. Hơn thế nữa, Hồ T&acirc;y l&agrave; một v&ugrave;ng đất địa linh, rất linh thi&ecirc;ng, bởi nơi đ&acirc;y tiếp thu rất nhiều dưỡng kh&iacute; của trời đất, b&ocirc;ng sen ở đ&acirc;y cũng v&igrave; thế m&agrave; tận hưởng được từ đấy v&agrave; đặc trưng hơn hẳn v&ugrave;ng kh&aacute;c.</p>\r\n<p>&nbsp; &nbsp; &nbsp; Sen đ&atilde; thơm th&igrave; tr&agrave; được với sen cũng phải l&agrave; loại tr&agrave; ngon nhất trong c&aacute;c loại tr&agrave;, đ&oacute; ch&iacute;nh l&agrave; tr&agrave; th&aacute;i nguy&ecirc;n hảo hạng, c&oacute; thế mới tạo n&ecirc;n được sự h&ograve;a quyện giữa hương thơm thanh tao v&agrave; vị tr&agrave; thuần khiết đậm đ&agrave;.</p>\r\n<p>&nbsp; &nbsp; &nbsp; Thưởng thức tr&agrave; sen l&agrave; thưởng thức hương thơm của sen khi h&ograve;a quyện với tr&agrave;, ch&iacute;nh v&igrave; thế tr&agrave; khi được d&ugrave;ng l&agrave;m thức uống sẽ c&oacute; vị kh&ocirc;ng qu&aacute; ch&aacute;t m&agrave; chỉ chan ch&aacute;t nhẹ, thấm đượm hậu ngọt v&agrave; hương thơm tươi m&aacute;t thanh tao của hoa sen.</p>\r\n<p>&nbsp;</p>'),
(3000044, 2, 'Trà Tân Cương đặc biêt', NULL, 500, 95000, 115000, 0, 'dac_biet_1.jpg', '<p>Ch&egrave; Th&aacute;i Nguy&ecirc;n Đặc Biệt TC2 - 500g</p>\r\n<p>Khối Lượng : 500 gram</p>\r\n<p>Hạn sử dụng : 1 năm kể từ ng&agrave;y sản xuất</p>', '<p>Tr&agrave; T&acirc;n Cương Đặc Biệt TC2 được sản xuất bằng nguy&ecirc;n liếp b&uacute;p ch&egrave; th&aacute;i nguy&ecirc;n 1 t&ocirc;m 3 l&aacute; của v&ugrave;ng tr&agrave; t&acirc;n cương nổi tiếng .được h&aacute;i v&agrave; chế biến theo đ&uacute;ng quy tr&igrave;nh kỹ thuật truyền thống với b&iacute; quyết chỉnh lửa của c&aacute;c nghệ nh&acirc;n bản xứ v&agrave; c&aacute;ch lấy hương bằng lửa tạo ra hương thơm thanh khiết độc nhất v&ocirc; nhị của sản phẩm.</p>\r\n<p>Ch&egrave; nguy&ecirc;n liệu được trồng v&agrave; chăm s&oacute;c theo ti&ecirc;u chuẩn VietGAP với quy tr&igrave;nh trồng ch&egrave; hữu cơ tại v&ugrave;ng ch&egrave; đặc sản T&acirc;n Cương Th&aacute;i Nguy&ecirc;n.</p>\r\n<p>Tiếp đ&oacute;, những b&uacute;p ch&egrave; 1 t&ocirc;m 3 l&aacute; được h&aacute;i v&agrave; chế biến theo đ&uacute;ng quy tr&igrave;nh kỹ thuật truyền thống với b&iacute; quyết chỉnh lửa của c&aacute;c nghệ nh&acirc;n bản xứ v&agrave; c&aacute;ch lấy hương bằng lửa tạo ra hương thơm thanh khiết độc nhất v&ocirc; nhị của sản phẩm.</p>'),
(3000045, 2, 'Trà Tân Cương thượng hạng', NULL, 500, 95000, 150000, 0, 'thuong_hang_1.jpg', '<p>Gi&aacute;: 150.000 đồng/ 500 gr</p>\r\n<p>Quy c&aacute;ch đ&oacute;ng g&oacute;i kh&aacute;c: 100 gr, 200 gr, 500 gr</p>\r\n<p>Hạn sử dụng 1 năm kể từ ng&agrave;y sản xuất</p>', '<p>Ch&egrave; t&acirc;n cương th&aacute;i nguy&ecirc;n nguy&ecirc;n liệu được trồng v&agrave; chăm s&oacute;c theo ti&ecirc;u chuẩn VietGAP với quy tr&igrave;nh trồng ch&egrave; hữu cơ tại v&ugrave;ng ch&egrave; đặc sản T&acirc;n Cương Th&aacute;i Nguy&ecirc;n.</p>\r\n<p>Tiếp đ&oacute; những b&uacute;p ch&egrave; 1 t&ocirc;m 2 l&aacute; được h&aacute;i v&agrave; chế biến theo đ&uacute;ng quy tr&igrave;nh kỹ thuật truyền thống với b&iacute; quyết chỉnh lửa của c&aacute;c nghệ nh&acirc;n bản xứ v&agrave; c&aacute;ch lấy hương bằng lửa tạo ra hương thơm thanh khiết độc nhất v&ocirc; nhị của sản phẩm ch&egrave; th&aacute;i nguy&ecirc;n ngon</p>\r\n<p>-Pha tr&agrave; tốt nhất l&agrave; bằng ấm sứ hoặc ấm đất c&oacute; thể t&iacute;ch từ khoảng&nbsp; 250 ml- 350 ml;</p>\r\n<p>T&ugrave;y theo số người uống m&agrave; d&ugrave;ng ấm to hay ấm nhỏ;</p>\r\n<p>T&ugrave;y theo số người uống m&agrave; cho nhiều hay &iacute;t tr&agrave;;</p>\r\n<p>-Tr&aacute;ng ấm pha tr&agrave; bằng nước s&ocirc;i;</p>\r\n<p>-Cho khoảng 5 gam đến 7 gam tr&agrave; v&agrave;o ấm;</p>\r\n<p>-Chế nước s&ocirc;i v&agrave;o để l&agrave;m n&oacute;ng ấm v&agrave; đ&aacute;nh thức tr&agrave;, chắt hết nước trong ấm tr&agrave; bỏ đi;</p>\r\n<p>-Chế lượng nước s&ocirc;i vừa đủ- khoảng 150 ml- 250 ml t&ugrave;y theo sở trường đậm, nhạt;</p>\r\n<p>-H&atilde;m khoảng 5 ph&uacute;t;</p>\r\n<p>-R&oacute;t ra ch&eacute;n rồi thưởng thức.</p>'),
(3000046, 2, 'Trà Tân Cương Long Đình', NULL, 500, 150000, 225000, 0, 'long_dinh_2.jpg', '<p>Gi&aacute;: 450.000 đồng/kg</p>\r\n<p>Quy c&aacute;ch đ&oacute;ng g&oacute;i: 100 gr, 200 gr, 500 gr</p>\r\n<p>(C&oacute; hộp k&egrave;m theo)</p>\r\n<p>Hạn sử dụng 1 năm kể từ ng&agrave;y sản xuất</p>', '<p>M&ocirc; tả : Ch&egrave; T&acirc;n Cương Long Đ&igrave;nh được sản xuất bởi nguy&ecirc;n liệu l&agrave; c&aacute;c b&uacute;p tr&agrave; m&oacute;c c&acirc;u tươi ngon nhất của v&ugrave;ng ch&egrave; T&acirc;n Cương Th&aacute;i Nguy&ecirc;n nổi tiếng</p>\r\n<p>Ch&egrave; T&acirc;n Cương Long Đ&igrave;nh cho một chất lượng, hương vị thơm ngon &iacute;t tr&agrave; n&agrave;o c&oacute; thể s&aacute;nh kịp, tr&agrave; đạt đầy đủ ti&ecirc;u ch&iacute; như Hương - Sắc - Vị - Thần, bao b&igrave; sang trọng, thiết kế tỉ mỉ c&ocirc;ng phu n&ecirc;n tr&agrave; t&acirc;n cương long đ&igrave;nh c&oacute; thể l&agrave;m qu&agrave; biếu sang trọng với đối t&aacute;c hoặc kh&aacute;ch h&agrave;ng cao cấp</p>\r\n<p>Điều l&agrave;m n&ecirc;n chất lượng thơm ngon của Ch&egrave; T&acirc;n Cương Long Đ&igrave;nh đ&oacute; ch&iacute;nh l&agrave; nguy&ecirc;n liệu l&agrave; c&aacute;c b&uacute;p ch&egrave; t&acirc;n cương thơm ngon, yếu tố thứ 2 l&agrave;m ch&egrave; ngon đ&oacute; l&agrave; bởi thổ nhưỡng, điều kiện tự nhi&ecirc;n l&agrave;m cho ch&egrave; T&acirc;n Cương Long Đ&igrave;nh c&ograve;n được gọi l&agrave; đệ nhất danh tr&agrave;</p>\r\n<p>Ch&egrave; T&acirc;n Cương Long Đ&igrave;nh đ&oacute;ng g&oacute;i mỗi hộp 500gram v&agrave; đi k&egrave;m 1kg&nbsp; l&agrave; 1 t&uacute;i giấy x&aacute;ch ngo&agrave;i sang trọng.</p>'),
(3000047, 2, 'Trà Tân Cương Tước Thiệt', NULL, 500, 210000, 300000, 0, 'tuoc_thiet_3.jpg', '<p>Gi&aacute; ni&ecirc;m yết: 600.000 đồng/kg</p>\r\n<p>Quy c&aacute;ch đ&oacute;ng g&oacute;i: 100 gr, 200 gr, 500 gr</p>\r\n<p>(C&oacute; hộp k&egrave;m theo)</p>\r\n<p>Hạn sử dụng 1 năm kể từ ng&agrave;y sản xuất</p>', '<p>Ch&egrave; t&acirc;n cương th&aacute;i nguy&ecirc;n nguy&ecirc;n liệu được trồng v&agrave; chăm s&oacute;c theo ti&ecirc;u chuẩn VietGAP với quy tr&igrave;nh trồng ch&egrave; hữu cơ tại v&ugrave;ng ch&egrave; đặc sản T&acirc;n Cương Th&aacute;i Nguy&ecirc;n.</p>\r\n<p>Tiếp đ&oacute;, những t&ocirc;m ch&egrave; mới nh&uacute; như lưỡi chim sẻ (tước thiệt) được h&aacute;i v&agrave; chế biến theo đ&uacute;ng quy tr&igrave;nh kỹ thuật truyền thống với b&iacute; quyết chỉnh lửa của c&aacute;c nghệ nh&acirc;n bản xứ v&agrave; c&aacute;ch lấy hương bằng lửa tạo ra hương thơm thanh khiết độc nhất v&ocirc; nhị của sản phẩm ch&egrave; th&aacute;i nguy&ecirc;n t&acirc;n cương tước thiệt</p>\r\n<p>Thu h&aacute;i v&agrave;o l&uacute;c s&aacute;ng sớm khi b&uacute;p tr&agrave; c&ograve;n đang ngậm sương</p>\r\n<p>Sản xuất: 100% sao chế thủ c&ocirc;ng truyền thống với c&aacute;ch lấy hương bằng đ&aacute;nh lửa tự nhi&ecirc;n</p>'),
(3000048, 2, 'Trà Tân Cương Đinh Tiến Quan', NULL, 500, 370000, 500000, 5, 'dinh_tien_quan_1.jpg', '<p>Gi&aacute; ni&ecirc;m yết: 1.000.000 đồng/kg</p>\r\n<p>Quy c&aacute;ch đ&oacute;ng g&oacute;i: 100 gr v&agrave; 500 gr</p>\r\n<p>(C&oacute; hộp k&egrave;m theo)</p>\r\n<p>Hạn sử dụng 1 năm kể từ ng&agrave;y sản xuất</p>', '<p>B&uacute;p tr&agrave; mới nh&uacute; nhọn những những chiếc đinh đ&atilde; được thu hoạch khi mặt trời c&ograve;n chưa mọc để tận dụng được hơi sương thấm v&agrave;o b&uacute;p tr&agrave; tạo n&ecirc;n vị tr&agrave; rất đặc trưng.</p>\r\n<p>Để c&oacute; được 1kg tr&agrave; đinh kh&ocirc; cần tới 10 kg tr&agrave; tươi th&ocirc;ng qua sự kh&eacute;o l&eacute;o v&agrave; tỉ mẩn của những c&ocirc; g&aacute;i h&aacute;i tr&agrave;. Từ đ&oacute; th&ocirc;ng qua sự chế biến cầu k&igrave; của c&aacute;c nghệ nh&acirc;n với phương thức gia truyền để tạo n&ecirc;n n&otilde;n ch&egrave; trở n&ecirc;n xoăn chắc, nhỏ để khi khi pha sẽ tạo ra một ly tr&agrave; s&aacute;nh, xanh v&agrave; hương thơm tuyệt hảo hội tụ tinh t&uacute; của đất- trời v&agrave; người- thứ thức uống m&agrave; ng&agrave;y trước chỉ vua ch&uacute;a mới được thưởng thức.</p>\r\n<p>Trong qu&aacute; tr&igrave;nh chế biến, c&ocirc;ng đoạn sao ch&egrave; l&agrave; kh&acirc;u quan trọng nhất, kỳ c&ocirc;ng nhất. Kinh nghiệm, kỹ năng ho&agrave;n hảo, sự lắng nghe hơi thở của từng n&otilde;n ch&egrave; cho ph&eacute;p người nghệ nh&acirc;n biến những b&uacute;p ch&egrave; nhỏ v&agrave; mỏng ngoan ngo&atilde;n xoắn v&acirc;n, kh&ocirc; gi&ograve;n v&agrave; nựng thơm hương cốm. B&iacute; quyết chọn củi, chỉnh lửa v&agrave; lấy hương bằng lửa b&iacute; truyền của người nghệ nh&acirc;n đ&atilde; tạo ra sản phẩ</p>\r\n<p>C&aacute;nh Ch&egrave; T&acirc;n Cương Đinh Th&agrave;nh Phẩm : C&aacute;nh nh&igrave;n giống chiếc đinh nhỏ - đen nh&aacute;nh v&agrave; uống rất thơm,hương thơm đệ nhất của ch&egrave; đinh t&acirc;n cương c&ograve;n được thể hiện khi ch&egrave; c&oacute; thể uống được 4-5 nước vẫn thơm ngon</p>'),
(3000049, 2, 'Trà Tân Cương Tứ Quý', NULL, 500, 900000, 1500000, 10, 'dinh_tu_quy_1.jpg', '<p>Gi&aacute; ni&ecirc;m yết: 3.000.000 đồng/kg</p>\r\n<p>Quy c&aacute;ch đ&oacute;ng g&oacute;i: 100 gr v&agrave; 500 gr</p>\r\n<p>(C&oacute; hộp sang trọng k&egrave;m theo)</p>\r\n<p>Hạn sử dụng 1 năm kể từ ng&agrave;y sản xuất</p>', '<p>B&uacute;p tr&agrave; mới nh&uacute; nhọn những những chiếc đinh đ&atilde; được thu hoạch khi mặt trời c&ograve;n chưa mọc để tận dụng được hơi sương thấm v&agrave;o b&uacute;p tr&agrave; tạo n&ecirc;n vị tr&agrave; rất đặc trưng.</p>\r\n<p>Để c&oacute; được 1kg tr&agrave; đinh kh&ocirc; cần tới 10 kg tr&agrave; tươi th&ocirc;ng qua sự kh&eacute;o l&eacute;o v&agrave; tỉ mẩn của những c&ocirc; g&aacute;i h&aacute;i tr&agrave;. Từ đ&oacute; th&ocirc;ng qua sự chế biến cầu k&igrave; của c&aacute;c nghệ nh&acirc;n với phương thức gia truyền để tạo n&ecirc;n n&otilde;n ch&egrave; trở n&ecirc;n xoăn chắc, nhỏ để khi khi pha sẽ tạo ra một ly tr&agrave; s&aacute;nh, xanh v&agrave; hương thơm tuyệt hảo hội tụ tinh t&uacute; của đất- trời v&agrave; người- thứ thức uống m&agrave; ng&agrave;y trước chỉ vua ch&uacute;a mới được thưởng thức.</p>\r\n<p>Trong qu&aacute; tr&igrave;nh chế biến, c&ocirc;ng đoạn sao ch&egrave; l&agrave; kh&acirc;u quan trọng nhất, kỳ c&ocirc;ng nhất. Kinh nghiệm, kỹ năng ho&agrave;n hảo, sự lắng nghe hơi thở của từng n&otilde;n ch&egrave; cho ph&eacute;p người nghệ nh&acirc;n biến những b&uacute;p ch&egrave; nhỏ v&agrave; mỏng ngoan ngo&atilde;n xoắn v&acirc;n, kh&ocirc; gi&ograve;n v&agrave; nựng thơm hương cốm. B&iacute; quyết chọn củi, chỉnh lửa v&agrave; lấy hương bằng lửa b&iacute; truyền của người nghệ nh&acirc;n đ&atilde; tạo ra sản phẩ</p>\r\n<p>C&aacute;nh Ch&egrave; T&acirc;n Cương Đinh Th&agrave;nh Phẩm : C&aacute;nh nh&igrave;n giống chiếc đinh nhỏ - đen nh&aacute;nh v&agrave; uống rất thơm,hương thơm đệ nhất của ch&egrave; đinh t&acirc;n cương c&ograve;n được thể hiện khi ch&egrave; c&oacute; thể uống được 4-5 nước vẫn thơm ngon</p>'),
(3000050, 4, 'Hộp trà Phúc Lộc', NULL, 600, 355000, 550000, 5, 'phuc_loc_2.jpg', '<p>HỘP &Ocirc; LONG PH&Uacute;C LỘC&nbsp;</p>\r\n<p>ĐƠN GI&Aacute;: 550.000đ</p>\r\n<p>TRỌNG LƯỢNG: 600gr</p>\r\n<p>VỎ HỘP: Hộp giấy cứng sang trọng</p>', '<p>Đặc t&iacute;nh của tr&agrave; &ocirc; long do c&ocirc;ng ty cung cấp l&agrave; kh&ocirc;ng sử dụng bất k&igrave; hương hay phụ gia n&agrave;o trong qu&aacute; tr&igrave;nh chế biến, m&agrave; đ&oacute; l&agrave; m&ugrave;i hương đặc trưng sẵn c&oacute; của tr&agrave; &ocirc; long. Để c&oacute; được th&agrave;nh phẩm tr&agrave; Oolong th&igrave; từ nguy&ecirc;n liệu tươi thu h&aacute;i về phải mất hơn 30 tiếng qua nhiều c&ocirc;ng đoạn kh&aacute;c nhau như: l&agrave;m h&eacute;o, quay thơm, diệt men, định h&igrave;nh v&agrave; cuối c&ugrave;ng l&agrave; đ&oacute;ng g&oacute;i tạo ra nhiều chủng loại tr&agrave; Oolong theo thị hiếu người ti&ecirc;u d&ugrave;ng.</p>\r\n<p>Nh&igrave;n chung sản phẩm tr&agrave; &ocirc; long đều c&oacute; h&igrave;nh thể m&agrave;u xanh đen, b&oacute;ng, h&igrave;nh cầu hoặc b&aacute;n cầu, c&oacute; m&agrave;u sắc v&agrave; k&iacute;ch thước hạt đồng nhất, m&agrave;u nước v&agrave;ng xanh, vị ch&aacute;t nhẹ, dịu, hậu ngọt v&agrave; hương Oolong tự nhi&ecirc;n.</p>\r\n<p>Nhờ v&agrave;o quy tr&igrave;nh sản xuất b&aacute;n l&ecirc;n men n&ecirc;n tr&agrave; Oolong c&oacute; nguồn Polyphenol dồi d&agrave;o, kh&ocirc;ng chỉ ngăn ngừa c&aacute;c bệnh ung thư, vi&ecirc;m khớp, ti&ecirc;u chảy ..... như tr&agrave; xanh m&agrave; c&ograve;n c&oacute; thể chống suy thận, suy l&agrave; l&aacute;ch, s&acirc;u răng, đồng thời gi&uacute;p l&agrave;n da tươi trẻ v&agrave; hạn chế chứng b&eacute;o ph&igrave;.</p>'),
(3000051, 1, 'Hộp trà Ô Long gỗ thông', NULL, 300, 600000, 650000, 10, 'che_tan_cuong_go_thong.jpg', '<p>Bộ tr&agrave; &Ocirc; Long gỗ th&ocirc;ng trắng với nguy&ecirc;n liệu tr&agrave; được l&agrave;m bằng c&aacute;c b&uacute;p tr&agrave; t&acirc;n cương cao cấp. Với c&aacute;ch đ&aacute;nh hương bằng lửa tự nhi&ecirc;n</p>\r\n<p>Vỏ hộp l&agrave;m bằng chất liệu gỗ th&ocirc;ng trắng. Sản phẩm được d&ugrave;ng l&agrave;m qu&agrave; biếu cho đối t&aacute;c hoặc kh&aacute;ch h&agrave;ng cao cấp</p>', '<p>Tr&agrave; T&acirc;n &Ocirc; Long Hạng nguy&ecirc;n liệu được trồng v&agrave; chăm s&oacute;c theo ti&ecirc;u chuẩn VietGAP với quy tr&igrave;nh trồng ch&egrave; hữu cơ tại v&ugrave;ng ch&egrave; đặc sản T&acirc;n Cương Th&aacute;i Nguy&ecirc;n</p>'),
(3000052, 4, 'Hộp trà Long Phượng', NULL, 200, 170000, 250000, 0, 'long_phuong_1.jpg', '<p>HỘP T&Acirc;N CƯƠNG LONG PHƯỢNG</p>\r\n<p>ĐƠN GI&Aacute;: 250.000đ</p>\r\n<p>TRỌNG LƯỢNG: 200gr</p>\r\n<p>VỎ HỘP: Hộp giấy cứng sang trọng in h&igrave;nh long phượng</p>', '<p>Bộ Tr&agrave; T&acirc;n Cương Long Phượng được sản xuất tr&ecirc;n nguy&ecirc;n liệu b&uacute;p tr&agrave; t&acirc;n cương đinh đặc sản thơm ngon đệ nhất của v&ugrave;ng tr&agrave; t&acirc;n cương th&aacute;i nguy&ecirc;n , tiếp đ&oacute; những b&uacute;p tr&agrave; t&acirc;n cương được sao tay thủ c&ocirc;ng truyền thống của c&aacute;c nghệ nh&acirc;n T&acirc;n Cương Xanh</p>\r\n<p>Hộp của Bộ Tr&agrave; T&acirc;n Cương Long Phượng được thiết kế tr&ecirc;n chất liệu giấy thủ c&ocirc;ng phủ tr&ecirc;n nền của chất liệu carton lạnh v&ocirc; c&ugrave;ng chắc chắn, đi k&egrave;m ngo&agrave;i l&agrave; 1 t&uacute;i giấy nguy&ecirc;n bộ , trong hộp lớn l&agrave; 2 hộp nhỏ, mỗi hộp nhỏ sẽ chứa 10 g&oacute;i 10gram tr&agrave; t&acirc;n cương đinh đặc sản</p>\r\n<p>Đ&acirc;y l&agrave; d&ograve;ng sản phẩm qu&agrave; biếu cao cấp nhất của T&acirc;n Cương Xanh, sản phẩm d&ugrave;ng để biếu đối t&aacute;c hoặc kh&aacute;ch h&agrave;ng cao cấp</p>'),
(3000053, 4, 'Hộp trà Long Việt', NULL, 400, 300000, 450000, 0, 'long_viet_3.jpg', '<p>HỘP T&Acirc;N CƯƠNG LONG VIỆT</p>\r\n<p>ĐƠN GI&Aacute;: 450.000đ</p>\r\n<p>TRỌNG LƯỢNG: 400gr</p>\r\n<p>VỎ HỘP: Hộp giấy cứng sang trọng</p>', '<p>Bộ hộp ch&egrave; t&acirc;n cương Long Việt được sản xuất tr&ecirc;n nguy&ecirc;n liệu 100% b&uacute;p ch&egrave; tươi ngon nhất do c&aacute;c nghệ nh&acirc;n trong nh&agrave; m&aacute;y c&ocirc;ng ty T&acirc;n Cương Xanh sản xuất.</p>\r\n<p>Hộp được l&agrave;m bằng chất liệu carton lạnh, b&ecirc;n ngo&agrave;i l&agrave; hộp to, b&ecirc;n trong l&agrave; 2 hộp nhỏ đựng trong khay phủ bởi lụa v&agrave;ng tạo ra một sản phẩm rất cứng chắc chắn, sang trọng, Sản phẩm được d&ugrave;ng l&agrave;m qu&agrave; biếu đối t&aacute;c v&agrave; kh&aacute;ch h&agrave;ng cao cấp</p>\r\n<p>Mỗi 1 hộp ch&egrave; t&acirc;n cương Long Việt đều tặng k&egrave;m 1 t&uacute;i giấy x&aacute;ch ngo&agrave;i</p>\r\n<p>Qu&yacute; kh&aacute;ch c&oacute; thể đến cuỗi cửa h&agrave;ng của c&ocirc;ng ty T&acirc;n Cương Xanh ch&uacute;ng t&ocirc;i để chọn v&agrave; mua những sản phẩm tr&agrave; ngon sạch ch&iacute;nh hiệu.</p>'),
(3000054, 1, 'Trà Ô Long Tiến Vua', NULL, 300, 300000, 400000, 0, 'tra_tien_vua.jpg', '<p>Tr&agrave; &ocirc; long tiến vua 100gram gồm 10 g&oacute;i x10gram h&uacute;t ch&acirc;n kh&ocirc;ng v&agrave; đựng trong hộp thiếc...</p>\r\n<p>&nbsp;</p>', '<p>Tr&agrave; được nhập khẩu dưới dạng th&ocirc; 18kg/th&ugrave;ng của v&ugrave;ng Cao Sơn - Đ&agrave;i Loan qu&ecirc; hương của tr&agrave; &ocirc; long. V&agrave; được đ&oacute;ng g&oacute;i tại nh&agrave; m&aacute;y sản xuất tr&agrave; ngon T&acirc;n Cương Xanh.</p>\r\n<p>&nbsp;</p>'),
(3000055, 4, 'Hộp trà Hoàng Gia', NULL, 600, 750000, 1100000, 0, 'thuy_ngoc_1.jpg', '<p>M&ocirc; tả : Tr&agrave; &ocirc; long ho&agrave;ng gia l&agrave; sản phẩm cao cấp với nguy&ecirc;n liệu l&agrave;m bằng b&uacute;p tr&agrave; &ocirc; long tứ qu&yacute; ngon nhất. Sản phẩm gồm 1 hộp to v&agrave; 4 hộp nhỏ b&ecirc;n trong. Chất liệu hộp carton lạnh với hoa văn họa tiết tinh xảo</p>\r\n<p>Quy c&aacute;ch : Sản phẩm gồm 4 g&oacute;i tr&agrave; &ocirc; long h&uacute;t ch&acirc;n kh&ocirc;ng v&agrave; đựng trong 4 hộp. Đi k&egrave;m tặng ngo&agrave;i l&agrave; t&uacute;i x&aacute;ch sang trọng</p>', '<p>Từ v&ugrave;ng đất Bảo Lộc (L&acirc;m Đồng) với những ưu đ&atilde;i đặc biệt của thi&ecirc;n nhi&ecirc;n về kh&iacute; hậu v&agrave; thổ nhưỡng, ch&uacute;ng t&ocirc;i sản xuất ra loại tr&agrave; &ocirc; long Việt Nam kh&ocirc;ng chỉ tương đồng về chất lượng với tr&agrave; Đ&agrave;i Loan m&agrave; c&ograve;n ph&ugrave; hợp với khẩu vị của người Việt. C&aacute;c sản phẩm tr&agrave; &ocirc; long Việt như Olong Cao Cấp Việt Nam, Olong Tứ Q&uacute;y Việt Nam....</p>\r\n<p>Đồng thời để đ&aacute;p ứng cho những thực kh&aacute;ch ưu c&aacute;i vị thanh nhẹ của tr&agrave; olong Đ&agrave;i Loan, ch&uacute;ng t&ocirc;i nhập khẩu c&aacute;c loại tr&agrave; Olong của v&ugrave;ng Cao Sơn (Đ&agrave;i Loan) như tr&agrave; &ocirc; long Th&uacute;y Ngọc, tr&agrave; &ocirc; long Tứ Q&uacute;y Đ&agrave;i Loan, tr&agrave; &ocirc; long Thượng Hạng Đ&agrave;i Loan.</p>\r\n<p>Ngo&agrave;i c&aacute;c d&ograve;ng sản phẩm tr&agrave; uống, hiện tại c&ocirc;ng ty cũng sản xuất v&agrave; nhập khẩu c&aacute;c bộ Tr&agrave; sang trọng v&agrave; đẳng cấp đ&aacute;p ứng nhu cầu qu&agrave; biếu lễ tết của kh&aacute;ch h&agrave;ng như Hộp tr&agrave; Olong Hứng Dừa- Long Phượng- Tứ Long, Hộp Gấm Ho&agrave;ng Gia, Olong Ph&uacute;c Lộc.</p>\r\n<p>Với hơn một thập kỉ x&acirc;y dựng v&agrave; ph&aacute;t triển thương hiệu tr&agrave; T&acirc;n Cương, ch&uacute;ng t&ocirc;i tự h&agrave;o l&agrave; nh&agrave; cung cấp số 1 tr&ecirc;n thị trường hiện nay về số lượng v&agrave; chất lượng tuyệt hảo của c&aacute;c loại tr&agrave; &ocirc; long Việt- Đ&agrave;i. Sự y&ecirc;u mến v&agrave; t&iacute;n nhiệm của qu&yacute; kh&aacute;ch h&agrave;ng cũng ch&iacute;nh l&agrave; động lực hướng tới sự ph&aacute;t triển của c&ocirc;ng ty!</p>'),
(3000056, 1, 'Gói trà Ô Long cao cấp', NULL, 200, 300000, 450000, 20, 'goi_tra.jpg', '<p>G&oacute;i Tr&agrave; &Ocirc; Long Cao Cấp 200gram Tr&agrave; &ocirc; long cao cấp được l&agrave;m bằng c&aacute;c b&uacute;p tr&agrave; &ocirc; long tứ qu&yacute; của v&ugrave;ng tr&agrave; &ocirc; long...</p>\r\n<p>&nbsp;</p>', '<p>Tr&agrave; &ocirc; long cao cấp được l&agrave;m bằng c&aacute;c b&uacute;p tr&agrave; &ocirc; long tứ qu&yacute; của v&ugrave;ng tr&agrave; &ocirc; long Bảo Lộc. Tiếp đ&oacute; được đ&oacute;ng trong bao b&igrave; giấy đẹp sang trọng</p>\r\n<p>Sản phẩm kh&ocirc;ng chỉ thơm ngon m&agrave; c&ograve;n tốt cho sức khỏe. Sản phẩm hiện được b&aacute;n tại hệ thống cửa h&agrave;ng tr&agrave; &ocirc; long - T&acirc;n Cương Xanh</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>');
INSERT INTO `tbl_san_pham` (`ma_san_pham`, `ma_loai_san_pham`, `ten_san_pham`, `so_luong`, `khoi_luong`, `gia_nhap`, `gia_ban`, `giam_gia`, `anh_minh_hoa`, `mo_ta_ngan_gon`, `mo_ta`) VALUES
(3000057, 4, 'Trà Ô Long gấm Hoàng Gia', NULL, 600, 875000, 1200000, 0, 'gam_hoang_gia.jpg', '<p>600 gram b&uacute;p tr&agrave; &ocirc; long thơm ngon nhất được đựng trong 4 hộp nhỏ , mỗi hộp nhỏ l&agrave; 150gram tr&agrave; &ocirc; long đặc biệt cao cấp thơm ngon . Đi k&egrave;m mỗi h&ocirc;p l&agrave; 1 t&uacute;i giấy x&aacute;ch ngo&agrave;i sang trọng. Sản phẩm qu&agrave; biếu đối t&aacute;c v&agrave; kh&aacute;ch h&agrave;ng cao cấp.</p>', '<p>600 gram b&uacute;p tr&agrave; &ocirc; long thơm ngon nhất được đựng trong 4 hộp nhỏ , mỗi hộp nhỏ l&agrave; 150gram tr&agrave; &ocirc; long đặc biệt cao cấp thơm ngon . Đi k&egrave;m mỗi h&ocirc;p l&agrave; 1 t&uacute;i giấy x&aacute;ch ngo&agrave;i sang trọng. Sản phẩm qu&agrave; biếu đối t&aacute;c v&agrave; kh&aacute;ch h&agrave;ng cao cấp</p>\r\n<p>Tr&agrave; &ocirc; long c&oacute; nguồn gốc b&ecirc;n Đ&agrave;i Loan v&agrave; được xuất khẩu đi to&agrave;n thế giới. Tr&agrave; &ocirc; long được trồng ở Việt Nam ta tại 2 tỉnh l&agrave; L&acirc;m Đồng v&agrave; Mộc Ch&acirc;u - Sơn La từ năm 1989. Đầu ti&ecirc;n tr&agrave; &ocirc; long c&aacute;nh thẳng rất dễ vỡ vụn, nhưng sau đ&oacute; c&aacute;c nh&agrave; sản xuất Đ&agrave;i Loan đ&atilde; biết c&aacute;ch l&ecirc;n men v&agrave; tạo h&igrave;nh, ch&iacute;nh bởi vậy c&aacute;nh tr&agrave; &ocirc; long tr&ograve;n v&agrave; cứng như hiện nay.</p>\r\n<p>Qu&yacute; kh&aacute;ch c&oacute; thể đến cuỗi cửa h&agrave;ng của c&ocirc;ng ty T&acirc;n Cương Xanh ch&uacute;ng t&ocirc;i để chọn v&agrave; mua những sản phẩm tr&agrave; ngon sạch ch&iacute;nh hiệu.</p>'),
(3000058, 1, 'Trà Ô Long Hoa Mai', NULL, 500, 1200000, 1500000, 15, '2.jpg', '<p>Tr&agrave; &Ocirc; Long 5 Hoa Mai l&agrave; sản phẩm bộ qu&agrave; tặng tr&agrave; cao cấp với trọng lượng 600 gram tr&agrave; &Ocirc; Long chất lượng tốt nhất.</p>\r\n<p>Sản phẩm được thiết kế sang trọng v&agrave; chất lượng tr&agrave; tuyệt vời, l&agrave; niềm tự h&agrave;o của tr&agrave; Long Đỉnh!</p>', '<p>th&agrave;nh phần: b&uacute;p tr&agrave; &Ocirc; Long một t&ocirc;m, hai l&aacute; non</p>\r\n<p>&ndash; H&igrave;nh d&aacute;ng: vi&ecirc;n tr&ograve;n to đều đặn, m&agrave;u xanh đen b&oacute;ng tuyệt đẹp</p>\r\n<p>&ndash; Chỉ dẫn hương vị: nước tr&agrave; xanh &aacute;nh v&agrave;ng trong suốt, vị đậm đ&agrave; mạnh mẽ, m&ugrave;i hạt h&ograve;a với hương hoa nồng n&agrave;n</p>\r\n<p>&ndash; Đ&oacute;ng g&oacute;i: Bộ qu&agrave; tặng tr&agrave; &Ocirc; long 5 Hoa Mai gồm hộp lớn ngo&agrave;i , b&ecirc;n trong c&oacute; 4 hộp x 150 gram tr&agrave; &Ocirc; Long h&uacute;t ch&acirc;n kh&ocirc;ng. T&uacute;i x&aacute;ch đồng bộ đi k&egrave;m l&agrave;m qu&agrave; biếu sang trọng</p>\r\n<p>&ndash; K&iacute;ch thước hộp ngo&agrave;i: D&agrave;i: 28.5 cm, Rộng: 28.5 cm, D&agrave;y: 9.5 cm</p>\r\n<p>&ndash; K&iacute;ch thước hộp trong: D&agrave;i: 10 cm, Rộng: 7.5 cm, D&agrave;y: 7.5 cm</p>\r\n<p>&ndash; Tổng trọng lượng (hộp v&agrave; tr&agrave;): 2000 gram</p>\r\n<p>&nbsp;</p>'),
(3000059, 1, 'Trà Ô Long Lộc Hương', NULL, 500, 350000, 500000, 10, '5.jpg', '<p>Bộ qu&agrave; tặng tr&agrave; cao cấp tr&agrave; Oolong Lộc Hương mang m&agrave;u xanh chủ đạo kh&eacute;o l&eacute;o kết hợp với m&agrave;u đỏ, vừa thể hiện sự tinh tế, kh&eacute;o l&eacute;o lại vừa thể hiện sự sang trọng. Họa tiết trống đồng mang đặc trưng của văn h&oacute;a Việt được lồng gh&eacute;p h&agrave;i h&ograve;a ở bề mặt c&agrave;ng l&agrave;m cho m&oacute;n qu&agrave; trở n&ecirc;n &yacute; nghĩa , ho&agrave;n to&agrave;n th&iacute;ch hợp l&agrave;m qu&agrave; tặng, qu&agrave; biếu.</p>\r\n<p>&nbsp;</p>', '<p>Nguy&ecirc;n liệu sản xuất tr&agrave; oolong l&agrave; những b&uacute;p tr&agrave; non được thu h&aacute;i v&agrave;o buổi s&aacute;ng, l&uacute;c tan sương, kh&ocirc;ng h&aacute;i v&agrave;o ng&agrave;y mưa hoặc ng&agrave;y nắng gắt. Tr&agrave; sau khi h&aacute;i được đựng trong g&ugrave;i tre để tr&aacute;nh việc bị dập c&aacute;nh tr&agrave;.</p>\r\n<p>Tr&agrave; Oolong mang hương thơm nhẹ nh&agrave;ng của hoa cỏ, nước tr&agrave; v&agrave;ng trong v&agrave; hậu ngọt thơm, ai đ&atilde; từng thưởng thức qua cũng nhớ m&atilde;i hương vị n&agrave;y.</p>\r\n<p>H&ocirc;̣p quà tặng hộp qu&agrave; truyền thống &ndash; tr&agrave; oolong Lộc Hương l&agrave; bộ qu&agrave; tặng tr&agrave; cao cấp mang ý nghĩa may mắn và thành c&ocirc;ng, phù hợp khi bi&ecirc;́u tặng đ&ocirc;́i tác, qu&agrave; tặng đồng nghiệp, quà tặng cho những h&ocirc;̣i nghị khách hàng&hellip;</p>'),
(3000060, 1, 'Trà Xanh OLONG Trà Vương', NULL, 500, 500000, 550000, 0, '9.jpg', '<p>đ&acirc;y l&agrave; sản phẩm c&oacute; mức gi&aacute; mềm trong sản phẩm Tr&agrave; Vương.</p>\r\n<p>Ddảm bảo chất lượng, v&agrave; ph&ugrave; hợp với mọi lứa tuổi.</p>', '<p>Đặc biệt sản phẩm n&agrave;y c&ograve;n được kết hợp với th&agrave;nh phần chiết xuất từ củ s&acirc;m v&agrave; l&aacute; s&acirc;m gi&uacute;p người uống kh&ocirc;ng chỉ thưởng thức được hương vị thơm ngon của tr&agrave; m&agrave; cơ thể&nbsp; c&ograve;n được bồi bổ với những th&agrave;nh phần dưỡng chất từ s&acirc;m.</p>'),
(3000061, 1, 'Trà OLong Pha trà sữa', NULL, 500, 200000, 250000, 0, '13.jpg', '<p>C&oacute; m&ugrave;i thơm, dịu, rất ph&ugrave; hợp cho người trẻ.</p>', '<p>Xuất xứ: Việt Nam</p>\r\n<p>Hạn sử dụng: 1 năm kể từ ng&agrave;y sản xuất</p>\r\n<p>Bảo quản nơi kh&ocirc; r&aacute;o, tr&aacute;nh ẩm mốc.</p>');

